import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField,
  Select, MenuItem, Checkbox, Fade, useMediaQuery
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import GroupAddIcon from "@mui/icons-material/GroupAdd";
import DiamondIcon from "@mui/icons-material/Diamond";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваша бронирование подтверждено. Ждем вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

const Sidebar = styled(Box)(({ theme }) => ({
  background: "#f7f9fb",
  borderRadius: 24,
  minWidth: 300,
  maxWidth: 350,
  boxShadow: "0 4px 20px 0 rgba(20,25,55,0.06)",
  padding: theme.spacing(4, 2),
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  marginRight: theme.spacing(4),
  [theme.breakpoints.down("md")]: {
    maxWidth: "100%",
    marginRight: 0,
    marginBottom: theme.spacing(3),
  }
}));

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index} role="tabpanel">
      {value === index && <Fade in>{children}</Fade>}
    </div>
  );
}

export default function CustomerProfileCard() {
  const isMobile = useMediaQuery("(max-width:900px)");
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    city: "Краснодар",
    country: "Россия",
    gender: "Женский",
    clientType: "Физ. лицо",
    address: "ул. Ленина, д. 10, кв. 24",
    marketing: true,
    lang: "Русский",
    age: 36,
    tags: ["VIP", "Постоянный клиент"],
    referrals: 8,
    bonusLevel: "Gold",
    ltv: "108 400 ₽",
    bonus: "1 563",
    interests: [
      "Горные туры", "Зимний отдых", "SPA и релакс", "Детские активности", "Экстрим"
    ],
    promoCodes: [
      { code: "VIP2025", discount: "15%", used: false },
      { code: "SKI2024", discount: "20%", used: true }
    ],
    gifts: [
      { date: "2024-01-15", name: "Подарочная карта 1 000 ₽", status: "Активна" },
      { date: "2023-11-22", name: "Фирменный термос", status: "Получено" }
    ],
  });
  const isVip = profile.tags.includes("VIP");
  const copyToClipboard = (val) => navigator.clipboard.writeText(val);

  const purchases = [
    {
      date: "30.05.2025",
      item: "Бронирование отеля",
      amount: "4 400 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 4400, writtenOff: false }
      ]
    }
  ];

  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление", campaign: "Акция «Вернем 30%»" }
  ];
  const [messageHistory, setMessageHistory] = useState([
    { date: "10.06.2025 10:23", channel: "SMS", direction: "Исходящее", text: "Спасибо за ваш отзыв!" }
  ]);
  const [tab, setTab] = useState(0);
  const [editOpen, setEditOpen] = useState(false);
  const [form, setForm] = useState(profile);

  // --- UI
  return (
    <Box sx={{
      bgcolor: "#f4f7fa",
      minHeight: "100vh",
      py: isMobile ? 2 : 5,
      px: isMobile ? 0 : 3
    }}>
      <Box sx={{
        maxWidth: 1220,
        mx: "auto",
        bgcolor: "#fff",
        borderRadius: 5,
        boxShadow: "0 4px 32px 0 rgba(20,25,55,0.08)",
        px: isMobile ? 1 : 4,
        py: isMobile ? 2 : 4,
        overflow: "hidden"
      }}>
        <Box sx={{
          display: isMobile ? "block" : "flex",
          alignItems: "flex-start"
        }}>
          {/* Sidebar Left */}
          <Sidebar>
            <Avatar
              sx={{
                width: 92,
                height: 92,
                fontSize: 46,
                bgcolor: isVip ? "#ffe082" : "#EEF1F7",
                color: isVip ? "#000" : "#4361EE",
                border: isVip ? "3px solid #FFD700" : undefined,
                mb: 2,
                transition: "all 0.2s"
              }}
            >
              {profile.name.split(" ").map(part => part[0]).join("").toUpperCase()}
            </Avatar>
            <Typography variant="h5" fontWeight={700} mb={.3}>
              {profile.name}
              {isVip && <DiamondIcon sx={{ color: "#FFD700", ml: 1, fontSize: 28 }} />}
            </Typography>
            <Chip
              label={profile.bonusLevel}
              color="warning"
              size="small"
              icon={<EmojiEventsIcon />}
              sx={{ mb: 2, fontWeight: 700, bgcolor: "#fff3e0", color: "#b8860b" }}
            />
            <Chip
              label={`Рефералов: ${profile.referrals}`}
              color="info"
              size="small"
              icon={<GroupAddIcon />}
              sx={{ mb: 2, fontWeight: 700 }}
            />
            <Divider sx={{ my: 2, width: "100%" }} />
            <Stack spacing={1} sx={{ width: "100%" }}>
              <Stack direction="row" alignItems="center">
                <EmailIcon color="primary" sx={{ mr: 1 }} />
                <Typography sx={{ fontSize: 16 }}>{profile.email}</Typography>
                <Tooltip title="Скопировать">
                  <IconButton size="small" onClick={() => copyToClipboard(profile.email)}><ContentCopyIcon fontSize="small" /></IconButton>
                </Tooltip>
              </Stack>
              <Stack direction="row" alignItems="center">
                <SmsIcon color="success" sx={{ mr: 1 }} />
                <Typography sx={{ fontSize: 16 }}>{profile.phone}</Typography>
                <Tooltip title="Скопировать">
                  <IconButton size="small" onClick={() => copyToClipboard(profile.phone)}><ContentCopyIcon fontSize="small" /></IconButton>
                </Tooltip>
              </Stack>
              <Stack direction="row" alignItems="center">
                <PersonIcon color="disabled" sx={{ mr: 1 }} />
                <Typography sx={{ fontSize: 16 }}>ID: {profile.id}</Typography>
              </Stack>
            </Stack>
            <Divider sx={{ my: 2, width: "100%" }} />
            <Stack direction="row" spacing={1}>
              <Tooltip title="Отправить сообщение">
                <IconButton sx={{ bgcolor: "#F1F3F6" }}><SendIcon fontSize="medium" sx={{ color: "#3f51b5" }} /></IconButton>
              </Tooltip>
              <Tooltip title="Редактировать профиль">
                <IconButton sx={{ bgcolor: "#F1F3F6" }} onClick={() => setEditOpen(true)}>
                  <EditIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
            </Stack>
          </Sidebar>

          {/* Main right */}
          <Box flex={1} minWidth={0}>
            <Stack direction={isMobile ? "column" : "row"} spacing={3} mt={isMobile ? 3 : 0} mb={2}>
              <Paper variant="outlined" sx={{ flex: 1, minWidth: 270, bgcolor: "#f8fafc", p: 2, borderRadius: 4 }}>
                <Typography fontWeight={700} color="text.secondary">Общий LTV</Typography>
                <Typography variant="h5" fontWeight={700}>{profile.ltv}</Typography>
              </Paper>
              <Paper variant="outlined" sx={{ flex: 1, minWidth: 270, bgcolor: "#f8fafc", p: 2, borderRadius: 4 }}>
                <Typography fontWeight={700} color="text.secondary">Баланс бонусов</Typography>
                <Typography variant="h5" fontWeight={700} color="primary">{profile.bonus}</Typography>
              </Paper>
            </Stack>
            <Paper variant="outlined" sx={{ p: isMobile ? 1 : 2, mb: 3, borderRadius: 4 }}>
              <Table size="small">
                <TableBody>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 700, width: 150 }}>Дата рождения</TableCell>
                    <TableCell>{profile.dob.split("-").reverse().join(".")}</TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 700 }}>Город</TableCell>
                    <TableCell>{profile.city}, {profile.country}</TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 700 }}>Пол</TableCell>
                    <TableCell>{profile.gender}</TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 700 }}>Адрес</TableCell>
                    <TableCell>{profile.address}</TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 700 }}>Язык интерфейса</TableCell>
                    <TableCell>{profile.lang}</TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 700 }}>Статус рассылки</TableCell>
                    <TableCell>
                      {profile.marketing
                        ? <Chip label="Подписан" color="success" size="small" />
                        : <Chip label="Нет согласия" color="warning" size="small" />}
                    </TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 700 }}>Теги клиента</TableCell>
                    <TableCell>
                      <Stack direction="row" spacing={1} flexWrap="wrap">
                        {profile.tags.map((t, idx) => (
                          <Chip key={idx} label={t} size="small"
                                sx={t === "VIP" ? { bgcolor: '#FFD700', color: '#222', fontWeight: 700 } : {}} />
                        ))}
                      </Stack>
                    </TableCell>
                  </TableRow>
                </TableBody>
              </Table>
            </Paper>

            {/* Табы */}
            <Tabs
              value={tab}
              onChange={(_, v) => setTab(v)}
              textColor="primary"
              indicatorColor="primary"
              variant={isMobile ? "scrollable" : "standard"}
              allowScrollButtonsMobile
              sx={{
                mb: 1,
                ".MuiTabs-indicator": { height: 4, borderRadius: 2 }
              }}
            >
              <Tab icon={<LocalOfferIcon />} label="Промокоды" />
              <Tab icon={<CardGiftcardIcon />} label="Подарки" />
              <Tab label="Бонусы" />
              <Tab icon={<SmsIcon />} label="Сообщения" />
              <Tab icon={<StarIcon />} label="Интересы" />
            </Tabs>

            {/* Контент вкладок */}
            <TabPanel value={tab} index={0}>
              <Stack spacing={2} direction="row" flexWrap="wrap">
                {profile.promoCodes.map((p, idx) => (
                  <Chip
                    key={idx}
                    icon={<LocalOfferIcon />}
                    label={`${p.code} (${p.discount})`}
                    color={p.used ? "default" : "success"}
                    sx={{ fontWeight: 700, fontSize: 18, px: 2, py: 2, mr: 2, mb: 2 }}
                    variant={p.used ? "outlined" : "filled"}
                  />
                ))}
              </Stack>
            </TabPanel>
            <TabPanel value={tab} index={1}>
              <Stack spacing={2}>
                {profile.gifts.map((g, idx) => (
                  <Paper key={idx} sx={{
                    p: 2, mb: 1, borderRadius: 3,
                    bgcolor: g.status === "Активна" ? "#e8f5e9" : "#fafafa"
                  }}>
                    <Stack direction="row" alignItems="center" spacing={2}>
                      <CardGiftcardIcon color={g.status === "Активна" ? "success" : "action"} />
                      <Stack>
                        <Typography fontWeight={700}>{g.name}</Typography>
                        <Typography variant="body2" color="text.secondary">{g.date}</Typography>
                        <Chip label={g.status} color={g.status === "Активна" ? "success" : "default"} size="small" sx={{ mt: .5 }} />
                      </Stack>
                    </Stack>
                  </Paper>
                ))}
              </Stack>
            </TabPanel>
            <TabPanel value={tab} index={2}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Дата</TableCell>
                    <TableCell>Тип</TableCell>
                    <TableCell>Кампания</TableCell>
                    <TableCell>Сумма</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {bonusHistory.map((b, i) => (
                    <TableRow key={i}>
                      <TableCell>{b.date}</TableCell>
                      <TableCell>
                        <Chip label={b.type} color={b.type === "Начисление" ? "success" : "warning"} size="small" />
                      </TableCell>
                      <TableCell>{b.campaign}</TableCell>
                      <TableCell>
                        <Typography sx={{
                          fontWeight: 700,
                          color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c"
                        }}>{b.value}</Typography>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TabPanel>
            <TabPanel value={tab} index={3}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Дата</TableCell>
                    <TableCell>Канал</TableCell>
                    <TableCell>Тип</TableCell>
                    <TableCell>Текст</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {messageHistory.map((m, i) => (
                    <TableRow key={i}>
                      <TableCell>{m.date}</TableCell>
                      <TableCell>
                        {m.channel === "SMS"
                          ? <Chip icon={<SmsIcon fontSize="small" />} label="SMS" size="small" />
                          : <Chip icon={<TelegramIcon fontSize="small" />} label="Telegram" size="small" color="primary" />}
                      </TableCell>
                      <TableCell>
                        <Chip label={m.direction} size="small" color={m.direction === "Исходящее" ? "success" : "warning"} />
                      </TableCell>
                      <TableCell>{m.text}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TabPanel>
            <TabPanel value={tab} index={4}>
              <Stack direction="row" spacing={1} flexWrap="wrap">
                {profile.interests.map((interest, i) => (
                  <Chip key={i} label={interest} sx={{
                    fontSize: 17, p: 2, m: .5,
                    bgcolor: "#f7ebff",
                    color: "#6610f2", fontWeight: 700
                  }} />
                ))}
              </Stack>
            </TabPanel>
          </Box>
        </Box>
      </Box>
    </Box>
  );
}
