import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider, Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import EditIcon from "@mui/icons-material/Edit";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";
import EmailIcon from "@mui/icons-material/Email";
import TimelineIcon from "@mui/icons-material/Timeline";
import SupportAgentIcon from "@mui/icons-material/SupportAgent";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import LoyaltyIcon from "@mui/icons-material/Loyalty";
import TagIcon from "@mui/icons-material/Tag";

function a11yProps(index) {
  return {
    id: `tab-${index}`,
    "aria-controls": `tabpanel-${index}`,
  };
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

export default function CustomerProfileCard() {
  const [tab, setTab] = useState(0);

  // Данные
  const tags = ["VIP", "Низкая активность", "Гость сайта", "Риск оттока"];
  const timeline = [
    { date: "10.06.2025", text: "Вход на сайт" },
    { date: "09.06.2025", text: "Запрос баланса" },
    { date: "08.06.2025", text: "Просмотр условий акции" }
  ];
  const purchases = [
    { date: "30.05.2025", item: "Бронирование отеля", amount: "4 400 ₽", status: "Оплачено" },
    { date: "03.03.2025", item: "Ски-пасс", amount: "25 266,7 ₽", status: "Оплачено" },
    { date: "03.03.2025", item: "Бронирование отеля", amount: "12 615,83 ₽", status: "Отменено" },
    { date: "30.08.2024", item: "Сувенирный магазин", amount: "20 ₽", status: "Оплачено" }
  ];
  const support = [
    { date: "02.06.2025", type: "Чат", text: "Проблема с бронированием" },
    { date: "25.05.2025", type: "Email", text: "Запрос на возврат" },
    { date: "12.05.2025", type: "Телефон", text: "Вопрос по акциям" }
  ];
  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление" },
    { date: "20.05.2025", value: "-375", type: "Списание" },
    { date: "10.04.2025", value: "+300", type: "Начисление" }
  ];
  const interests = [
    "Горные туры", "Отели 4–5*", "Зимний отдых", "Фитнес и SPA",
    "Детские активности", "Экстрим", "Кулинария"
  ];

  return (
    <Box sx={{
      bgcolor: "#F4F6FA",
      minHeight: "100vh",
      py: 5,
      px: 1
    }}>
      <Box
        sx={{
          maxWidth: 1150,
          mx: "auto",
          bgcolor: "#fff",
          borderRadius: 4,
          boxShadow: "0 4px 24px rgba(20,25,55,0.06)",
          px: { xs: 2, sm: 5 },
          py: { xs: 3, sm: 5 }
        }}
      >
        {/* Header */}
        <Grid container spacing={3} alignItems="center">
          <Grid item>
            <Avatar
              sx={{
                width: 86,
                height: 86,
                fontSize: 40,
                bgcolor: "#EEF1F7",
                color: "#4361EE"
              }}
            >
              МЛ
            </Avatar>
          </Grid>
          <Grid item xs>
            <Typography variant="h4" fontWeight={700} color="#22334d" sx={{ letterSpacing: 1 }}>
              Мария Летунова
            </Typography>
            <Typography color="text.secondary" sx={{ fontSize: 17, mt: .5, mb: 1 }}>
              <PersonIcon fontSize="small" sx={{ verticalAlign: "middle", mr: 1 }} />
              ID: 874392 &nbsp;|&nbsp; 36 лет &nbsp;|&nbsp; Краснодар
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {tags.map((t, idx) => (
                <Chip
                  key={idx}
                  label={t}
                  size="small"
                  color={t === "VIP" ? "success" : t === "Риск оттока" ? "error" : "default"}
                  variant={t === "VIP" ? "filled" : "outlined"}
                  sx={{
                    borderRadius: 1,
                    bgcolor: t === "VIP" ? "#e5f8ef" : undefined,
                    color: t === "VIP" ? "#219653" : undefined,
                    fontWeight: 600
                  }}
                />
              ))}
            </Stack>
          </Grid>
          <Grid item>
            <Stack direction="row" spacing={1}>
              <Tooltip title="Email">
                <IconButton sx={{
                  bgcolor: "#F1F3F6",
                  "&:hover": { bgcolor: "#e8ebf0" }
                }}>
                  <EmailIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
              <Tooltip title="WhatsApp">
                <IconButton sx={{
                  bgcolor: "#F1F3F6",
                  "&:hover": { bgcolor: "#e8ebf0" }
                }}>
                  <WhatsAppIcon fontSize="medium" sx={{ color: "#32CD32" }} />
                </IconButton>
              </Tooltip>
              <Tooltip title="Редактировать">
                <IconButton sx={{
                  bgcolor: "#F1F3F6",
                  "&:hover": { bgcolor: "#e8ebf0" }
                }}>
                  <EditIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
            </Stack>
          </Grid>
        </Grid>

        <Divider sx={{ my: 3 }} />

        {/* Tabs */}
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          aria-label="customer tabs"
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2 }}
        >
          <Tab label="Профиль" {...a11yProps(0)} />
          <Tab label="Бонусы" {...a11yProps(1)} />
          <Tab label="История покупок" {...a11yProps(2)} />
          <Tab label="Активность" {...a11yProps(3)} />
          <Tab label="Обращения" {...a11yProps(4)} />
          <Tab label="Интересы" {...a11yProps(5)} />
        </Tabs>

        {/* Tab Content */}
        <TabPanel value={tab} index={0}>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6} md={3}>
              <Paper elevation={0} sx={{ bgcolor: "#F4F6FA", p: 2, borderRadius: 3 }}>
                <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
                  Email
                </Typography>
                <Typography fontSize={16} fontWeight={600} mb={.5}>
                  maria.letunova@yandex.ru
                </Typography>
                <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
                  Телефон
                </Typography>
                <Typography fontSize={16} fontWeight={600}>
                  +7 988 509-96-62
                </Typography>
                <Divider sx={{ my: 2 }} />
                <Typography fontWeight={700} mb={.7}>Дата рождения</Typography>
                <Typography fontSize={16}>16.01.1989</Typography>
                <Typography fontWeight={700} mb={.7} mt={1}>Город</Typography>
                <Typography fontSize={16}>Краснодар</Typography>
              </Paper>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Paper elevation={0} sx={{ bgcolor: "#F4F6FA", p: 2, borderRadius: 3 }}>
                <Typography fontWeight={700} mb={1.5} sx={{ fontSize: 18 }}>LTV</Typography>
                <Typography fontSize={24} fontWeight={700}>46 301,3 ₽</Typography>
                <Typography fontSize={15} color="text.secondary">Lifetime Value</Typography>
                <Divider sx={{ my: 2 }} />
                <Typography fontWeight={700}>Теги/Сегменты</Typography>
                <Stack direction="row" spacing={1} flexWrap="wrap" mt={1}>
                  {tags.map((tag, idx) => (
                    <Chip
                      key={idx}
                      label={tag}
                      size="small"
                      color={tag === "VIP" ? "success" : tag === "Риск оттока" ? "error" : "primary"}
                      variant={tag === "VIP" ? "filled" : "outlined"}
                    />
                  ))}
                </Stack>
              </Paper>
            </Grid>
          </Grid>
        </TabPanel>

        <TabPanel value={tab} index={1}>
          <Paper elevation={0} sx={{ bgcolor: "#F4F6FA", p: 2, borderRadius: 3 }}>
            <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <LoyaltyIcon sx={{ fontSize: 22, mr: .8, color: "#6c8af6" }} /> Бонусы
            </Typography>
            <Typography fontSize={22} fontWeight={700}>1 563</Typography>
            <Typography fontSize={15} color="text.secondary">Баланс</Typography>
            <Divider sx={{ my: 1 }} />
            <Typography fontWeight={600} sx={{ mt: 1, mb: .5 }}>История бонусов:</Typography>
            {bonusHistory.map((b, idx) => (
              <Typography
                key={idx}
                fontSize={15}
                color={b.type === "Начисление" ? "success.main" : "error.main"}
                sx={{ fontWeight: 500 }}
              >
                {b.value} бонусов — {b.date}
              </Typography>
            ))}
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={2}>
          <Paper elevation={0} sx={{ bgcolor: "#FAFBFD", borderRadius: 3, p: 2, mb: 3 }}>
            <Typography fontWeight={700} mb={2} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <ShoppingCartIcon sx={{ fontSize: 20, mr: 1, color: "#faad13" }} /> История покупок
            </Typography>
            <Table size="small" sx={{ background: "none" }}>
              <TableHead>
                <TableRow>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Дата</TableCell>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Товар/Услуга</TableCell>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Сумма</TableCell>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Статус</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {purchases.map((p, idx) => (
                  <TableRow key={idx}>
                    <TableCell>{p.date}</TableCell>
                    <TableCell>{p.item}</TableCell>
                    <TableCell><b>{p.amount}</b></TableCell>
                    <TableCell>
                      <Chip
                        label={p.status}
                        size="small"
                        color={p.status === "Оплачено" ? "success" : "error"}
                        variant="outlined"
                        sx={{
                          fontWeight: 600,
                          bgcolor: p.status === "Оплачено" ? "#eafbe7" : "#fff2f2",
                          color: p.status === "Оплачено" ? "#1bb934" : "#e44d41",
                          border: "none"
                        }}
                      />
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={3}>
          <Paper elevation={0} sx={{ bgcolor: "#FAFBFD", borderRadius: 3, p: 2 }}>
            <Typography fontWeight={700} mb={2} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <TimelineIcon sx={{ fontSize: 20, mr: 1, color: "#52b69a" }} /> Лента активности
            </Typography>
            <Stack spacing={1.3}>
              {timeline.map((event, idx) => (
                <Box key={idx} sx={{ pl: .5, borderLeft: "3px solid #e3e7ef" }}>
                  <Typography fontSize={15} color="#22334d">
                    <span style={{ color: "#a2abb9", fontWeight: 500 }}>{event.date}</span> — {event.text}
                  </Typography>
                </Box>
              ))}
            </Stack>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={4}>
          <Paper elevation={0} sx={{ bgcolor: "#FAFBFD", borderRadius: 3, p: 2 }}>
            <Typography fontWeight={700} mb={2} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <SupportAgentIcon sx={{ fontSize: 20, mr: 1, color: "#4361ee" }} /> Обращения
            </Typography>
            <Stack spacing={1.3}>
              {support.map((s, idx) => (
                <Box key={idx} sx={{ pl: .5 }}>
                  <Typography fontSize={15} color="#22334d">
                    <span style={{ color: "#a2abb9", fontWeight: 500 }}>{s.date}</span> — {s.type}: {s.text}
                  </Typography>
                </Box>
              ))}
            </Stack>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={5}>
          <Paper elevation={0} sx={{ bgcolor: "#F4F6FA", p: 2, borderRadius: 3 }}>
            <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <TagIcon sx={{ fontSize: 22, mr: .8, color: "#b982e7" }} /> Интересы
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {interests.map((i, idx) => (
                <Chip
                  key={idx}
                  label={i}
                  size="small"
                  variant="outlined"
                  sx={{
                    bgcolor: "#fff",
                    borderRadius: 2,
                    fontSize: 14,
                    mb: .5,
                    borderColor: "#eee",
                    color: "#7e7e7e"
                  }}
                />
              ))}
            </Stack>
          </Paper>
        </TabPanel>
      </Box>
    </Box>
  );
}
