import React, { useState } from "react";
import {
  Box, Typography, Stack, Chip, Button, Tabs, Tab, Paper
} from "@mui/material";
import TelegramIcon from "@mui/icons-material/Telegram";
import PlaceIcon from "@mui/icons-material/Place";
import WarningAmberIcon from "@mui/icons-material/WarningAmber";
import CheckIcon from "@mui/icons-material/Check";

const profile = {
  ltv: "188 200 ₽",
  bonus: "2 413",
  age: 36,
  city: "Краснодар",
  regSource: "Сайт",
  regDate: "2017-11-03",
  preferredChannel: "Telegram",
  rfm: "Gold",
  segments: [
    "VIP",
    "По интересам: SPA",
    { type: "geo", label: "Красная поляна" },
    "RFM: Gold",
  ],
  interests: [
    "Горные туры",
    "Зимний отдых",
    "SPA и релакс",
    "Детские активности",
    "Экстрим"
  ],
  favProducts: [
    "Ски-пасс взрослый",
    "SPA-процедуры"
  ],
  agreements: [
    { name: "Рекламные рассылки", accepted: true },
    { name: "Обработка ПДн", accepted: true },
    { name: "Push-уведомления", accepted: false }
  ],
  status: "Постоянный клиент",
  churnRisk: true
};

function TabPanel({ children, value, index }) {
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      style={{ width: "100%" }}
    >
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

export default function CustomerProfileCard() {
  const [tab, setTab] = useState(0);

  return (
    <Box sx={{
      minHeight: "100vh",
      bgcolor: "#f7f8fa",
      pt: 0, pb: 5,
      px: { xs: 1, md: 4 }
    }}>
      <Paper
        sx={{
          maxWidth: 1350,
          mx: "auto",
          mt: 4,
          mb: 2,
          borderRadius: 6,
          overflow: "hidden",
          position: "relative",
          background: "linear-gradient(120deg, #f3f6fd 60%, #f7faff 100%)",
          boxShadow: "0 1px 12px #e7ecfa",
          px: 5,
          py: 4
        }}
      >
        {/* TOP INFO */}
        <Stack direction="row" spacing={6} flexWrap="wrap" alignItems="flex-start" mb={2}>
          <Stack spacing={0.5} minWidth={230}>
            <Typography fontSize={19} color="text.secondary">Общий LTV</Typography>
            <Typography fontSize={34} fontWeight={800}>{profile.ltv}</Typography>
            <Typography fontSize={18} color="text.secondary" mt={3}>Предпочтительный канал</Typography>
            <Stack direction="row" alignItems="center" spacing={1} mt={0.7}>
              <TelegramIcon sx={{ color: "#229ed9", fontSize: 28 }} />
              <Typography fontSize={26} fontWeight={700}>Telegram</Typography>
            </Stack>
          </Stack>
          <Stack spacing={0.5} minWidth={200}>
            <Typography fontSize={19} color="text.secondary">Бонусы</Typography>
            <Typography fontSize={34} fontWeight={800}>{profile.bonus}</Typography>
            <Typography fontSize={18} color="text.secondary" mt={3}>RFM</Typography>
            <Chip
              label="Gold"
              sx={{
                bgcolor: "#ff8200", color: "#fff", fontWeight: 700, px: 2,
                fontSize: 19, borderRadius: 2, height: 36, mt: .7
              }}
            />
          </Stack>
          <Stack spacing={0.5} minWidth={210}>
            <Typography fontSize={19} color="text.secondary">Возраст</Typography>
            <Typography fontSize={34} fontWeight={800}>{profile.age} лет</Typography>
            <Typography fontSize={18} color="text.secondary" mt={3}>Источник регистрации</Typography>
            <Typography fontSize={25} fontWeight={700} mt={0.7}>{profile.regSource}</Typography>
          </Stack>
          <Stack spacing={0.5} minWidth={240}>
            <Typography fontSize={19} color="text.secondary">Город</Typography>
            <Typography fontSize={34} fontWeight={800}>{profile.city}</Typography>
            <Typography fontSize={18} color="text.secondary" mt={3}>Дата регистрации</Typography>
            <Typography fontSize={25} fontWeight={700} mt={0.7}>{profile.regDate}</Typography>
          </Stack>
        </Stack>
        <Box sx={{ borderBottom: "2px solid #e7eaf0", mt: 2, mb: 2 }} />
        {/* Segments & Interests */}
        <Stack direction="row" spacing={6} flexWrap="wrap" alignItems="flex-start" mt={1} mb={1}>
          <Box>
            <Typography fontSize={20} fontWeight={500} color="text.secondary" mb={1}>
              Сегменты и интересы:
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap" mb={1}>
              {profile.segments.map((seg, i) =>
                typeof seg === "object" && seg.type === "geo" ? (
                  <Chip
                    key={i}
                    icon={<PlaceIcon sx={{ color: "#30a17d" }} />}
                    label={`Geo: ${seg.label}`}
                    sx={{
                      bgcolor: "#e6fbf8",
                      color: "#10867e",
                      fontWeight: 700,
                      fontSize: 18,
                      height: 38,
                      px: 2.3,
                      borderRadius: 2,
                      mr: 1, mb: 1
                    }}
                  />
                ) : (
                  <Chip
                    key={i}
                    label={seg}
                    sx={{
                      bgcolor: seg === "VIP" ? "#f4f7fa" : "#e8edfa",
                      color: seg === "VIP" ? "#222" : "#2642b6",
                      fontWeight: 700,
                      fontSize: 18,
                      height: 38,
                      px: 2.3,
                      borderRadius: 2,
                      mr: 1, mb: 1
                    }}
                  />
                )
              )}
            </Stack>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.interests.map((item, idx) => (
                <Chip
                  key={idx}
                  label={item}
                  sx={{
                    bgcolor: "#f7f8fa",
                    color: "#1a2637",
                    fontWeight: 400,
                    fontSize: 18,
                    height: 36,
                    px: 2.3,
                    borderRadius: 2,
                    mr: 1, mb: 1
                  }}
                />
              ))}
            </Stack>
          </Box>
          <Box>
            <Typography fontSize={20} fontWeight={500} color="text.secondary" mb={1}>
              Любимые продукты:
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.favProducts.map((prod, idx) => (
                <Chip
                  key={idx}
                  label={prod}
                  sx={{
                    bgcolor: "#ffe4ea",
                    color: "#2d142c",
                    fontWeight: 700,
                    fontSize: 19,
                    height: 36,
                    px: 2.3,
                    borderRadius: 2,
                    mr: 1, mb: 1
                  }}
                />
              ))}
            </Stack>
          </Box>
        </Stack>
        {/* Agreements and Status */}
        <Stack direction="row" spacing={4} alignItems="center" flexWrap="wrap" mt={2}>
          <Box>
            <Typography fontSize={20} fontWeight={500} color="text.secondary" mb={1}>
              Согласия:
            </Typography>
            <Stack spacing={1.1}>
              {profile.agreements.map((agr, idx) => (
                <Button
                  key={idx}
                  variant="contained"
                  disableElevation
                  sx={{
                    minWidth: 280,
                    bgcolor: agr.accepted ? "#249d38" : "#dedede",
                    color: "#fff",
                    fontWeight: 700,
                    fontSize: 21,
                    mb: .5,
                    borderRadius: 2,
                    '&:hover': { bgcolor: agr.accepted ? "#21862f" : "#bbbbbb" }
                  }}
                  startIcon={agr.accepted ? <CheckIcon /> : null}
                >
                  {agr.name}
                </Button>
              ))}
            </Stack>
          </Box>
          <Box sx={{ ml: 5 }}>
            <Typography fontSize={20} fontWeight={500} color="text.secondary" mb={1}>
              Статус:
            </Typography>
            <Typography fontSize={25} fontWeight={700} sx={{ color: "#444", mb: 1 }}>
              {profile.status}
            </Typography>
            {profile.churnRisk &&
              <Chip
                icon={<WarningAmberIcon sx={{ color: "#d16d0d", fontSize: 23 }} />}
                label="Есть риск ухода"
                sx={{
                  bgcolor: "#ff8200",
                  color: "#fff",
                  fontWeight: 700,
                  fontSize: 19,
                  px: 2.5,
                  height: 39,
                  borderRadius: 2
                }}
              />
            }
          </Box>
        </Stack>
      </Paper>
      {/* ------ ТАБЫ ------ */}
      <Paper
        sx={{
          maxWidth: 1350,
          mx: "auto",
          borderRadius: 6,
          px: { xs: 1, md: 4 },
          py: { xs: 1, md: 3 },
          mt: 2,
          minHeight: 300
        }}
      >
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2, fontWeight: 800 }}
        >
          <Tab label="Покупки" />
          <Tab label="Бонусы" />
          <Tab label="Промокоды" />
          <Tab label="Подарки" />
          <Tab label="История сообщений" />
          <Tab label="NPS" />
          <Tab label="Активности" />
          <Tab label="Задачи" />
        </Tabs>
        <TabPanel value={tab} index={0}>
          {/* Сюда подставь свою таблицу покупок и чеков */}
          <Typography color="text.secondary">Здесь будет история покупок...</Typography>
        </TabPanel>
        <TabPanel value={tab} index={1}>
          <Typography color="text.secondary">Бонусы...</Typography>
        </TabPanel>
        <TabPanel value={tab} index={2}>
          <Typography color="text.secondary">Промокоды...</Typography>
        </TabPanel>
        <TabPanel value={tab} index={3}>
          <Typography color="text.secondary">Подарки...</Typography>
        </TabPanel>
        <TabPanel value={tab} index={4}>
          <Typography color="text.secondary">История сообщений...</Typography>
        </TabPanel>
        <TabPanel value={tab} index={5}>
          <Typography color="text.secondary">NPS...</Typography>
        </TabPanel>
        <TabPanel value={tab} index={6}>
          <Typography color="text.secondary">Активности...</Typography>
        </TabPanel>
        <TabPanel value={tab} index={7}>
          <Typography color="text.secondary">Задачи...</Typography>
        </TabPanel>
      </Paper>
    </Box>
  );
}
