sourcesContent:
import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Select, MenuItem, OutlinedInput, Checkbox, ListItemText
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import EditIcon from "@mui/icons-material/Edit";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";
import EmailIcon from "@mui/icons-material/Email";
import TimelineIcon from "@mui/icons-material/Timeline";
import SupportAgentIcon from "@mui/icons-material/SupportAgent";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import LoyaltyIcon from "@mui/icons-material/Loyalty";
import TagIcon from "@mui/icons-material/Tag";
import SmsIcon from "@mui/icons-material/Sms";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";

// -- Tabs helpers --
function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

const TAGS = [
  "VIP", "Низкая активность", "Гость сайта", "Риск оттока", "Корпоративный", "Постоянный клиент"
];

export default function CustomerProfileCard() {
  // Tabs
  const [tab, setTab] = useState(0);

  // Покупки с деталями чеков
  const purchases = [
    {
      date: "30.05.2025",
      item: "Бронирование отеля",
      amount: "4 400 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 4400 },
      ]
    },
    {
      date: "03.03.2025",
      item: "Ски-пасс",
      amount: "25 266,7 ₽",
      status: "Оплачено",
      pos: "Касса Горки Молл",
      receiptId: "CHK-9823",
      employee: "Иван Калашников",
      receipt: [
        { name: "Ски-пасс взрослый", qty: 2, price: 12000 },
        { name: "Ски-пасс детский", qty: 1, price: 5266.7 },
      ]
    },
    {
      date: "03.03.2025",
      item: "Бронирование отеля",
      amount: "12 615,83 ₽",
      status: "Отменено",
      pos: "Сайт",
      receiptId: "CHK-9765",
      employee: "—",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 12615.83 },
      ]
    },
    {
      date: "30.08.2024",
      item: "Сувенирный магазин",
      amount: "20 ₽",
      status: "Оплачено",
      pos: "Магазин GIFT",
      receiptId: "CHK-8431",
      employee: "Алексей Чернов",
      receipt: [
        { name: "Магнит", qty: 1, price: 20 },
      ]
    }
  ];

  // Для статистики (см. предыдущие сообщения)
  const paidPurchases = purchases.filter(p => p.status === "Оплачено");
  const totalPurchases = paidPurchases.length;
  const avgCheck =
    totalPurchases === 0
      ? 0
      : Math.round(
          paidPurchases
            .map(p => Number(p.amount.replace(/[ ₽,]/g, "").replace(",", ".")))
            .reduce((a, b) => a + b, 0) / totalPurchases
        );
  const lastPurchaseDate = paidPurchases.length
    ? paidPurchases
        .map(p => p.date)
        .sort((a, b) => new Date(b.split(".").reverse().join("-")) - new Date(a.split(".").reverse().join("-")))[0]
    : "-";

  // Profile (редактируемое)
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    age: 36,
    city: "Краснодар",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    tags: ["VIP", "Низкая активность", "Гость сайта", "Риск оттока"],
    ltv: "46 301,3 ₽",
    bonus: "1 563",
    interests: [
      "Горные туры", "Отели 4–5*", "Зимний отдых", "Фитнес и SPA",
      "Детские активности", "Экстрим", "Кулинария"
    ]
  });

  // Остальные тестовые данные
  const timeline = [
    { date: "10.06.2025", text: "Вход на сайт" },
    { date: "09.06.2025", text: "Запрос баланса" },
    { date: "08.06.2025", text: "Просмотр условий акции" }
  ];
  const support = [
    { date: "02.06.2025", type: "Чат", text: "Проблема с бронированием" },
    { date: "25.05.2025", type: "Email", text: "Запрос на возврат" },
    { date: "12.05.2025", type: "Телефон", text: "Вопрос по акциям" }
  ];
  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление" },
    { date: "20.05.2025", value: "-375", type: "Списание" },
    { date: "10.04.2025", value: "+300", type: "Начисление" }
  ];
  const messageHistory = [
    { date: "10.06.2025", channel: "Email", direction: "Исходящее", text: "Спасибо за ваш отзыв! Ваш бонус начислен." },
    { date: "05.06.2025", channel: "SMS", direction: "Входящее", text: "Получил купон, спасибо!" },
    { date: "30.05.2025", channel: "WhatsApp", direction: "Исходящее", text: "Ваше бронирование подтверждено." }
  ];

  // Edit dialog state
  const [editOpen, setEditOpen] = useState(false);
  const [form, setForm] = useState(profile);

  // Receipt modal
  const [receiptModal, setReceiptModal] = useState({ open: false, purchase: null });
  const openReceipt = (purchase) => setReceiptModal({ open: true, purchase });
  const closeReceipt = () => setReceiptModal({ open: false, purchase: null });

  // Открыть редактор
  const handleEdit = () => {
    setForm(profile);
    setEditOpen(true);
  };
  // Закрыть редактор
  const handleEditClose = () => setEditOpen(false);
  // Сохранить изменения
  const handleEditSave = () => {
    setProfile({
      ...form,
      age: form.dob ? (new Date().getFullYear() - Number(form.dob.slice(0, 4))) : profile.age
    });
    setEditOpen(false);
  };

  // Обработка изменений формы
  const handleFormChange = (field, value) => {
    setForm((prev) => ({
      ...prev,
      [field]: value
    }));
  };

  // Теги — мультиселект
  const handleTagsChange = (event) => {
    const {
      target: { value },
    } = event;
    setForm((prev) => ({
      ...prev,
      tags: typeof value === 'string' ? value.split(',') : value,
    }));
  };

  return (
    <Box sx={{ bgcolor: "#F4F6FA", minHeight: "100vh", py: 5, px: 1 }}>
      <Box
        sx={{
          maxWidth: 1150,
          mx: "auto",
          bgcolor: "#fff",
          borderRadius: 4,
          boxShadow: "0 4px 24px rgba(20,25,55,0.06)",
          px: { xs: 2, sm: 5 },
          py: { xs: 3, sm: 5 }
        }}
      >
        {/* Верхняя общая карточка */}
        <Grid container spacing={3} alignItems="center" mb={1}>
          <Grid item>
            <Avatar
              sx={{
                width: 86,
                height: 86,
                fontSize: 40,
                bgcolor: "#EEF1F7",
                color: "#4361EE"
              }}
            >
              {profile.name
                .split(" ")
                .map((part) => part[0])
                .join("")
                .toUpperCase()}
            </Avatar>
          </Grid>
          <Grid item xs>
            <Typography variant="h4" fontWeight={700} color="#22334d" sx={{ letterSpacing: 1 }}>
              {profile.name}
            </Typography>
            <Typography color="text.secondary" sx={{ fontSize: 17, mt: .5, mb: 1 }}>
              <PersonIcon fontSize="small" sx={{ verticalAlign: "middle", mr: 1 }} />
              ID: {profile.id} &nbsp;|&nbsp; {profile.age} лет &nbsp;|&nbsp; {profile.city}
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.tags.map((t, idx) => (
                <Chip
                  key={idx}
                  label={t}
                  size="small"
                  color={t === "VIP" ? "success" : t === "Риск оттока" ? "error" : "default"}
                  variant={t === "VIP" ? "filled" : "outlined"}
                  sx={{
                    borderRadius: 1,
                    bgcolor: t === "VIP" ? "#e5f8ef" : undefined,
                    color: t === "VIP" ? "#219653" : undefined,
                    fontWeight: 600
                  }}
                />
              ))}
            </Stack>
            <Stack direction="row" spacing={1} mt={2} flexWrap="wrap">
              <Chip icon={<EmailIcon />} label={profile.email} sx={{ mr: 1, bgcolor: "#F4F6FA" }} />
              <Chip label={profile.phone} sx={{ mr: 1, bgcolor: "#F4F6FA" }} />
              <Chip label={profile.dob.split("-").reverse().join(".")} sx={{ mr: 1, bgcolor: "#F4F6FA" }} />
            </Stack>
          </Grid>
          <Grid item>
            <Stack direction="row" spacing={1}>
              <Tooltip title="Email">
                <IconButton sx={{
                  bgcolor: "#F1F3F6",
                  "&:hover": { bgcolor: "#e8ebf0" }
                }}>
                  <EmailIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
              <Tooltip title="WhatsApp">
                <IconButton sx={{
                  bgcolor: "#F1F3F6",
                  "&:hover": { bgcolor: "#e8ebf0" }
                }}>
                  <WhatsAppIcon fontSize="medium" sx={{ color: "#32CD32" }} />
                </IconButton>
              </Tooltip>
              <Tooltip title="Редактировать профиль">
                <IconButton
                  sx={{ bgcolor: "#F1F3F6", "&:hover": { bgcolor: "#e8ebf0" } }}
                  onClick={handleEdit}
                >
                  <EditIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
            </Stack>
          </Grid>
        </Grid>
        {/* ДОП. СТАТИСТИКА */}
        <Grid container spacing={3} mt={0.5} mb={1}>
          <Grid item xs={12} sm={6} md={2.2}>
            <Paper elevation={0} sx={{
              bgcolor: "#F4F6FA", p: 2, borderRadius: 3, boxShadow: "none"
            }}>
              <Typography fontWeight={600} sx={{ fontSize: 16, color: "#8b8b8b" }}>Покупок</Typography>
              <Typography fontWeight={700} sx={{ fontSize: 22 }}>{totalPurchases}</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={2.2}>
            <Paper elevation={0} sx={{
              bgcolor: "#F4F6FA", p: 2, borderRadius: 3, boxShadow: "none"
            }}>
              <Typography fontWeight={600} sx={{ fontSize: 16, color: "#8b8b8b" }}>Средний чек</Typography>
              <Typography fontWeight={700} sx={{ fontSize: 22 }}>{avgCheck.toLocaleString()} ₽</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={2.2}>
            <Paper elevation={0} sx={{
              bgcolor: "#F4F6FA", p: 2, borderRadius: 3, boxShadow: "none"
            }}>
              <Typography fontWeight={600} sx={{ fontSize: 16, color: "#8b8b8b" }}>Последняя покупка</Typography>
              <Typography fontWeight={700} sx={{ fontSize: 18 }}>{lastPurchaseDate}</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={2.2}>
            <Paper elevation={0} sx={{
              bgcolor: "#F4F6FA", p: 2, borderRadius: 3, boxShadow: "none"
            }}>
              <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
                <LoyaltyIcon sx={{ fontSize: 22, mr: .8, color: "#6c8af6" }} /> Бонусы
              </Typography>
              <Typography fontSize={20} fontWeight={700}>{profile.bonus}</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={2.2}>
            <Paper elevation={0} sx={{
              bgcolor: "#F4F6FA", p: 2, borderRadius: 3, boxShadow: "none"
            }}>
              <Typography fontWeight={700} mb={1.5} sx={{ fontSize: 16 }}>LTV</Typography>
              <Typography fontSize={18} fontWeight={700}>{profile.ltv}</Typography>
            </Paper>
          </Grid>
        </Grid>
        {/* Интересы */}
        <Grid container spacing={3} mt={0.5}>
          <Grid item xs={12}>
            <Paper elevation={0} sx={{
              bgcolor: "#F4F6FA", p: 2, borderRadius: 3, boxShadow: "none"
            }}>
              <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
                <TagIcon sx={{ fontSize: 22, mr: .8, color: "#b982e7" }} /> Интересы
              </Typography>
              <Stack direction="row" spacing={1} flexWrap="wrap">
                {profile.interests.map((i, idx) => (
                  <Chip
                    key={idx}
                    label={i}
                    size="small"
                    variant="outlined"
                    sx={{
                      bgcolor: "#fff",
                      borderRadius: 2,
                      fontSize: 14,
                      mb: .5,
                      borderColor: "#eee",
                      color: "#7e7e7e"
                    }}
                  />
                ))}
              </Stack>
            </Paper>
          </Grid>
        </Grid>
        <Divider sx={{ my: 3 }} />

        {/* Tabs */}
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          aria-label="customer tabs"
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2 }}
        >
          <Tab label="Бонусы" />
          <Tab label="История покупок" />
          <Tab label="Активность" />
          <Tab label="Обращения" />
          <Tab label="Интересы" />
          <Tab icon={<SmsIcon />} iconPosition="start" label="История сообщений" />
        </Tabs>

        {/* Tab Content */}
        <TabPanel value={tab} index={0}>
          <Paper elevation={0} sx={{ bgcolor: "#F4F6FA", p: 2, borderRadius: 3 }}>
            <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <LoyaltyIcon sx={{ fontSize: 22, mr: .8, color: "#6c8af6" }} /> Бонусы
            </Typography>
            <Typography fontSize={22} fontWeight={700}>{profile.bonus}</Typography>
            <Typography fontSize={15} color="text.secondary">Баланс</Typography>
            <Divider sx={{ my: 1 }} />
            <Typography fontWeight={600} sx={{ mt: 1, mb: .5 }}>История бонусов:</Typography>
            {bonusHistory.map((b, idx) => (
              <Typography
                key={idx}
                fontSize={15}
                color={b.type === "Начисление" ? "success.main" : "error.main"}
                sx={{ fontWeight: 500 }}
              >
                {b.value} бонусов — {b.date}
              </Typography>
            ))}
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={1}>
          <Paper elevation={0} sx={{ bgcolor: "#FAFBFD", borderRadius: 3, p: 2, mb: 3 }}>
            <Typography fontWeight={700} mb={2} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <ShoppingCartIcon sx={{ fontSize: 20, mr: 1, color: "#faad13" }} /> История покупок
            </Typography>
            <Table size="small" sx={{ background: "none" }}>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Товар/Услуга</TableCell>
                  <TableCell>Точка продаж</TableCell>
                  <TableCell>ID чека</TableCell>
                  <TableCell>Сотрудник</TableCell>
                  <TableCell>Сумма</TableCell>
                  <TableCell>Статус</TableCell>
                  <TableCell></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {purchases.map((p, idx) => (
                  <TableRow key={idx}>
                    <TableCell>{p.date}</TableCell>
                    <TableCell>{p.item}</TableCell>
                    <TableCell>{p.pos}</TableCell>
                    <TableCell>
                      <Chip label={p.receiptId} size="small" icon={<ReceiptLongIcon sx={{ fontSize: 18 }} />} />
                    </TableCell>
                    <TableCell>{p.employee}</TableCell>
                    <TableCell><b>{p.amount}</b></TableCell>
                    <TableCell>
                      <Chip
                        label={p.status}
                        size="small"
                        color={p.status === "Оплачено" ? "success" : "error"}
                        variant="outlined"
                        sx={{
                          fontWeight: 600,
                          bgcolor: p.status === "Оплачено" ? "#eafbe7" : "#fff2f2",
                          color: p.status === "Оплачено" ? "#1bb934" : "#e44d41",
                          border: "none"
                        }}
                      />
                    </TableCell>
                    <TableCell>
                      <Button size="small" variant="outlined" onClick={() => openReceipt(p)}>
                        Состав
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
          {/* Модалка с деталями чека */}
          <Dialog open={receiptModal.open} onClose={closeReceipt} maxWidth="xs" fullWidth>
            <DialogTitle>
              Чек {receiptModal.purchase?.receiptId} — {receiptModal.purchase?.date}
            </DialogTitle>
            <DialogContent>
              <Typography sx={{ mb: 1 }}>
                <b>Точка продаж:</b> {receiptModal.purchase?.pos}<br />
                <b>Сотрудник:</b> {receiptModal.purchase?.employee}
              </Typography>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Товар</TableCell>
                    <TableCell>Кол-во</TableCell>
                    <TableCell>Цена</TableCell>
                    <TableCell>Сумма</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {receiptModal.purchase?.receipt.map((row, i) => (
                    <TableRow key={i}>
                      <TableCell>{row.name}</TableCell>
                      <TableCell>{row.qty}</TableCell>
                      <TableCell>{row.price.toLocaleString()} ₽</TableCell>
                      <TableCell>{(row.qty * row.price).toLocaleString()} ₽</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </DialogContent>
            <DialogActions>
              <Button onClick={closeReceipt}>Закрыть</Button>
            </DialogActions>
          </Dialog>
        </TabPanel>

        <TabPanel value={tab} index={2}>
          <Paper elevation={0} sx={{ bgcolor: "#FAFBFD", borderRadius: 3, p: 2 }}>
            <Typography fontWeight={700} mb={2} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <TimelineIcon sx={{ fontSize: 20, mr: 1, color: "#52b69a" }} /> Лента активности
            </Typography>
            <Stack spacing={1.3}>
              {timeline.map((event, idx) => (
                <Box key={idx} sx={{ pl: .5, borderLeft: "3px solid #e3e7ef" }}>
                  <Typography fontSize={15} color="#22334d">
                    <span style={{ color: "#a2abb9", fontWeight: 500 }}>{event.date}</span> — {event.text}
                  </Typography>
                </Box>
              ))}
            </Stack>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={3}>
          <Paper elevation={0} sx={{ bgcolor: "#FAFBFD", borderRadius: 3, p: 2 }}>
            <Typography fontWeight={700} mb={2} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <SupportAgentIcon sx={{ fontSize: 20, mr: 1, color: "#4361ee" }} /> Обращения
            </Typography>
            <Stack spacing={1.3}>
              {support.map((s, idx) => (
                <Box key={idx} sx={{ pl: .5 }}>
                  <Typography fontSize={15} color="#22334d">
                    <span style={{ color: "#a2abb9", fontWeight: 500 }}>{s.date}</span> — {s.type}: {s.text}
                  </Typography>
                </Box>
              ))}
            </Stack>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={4}>
          <Paper elevation={0} sx={{ bgcolor: "#F4F6FA", p: 2, borderRadius: 3 }}>
            <Typography fontWeight={700} mb={1.5} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <TagIcon sx={{ fontSize: 22, mr: .8, color: "#b982e7" }} /> Интересы
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.interests.map((i, idx) => (
                <Chip
                  key={idx}
                  label={i}
                  size="small"
                  variant="outlined"
                  sx={{
                    bgcolor: "#fff",
                    borderRadius: 2,
                    fontSize: 14,
                    mb: .5,
                    borderColor: "#eee",
                    color: "#7e7e7e"
                  }}
                />
              ))}
            </Stack>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={5}>
          <Paper elevation={0} sx={{ bgcolor: "#F4F6FA", p: 2, borderRadius: 3 }}>
            <Typography fontWeight={700} mb={2} sx={{ display: "flex", alignItems: "center", fontSize: 18 }}>
              <SmsIcon sx={{ fontSize: 20, mr: 1, color: "#07a7ed" }} /> История отправки сообщений
            </Typography>
            <Table size="small" sx={{ background: "none" }}>
              <TableHead>
                <TableRow>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Дата</TableCell>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Канал</TableCell>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Направление</TableCell>
                  <TableCell sx={{ fontWeight: 700, color: "#22334d" }}>Сообщение</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {messageHistory.map((m, idx) => (
                  <TableRow key={idx}>
                    <TableCell>{m.date}</TableCell>
                    <TableCell>
                      <Chip
                        size="small"
                        label={m.channel}
                        icon={
                          m.channel === "WhatsApp" ? <WhatsAppIcon sx={{ color: "#32CD32", fontSize: 18 }} /> :
                          m.channel === "Email" ? <EmailIcon sx={{ color: "#4361EE", fontSize: 18 }} /> :
                          <SmsIcon sx={{ color: "#07a7ed", fontSize: 18 }} />
                        }
                      />
                    </TableCell>
                    <TableCell>
                      <Chip
                        size="small"
                        color={m.direction === "Исходящее" ? "success" : "default"}
                        label={m.direction}
                      />
                    </TableCell>
                    <TableCell>{m.text}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </TabPanel>

        {/* Модальное окно редактирования профиля */}
        <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
          <DialogTitle>Редактирование профиля</DialogTitle>
          <DialogContent>
            <Stack spacing={2} mt={1}>
              <TextField
                label="ФИО"
                fullWidth
                value={form.name}
                onChange={e => handleFormChange("name", e.target.value)}
              />
              <Stack direction="row" spacing={2}>
                <TextField
                  label="Email"
                  type="email"
                  fullWidth
                  value={form.email}
                  onChange={e => handleFormChange("email", e.target.value)}
                />
                <TextField
                  label="Телефон"
                  fullWidth
                  value={form.phone}
                  onChange={e => handleFormChange("phone", e.target.value)}
                />
              </Stack>
              <Stack direction="row" spacing={2}>
                <TextField
                  label="Город"
                  fullWidth
                  value={form.city}
                  onChange={e => handleFormChange("city", e.target.value)}
                />
                <TextField
                  label="Дата рождения"
                  type="date"
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  value={form.dob}
                  onChange={e => handleFormChange("dob", e.target.value)}
                />
              </Stack>
              <Select
                label="Теги"
                multiple
                fullWidth
                value={form.tags}
                onChange={handleTagsChange}
                input={<OutlinedInput label="Теги" />}
                renderValue={(selected) => selected.join(", ")}
              >
                {TAGS.map((tag) => (
                  <MenuItem key={tag} value={tag}>
                    <Checkbox checked={form.tags.indexOf(tag) > -1} />
                    <ListItemText primary={tag} />
                  </MenuItem>
                ))}
              </Select>
            </Stack>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEditClose}>Отмена</Button>
            <Button variant="contained" onClick={handleEditSave}>Сохранить</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
}
