sourcesContent:
import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField,
  Select, MenuItem, Checkbox, FormControl, InputLabel, List, ListItem, ListItemText, Card, CardContent, CardHeader
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import AssignmentIcon from "@mui/icons-material/Assignment";
import TimelineIcon from "@mui/icons-material/Timeline";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import SportsKabaddiIcon from "@mui/icons-material/SportsKabaddi";
import TrendingUpIcon from "@mui/icons-material/TrendingUp";
import WarningIcon from "@mui/icons-material/Warning";
import AddIcon from "@mui/icons-material/Add";
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import LinkIcon from "@mui/icons-material/Link";
import PollIcon from "@mui/icons-material/Poll";
import DeleteIcon from "@mui/icons-material/Delete";
import { LineChart, Line, XAxis, YAxis, Tooltip as RTooltip, ResponsiveContainer } from "recharts";

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваша бронирование подтверждено. Ждем вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div role="tabpanel" hidden={value !== index} {...other}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

export default function CustomerProfileCard() {
  // СЕГМЕНТЫ (теги/группы)
  const [segments, setSegments] = useState([
    { name: "VIP", icon: <StarIcon />, color: "#FFC107" },
    { name: "Часто покупает", icon: <ShoppingCartIcon />, color: "#1976d2" },
    { name: "Экстрим", icon: <SportsKabaddiIcon />, color: "#00b894" }
  ]);
  const handleDeleteSegment = name => setSegments(segments.filter(s => s.name !== name));
  const handleAddSegment = () => {
    setSegments([...segments, { name: "Новый сегмент", icon: <AddIcon />, color: "#bbb" }]);
  };

  // Профиль
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    city: "Краснодар",
    country: "Россия",
    gender: "Женский",
    clientType: "Физ. лицо",
    company: "",
    inn: "",
    address: "ул. Ленина, д. 10, кв. 24",
    marketing: true,
    lang: "Русский",
    age: 36,
    tags: ["День рождения сегодня", "Ждет ответа"],
    ltv: "108 400 ₽",
