sourcesContent:
import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Select, MenuItem, OutlinedInput, Checkbox, ListItemText
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import EditIcon from "@mui/icons-material/Edit";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";
import EmailIcon from "@mui/icons-material/Email";
import TimelineIcon from "@mui/icons-material/Timeline";
import SupportAgentIcon from "@mui/icons-material/SupportAgent";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import LoyaltyIcon from "@mui/icons-material/Loyalty";
import TagIcon from "@mui/icons-material/Tag";
import SmsIcon from "@mui/icons-material/Sms";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div role="tabpanel" hidden={value !== index} id={`tabpanel-${index}`} aria-labelledby={`tab-${index}`} {...other}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

const TAGS = [
  "VIP", "Низкая активность", "Гость сайта", "Риск оттока", "Корпоративный", "Постоянный клиент"
];

export default function App() {
  const [tab, setTab] = useState(0);

  // История покупок с примерами списания бонусов
  const purchases = [
    {
      date: "30.05.2025",
      item: "Бронирование отеля",
      amount: "4 400 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 4400, writtenOff: false }
