            <Stack spacing={2} mt={1}>
              <TextField
                label="ФИО"
                fullWidth
                value={form.name}
                onChange={e => handleFormChange("name", e.target.value)}
              />
              <Stack direction="row" spacing={2}>
                <TextField
                  label="Email"
                  type="email"
                  fullWidth
                  value={form.email}
                  onChange={e => handleFormChange("email", e.target.value)}
                />
                <TextField
                  label="Телефон"
                  fullWidth
                  value={form.phone}
                  onChange={e => handleFormChange("phone", e.target.value)}
                />
              </Stack>
              <Stack direction="row" spacing={2}>
                <TextField
                  label="Город"
                  fullWidth
                  value={form.city}
                  onChange={e => handleFormChange("city", e.target.value)}
                />
                <TextField
                  label="Дата рождения"
                  type="date"
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  value={form.dob}
                  onChange={e => handleFormChange("dob", e.target.value)}
                />
              </Stack>
              <Select
                label="Теги"
                multiple
                fullWidth
                value={form.tags}
                onChange={handleTagsChange}
                input={<OutlinedInput label="Теги" />}
                renderValue={(selected) => selected.join(", ")}
              >
                {TAGS.map((tag) => (
                  <MenuItem key={tag} value={tag}>
                    <Checkbox checked={form.tags.indexOf(tag) > -1} />
                    <ListItemText primary={tag} />
                  </MenuItem>
                ))}
              </Select>
            </Stack>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEditClose}>Отмена</Button>
            <Button variant="contained" onClick={handleEditSave}>Сохранить</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
}
