import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Select, MenuItem, OutlinedInput, Checkbox, ListItemText
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import EditIcon from "@mui/icons-material/Edit";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";
import EmailIcon from "@mui/icons-material/Email";
import TimelineIcon from "@mui/icons-material/Timeline";
import SupportAgentIcon from "@mui/icons-material/SupportAgent";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import LoyaltyIcon from "@mui/icons-material/Loyalty";
import TagIcon from "@mui/icons-material/Tag";
import SmsIcon from "@mui/icons-material/Sms";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div role="tabpanel" hidden={value !== index} id={`tabpanel-${index}`} aria-labelledby={`tab-${index}`} {...other}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

const TAGS = [
  "VIP", "Низкая активность", "Гость сайта", "Риск оттока", "Корпоративный", "Постоянный клиент"
];

export default function App() {
  const [tab, setTab] = useState(0);

  // История покупок с примерами списания бонусов
  const purchases = [
    {
      date: "30.05.2025",
      item: "Бронирование отеля",
      amount: "4 400 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 4400, writtenOff: false }
      ]
    },
    {
      date: "12.04.2025",
      item: "Тренировка в фитнес-зале",
      amount: "0 ₽",
      status: "Оплачено (списание бонусов)",
      pos: "Фитнес-клуб Центр",
      receiptId: "CHK-1022",
      employee: "Максим Шестаков",
      receipt: [
        { name: "Тренировка фитнес", qty: 1, price: 700, writtenOff: true, writtenOffBonuses: 700 }
      ]
    },
    {
      date: "03.03.2025",
      item: "Ски-пасс",
      amount: "15 266,7 ₽",
      status: "Оплачено (частично бонусами)",
      pos: "Касса Горки Молл",
      receiptId: "CHK-9823",
      employee: "Иван Калашников",
      receipt: [
        { name: "Ски-пасс взрослый", qty: 2, price: 12000, writtenOff: true, writtenOffBonuses: 10000 },
        { name: "Ски-пасс детский", qty: 1, price: 5266.7, writtenOff: false }
      ]
    },
    {
      date: "03.03.2025",
      item: "Бронирование отеля",
      amount: "12 615,83 ₽",
      status: "Отменено",
      pos: "Сайт",
      receiptId: "CHK-9765",
      employee: "—",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 12615.83, writtenOff: false }
      ]
    },
    {
      date: "30.08.2024",
      item: "Сувенирный магазин",
      amount: "0 ₽",
      status: "Оплачено (списание бонусов)",
      pos: "Магазин GIFT",
      receiptId: "CHK-8431",
      employee: "Алексей Чернов",
      receipt: [
        { name: "Магнит", qty: 1, price: 20, writtenOff: true, writtenOffBonuses: 20 }
      ]
    }
  ];

  // Статистика
  const paidPurchases = purchases.filter(p => p.status.includes("Оплачено"));
  const totalPurchases = paidPurchases.length;
  const avgCheck = totalPurchases === 0 ? 0 :
    Math.round(
      paidPurchases
        .map(p => Number(p.amount.replace(/[ ₽,]/g, "").replace(",", ".")))
        .reduce((a, b) => a + b, 0) / totalPurchases
    );
  const lastPurchaseDate = paidPurchases.length
    ? paidPurchases
        .map(p => p.date)
        .sort((a, b) => new Date(b.split(".").reverse().join("-")) - new Date(a.split(".").reverse().join("-")))[0]
    : "-";

  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    age: 36,
    city: "Краснодар",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    tags: ["VIP", "Низкая активность", "Гость сайта", "Риск оттока"],
    ltv: "46 301,3 ₽",
    bonus: "1 563",
    interests: [
      "Горные туры", "Отели 4–5*", "Зимний отдых", "Фитнес и SPA",
      "Детские активности", "Экстрим", "Кулинария"
    ]
  });

  // Timeline, support, messages, бонусы
  const timeline = [
    { date: "10.06.2025", text: "Вход на сайт" },
    { date: "09.06.2025", text: "Запрос баланса" },
    { date: "08.06.2025", text: "Просмотр условий акции" }
  ];
  const support = [
    { date: "02.06.2025", type: "Чат", text: "Проблема с бронированием" },
    { date: "25.05.2025", type: "Email", text: "Запрос на возврат" },
    { date: "12.05.2025", type: "Телефон", text: "Вопрос по акциям" }
  ];
  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление", campaign: "Акция «Вернем 30%»" },
    { date: "20.05.2025", value: "-375", type: "Списание", campaign: "Оплата заказа #CHK-8431" },
    { date: "12.04.2025", value: "-700", type: "Списание", campaign: "Фитнес по бонусам" },
    { date: "10.04.2025", value: "+300", type: "Начисление", campaign: "Программа лояльности" }
  ];
  const messageHistory = [
    { date: "10.06.2025", channel: "Email", direction: "Исходящее", text: "Спасибо за ваш отзыв! Ваш бонус начислен." },
    { date: "05.06.2025", channel: "SMS", direction: "Входящее", text: "Получил купон, спасибо!" },
    { date: "30.05.2025", channel: "WhatsApp", direction: "Исходящее", text: "Ваше бронирование подтверждено." }
  ];

  const [editOpen, setEditOpen] = useState(false);
  const [form, setForm] = useState(profile);
  const [receiptModal, setReceiptModal] = useState({ open: false, purchase: null });
  const openReceipt = (purchase) => setReceiptModal({ open: true, purchase });
  const closeReceipt = () => setReceiptModal({ open: false, purchase: null });

  const handleEdit = () => { setForm(profile); setEditOpen(true); };
  const handleEditClose = () => setEditOpen(false);
  const handleEditSave = () => { setProfile({ ...form, age: form.dob ? (new Date().getFullYear() - Number(form.dob.slice(0, 4))) : profile.age }); setEditOpen(false); };
  const handleFormChange = (field, value) => { setForm((prev) => ({ ...prev, [field]: value })); };
  const handleTagsChange = (event) => {
    const { target: { value }, } = event;
    setForm((prev) => ({
      ...prev,
      tags: typeof value === 'string' ? value.split(',') : value,
    }));
  };

  return (
    <Box sx={{ bgcolor: "#F4F6FA", minHeight: "100vh", py: 5, px: 1 }}>
      <Box sx={{
        maxWidth: 1150,
        mx: "auto",
        bgcolor: "#fff",
        borderRadius: 4,
        boxShadow: "0 4px 24px rgba(20,25,55,0.06)",
        px: { xs: 2, sm: 5 },
        py: { xs: 3, sm: 5 }
      }}>
        {/* Верхняя карточка */}
        <Grid container spacing={3} alignItems="center" mb={1}>
          <Grid item>
            <Avatar
              sx={{
                width: 86, height: 86, fontSize: 40,
                bgcolor: "#EEF1F7", color: "#4361EE"
              }}
            >
              {profile.name
                .split(" ")
                .map((part) => part[0])
                .join("")
                .toUpperCase()}
            </Avatar>
          </Grid>
          <Grid item xs>
            <Typography variant="h4" fontWeight={700} color="#22334d" sx={{ letterSpacing: 1 }}>
              {profile.name}
            </Typography>
            <Typography color="text.secondary" sx={{ fontSize: 17, mt: .5, mb: 1 }}>
              <PersonIcon fontSize="small" sx={{ verticalAlign: "middle", mr: 1 }} />
              ID: {profile.id} &nbsp;|&nbsp; {profile.age} лет &nbsp;|&nbsp; {profile.city}
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.tags.map((t, idx) => (
                <Chip
                  key={idx}
                  label={t}
                  size="small"
                  color={t === "VIP" ? "success" : t === "Риск оттока" ? "error" : "default"}
                  variant={t === "VIP" ? "filled" : "outlined"}
                  sx={{
                    borderRadius: 1,
                    bgcolor: t === "VIP" ? "#e5f8ef" : undefined,
                    color: t === "VIP" ? "#219653" : undefined,
                    fontWeight: 600
                  }}
                />
              ))}
            </Stack>
            <Stack direction="row" spacing={1} mt={2} flexWrap="wrap">
              <Chip icon={<EmailIcon />} label={profile.email} sx={{ mr: 1, bgcolor: "#F4F6FA" }} />
              <Chip label={profile.phone} sx={{ mr: 1, bgcolor: "#F4F6FA" }} />
              <Chip label={profile.dob.split("-").reverse().join(".")} sx={{ mr: 1, bgcolor: "#F4F6FA" }} />
            </Stack>
          </Grid>
          <Grid item>
            <Stack direction="row" spacing={1}>
              <Tooltip title="Email">
                <IconButton sx={{ bgcolor: "#F1F3F6", "&:hover": { bgcolor: "#e8ebf0" } }}>
                  <EmailIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
              <Tooltip title="WhatsApp">
                <IconButton sx={{ bgcolor: "#F1F3F6", "&:hover": { bgcolor: "#e8ebf0" } }}>
                  <WhatsAppIcon fontSize="medium" sx={{ color: "#32CD32" }} />
                </IconButton>
              </Tooltip>
              <Tooltip title="Редактировать профиль">
                <IconButton sx={{ bgcolor: "#F1F3F6", "&:hover": { bgcolor: "#e8ebf0" } }} onClick={handleEdit}>
                  <EditIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
            </Stack>
          </Grid>
        </Grid>
        <Divider sx={{ my: 3 }} />

        {/* Статистика */}
        <Grid container spacing={3} mb={2}>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Общее количество покупок</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{totalPurchases}</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Средний чек</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{avgCheck} ₽</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Дата последней покупки</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{lastPurchaseDate}</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Текущий баланс бонусов</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{profile.bonus}</Typography>
            </Paper>
          </Grid>
        </Grid>
        <Divider sx={{ my: 3 }} />

        {/* Tabs */}
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          aria-label="customer tabs"
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2 }}
        >
          <Tab label="Бонусы" />
          <Tab label="История покупок" />
          <Tab label="Активность" />
          <Tab label="Обращения" />
          <Tab label="Интересы" />
          <Tab icon={<SmsIcon />} iconPosition="start" label="История сообщений" />
        </Tabs>

        {/* Бонусы */}
        <TabPanel value={tab} index={0}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Тип операции</TableCell>
                <TableCell>Кампания</TableCell>
                <TableCell>Сумма</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {bonusHistory.map((b, i) => (
                <TableRow key={i}>
                  <TableCell>{b.date}</TableCell>
                  <TableCell>
                    {b.type === "Начисление" ? (
                      <Chip label="Начисление" color="success" size="small" />
                    ) : (
                      <Chip label="Списание" color="warning" size="small" />
                    )}
                  </TableCell>
                  <TableCell>{b.campaign}</TableCell>
                  <TableCell>
                    <Typography sx={{
                      fontWeight: 700,
                      color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c"
                    }}>{b.value}</Typography>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* История покупок */}
        <TabPanel value={tab} index={1}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Точка продаж</TableCell>
                <TableCell>Сотрудник</TableCell>
                <TableCell>Чек</TableCell>
                <TableCell>Сумма</TableCell>
                <TableCell>Статус</TableCell>
                <TableCell></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {purchases.map((p, i) => (
                <TableRow key={i}>
                  <TableCell>{p.date}</TableCell>
                  <TableCell>{p.pos}</TableCell>
                  <TableCell>{p.employee}</TableCell>
                  <TableCell>
                    <Button
                      variant="text"
                      size="small"
                      onClick={() => openReceipt(p)}
                      endIcon={<ReceiptLongIcon />}
                    >
                      {p.receiptId}
                    </Button>
                  </TableCell>
                  <TableCell>{p.amount}</TableCell>
                  <TableCell>{p.status}</TableCell>
                  <TableCell></TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Активность */}
        <TabPanel value={tab} index={2}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Событие</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {timeline.map((t, i) => (
                <TableRow key={i}>
                  <TableCell>{t.date}</TableCell>
                  <TableCell>{t.text}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Обращения */}
        <TabPanel value={tab} index={3}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Тип</TableCell>
                <TableCell>Описание</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {support.map((s, i) => (
                <TableRow key={i}>
                  <TableCell>{s.date}</TableCell>
                  <TableCell>
                    <Chip label={s.type} size="small" color="info" />
                  </TableCell>
                  <TableCell>{s.text}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Интересы */}
        <TabPanel value={tab} index={4}>
          <Stack direction="row" spacing={1} flexWrap="wrap">
            {profile.interests.map((interest, i) => (
              <Chip key={i} label={interest} sx={{ fontSize: 16, p: 1.5, m: .5, bgcolor: "#f3f6fc" }} />
            ))}
          </Stack>
        </TabPanel>

        {/* История сообщений */}
        <TabPanel value={tab} index={5}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Канал</TableCell>
                <TableCell>Тип</TableCell>
                <TableCell>Текст</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {messageHistory.map((m, i) => (
                <TableRow key={i}>
                  <TableCell>{m.date}</TableCell>
                  <TableCell>
                    <Chip label={m.channel} size="small" color="primary" />
                  </TableCell>
                  <TableCell>
                    <Chip label={m.direction} size="small" color={m.direction === "Исходящее" ? "success" : "warning"} />
                  </TableCell>
                  <TableCell>{m.text}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* ----------- Модальное окно ЧЕК ----------- */}
        <Dialog
          open={receiptModal.open}
          onClose={closeReceipt}
          maxWidth="xs"
          fullWidth
          PaperProps={{
            sx: {
              borderRadius: 4,
              p: 0,
              bgcolor: "#fff",
              boxShadow: "0 8px 32px rgba(24,32,52,0.18)"
            }
          }}
        >
          <DialogTitle
            sx={{
              fontWeight: 700,
              fontSize: 22,
              px: 4,
              pt: 3,
              pb: 1.5,
              bgcolor: "#f6f9fe",
              color: "#22334d",
              borderTopLeftRadius: 16,
              borderTopRightRadius: 16
            }}
          >
            Чек {receiptModal.purchase?.receiptId} — {receiptModal.purchase?.date}
          </DialogTitle>
          <DialogContent sx={{ px: 4, pt: 1, pb: 2 }}>
            <Typography sx={{ mb: 1.5, fontSize: 16 }}>
              <b>Точка продаж:</b> {receiptModal.purchase?.pos}<br />
              <b>Сотрудник:</b> {receiptModal.purchase?.employee}
            </Typography>
            <Table size="small" sx={{ background: "none" }}>
              <TableHead>
                <TableRow>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Товар</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Кол-во</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Цена</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Сумма</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Списано бонусов</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {receiptModal.purchase?.receipt.map((row, i) => (
                  <TableRow key={i} hover>
                    <TableCell>
                      <Stack direction="row" alignItems="center" spacing={1}>
                        <span>{row.name}</span>
                        {row.writtenOff && (
                          <Chip
                            label="Списано бонусами"
                            size="small"
                            sx={{
                              bgcolor: "#1976d2",
                              color: "#fff",
                              fontWeight: 600,
                              fontSize: 13,
                              height: "28px",
                              borderRadius: 2,
                              px: 1.5
                            }}
                          />
                        )}
                      </Stack>
                    </TableCell>
                    <TableCell>{row.qty}</TableCell>
                    <TableCell>{row.price.toLocaleString()} ₽</TableCell>
                    <TableCell>{(row.qty * row.price).toLocaleString()} ₽</TableCell>
                    <TableCell align="center">
                      {row.writtenOffBonuses
                        ? <span style={{
                            color: "#9c27b0",
                            fontWeight: 700,
                            fontSize: 16
                          }}>{row.writtenOffBonuses} бонусов</span>
                        : <span style={{ color: "#bbb" }}>—</span>
                      }
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </DialogContent>
          <DialogActions sx={{ bgcolor: "#f6f9fe", px: 4, py: 2, borderBottomLeftRadius: 16, borderBottomRightRadius: 16 }}>
            <Button
              onClick={closeReceipt}
              variant="outlined"
              sx={{
                bgcolor: "#f7faff",
                color: "#1976d2",
                borderRadius: 2,
                px: 3,
                fontWeight: 700,
                borderColor: "#e3e8f0",
                boxShadow: "none",
                textTransform: "none",
                '&:hover': { bgcolor: "#e3f0ff", borderColor: "#b6d0ff" }
              }}
            >
              ЗАКРЫТЬ
            </Button>
          </DialogActions>
        </Dialog>

        {/* ----------- Модальное окно РЕДАКТИРОВАНИЕ ПРОФИЛЯ ----------- */}
        <Dialog
          open={editOpen}
          onClose={handleEditClose}
          maxWidth="sm"
          fullWidth
          PaperProps={{
            sx: {
              borderRadius: 4,
              bgcolor: "#fff",
              boxShadow: "0 8px 32px rgba(24,32,52,0.14)"
            }
          }}
        >
          <DialogTitle
            sx={{
              fontWeight: 700,
              fontSize: 22,
              px: 4,
              pt: 3,
              pb: 1.5,
              bgcolor: "#f6f9fe",
              color: "#22334d",
              borderTopLeftRadius: 16,
              borderTopRightRadius: 16
            }}
          >
            Редактирование профиля
          </DialogTitle>
          <DialogContent sx={{ px: 4, pt: 1, pb: 3 }}>
            <Stack spacing={2} mt={1}>
              <TextField
                label="ФИО"
                fullWidth
                value={form.name}
                onChange={e => handleFormChange("name", e.target.value)}
                variant="outlined"
                sx={{ bgcolor: "#f9fafc", borderRadius: 2 }}
              />
              <Stack direction="row" spacing={2}>
                <TextField
                  label="Email"
                  type="email"
                  fullWidth
                  value={form.email}
                  onChange={e => handleFormChange("email", e.target.value)}
                  variant="outlined"
                  sx={{ bgcolor: "#f9fafc", borderRadius: 2 }}
                />
                <TextField
                  label="Телефон"
                  fullWidth
                  value={form.phone}
                  onChange={e => handleFormChange("phone", e.target.value)}
                  variant="outlined"
                  sx={{ bgcolor: "#f9fafc", borderRadius: 2 }}
                />
              </Stack>
              <Stack direction="row" spacing={2}>
                <TextField
                  label="Город"
                  fullWidth
                  value={form.city}
                  onChange={e => handleFormChange("city", e.target.value)}
                  variant="outlined"
                  sx={{ bgcolor: "#f9fafc", borderRadius: 2 }}
                />
                <TextField
                  label="Дата рождения"
                  type="date"
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  value={form.dob}
                  onChange={e => handleFormChange("dob", e.target.value)}
                  variant="outlined"
                  sx={{ bgcolor: "#f9fafc", borderRadius: 2 }}
                />
              </Stack>
              <Select
                label="Теги"
                multiple
                fullWidth
                value={form.tags}
                onChange={handleTagsChange}
                input={<OutlinedInput label="Теги" />}
                renderValue={(selected) => selected.join(", ")}
                sx={{ bgcolor: "#f9fafc", borderRadius: 2 }}
              >
                {TAGS.map((tag) => (
                  <MenuItem key={tag} value={tag}>
                    <Checkbox checked={form.tags.indexOf(tag) > -1} />
                    <ListItemText primary={tag} />
                  </MenuItem>
                ))}
              </Select>
            </Stack>
          </DialogContent>
          <DialogActions sx={{ bgcolor: "#f6f9fe", px: 4, py: 2, borderBottomLeftRadius: 16, borderBottomRightRadius: 16 }}>
            <Button
              onClick={handleEditClose}
              sx={{
                borderRadius: 2,
                bgcolor: "#fff",
                color: "#22334d",
                fontWeight: 600,
                textTransform: "none",
                px: 3,
                '&:hover': { bgcolor: "#f4f6fa" }
              }}
            >
              Отмена
            </Button>
            <Button
              variant="contained"
              onClick={handleEditSave}
              sx={{
                borderRadius: 2,
                bgcolor: "#1976d2",
                color: "#fff",
                fontWeight: 700,
                textTransform: "none",
                px: 3,
                boxShadow: "none",
                '&:hover': { bgcolor: "#1564b3" }
              }}
            >
              Сохранить
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
}
