sourcesContent:
import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField,
  Select, MenuItem, Checkbox, FormControl, InputLabel
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import EventIcon from "@mui/icons-material/Event";
import WarningAmberIcon from "@mui/icons-material/WarningAmber";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import { deepPurple } from "@mui/material/colors";

// ----- ВСПОМОГАТЕЛЬНЫЕ -----
const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваша бронирование подтверждено. Ждем вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      {...other}
      style={{ width: "100%" }}
    >
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

// ----- ОСНОВНОЙ КОМПОНЕНТ -----
export default function CustomerProfileCard() {
  // --- PROFILE
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    city: "Краснодар",
    country: "Россия",
    gender: "Женский",
    clientType: "Физ. лицо",
    address: "ул. Ленина, д. 10, кв. 24",
    marketing: true,
    lang: "Русский",
    age: 36,
    tags: ["VIP", "День рождения сегодня", "Часто покупает", "RFM: Gold"],
    interests: ["Горные туры", "Зимний отдых", "SPA и релакс"],
    ltv: "128 400 ₽",
    bonus: "3 563",
  });

  // ------ ДАННЫЕ -------
  const purchases = [
    {
      date: "12.06.2025",
      pos: "Офис Красная Поляна",
      employee: "Екатерина Миронова",
      receiptId: "CHK-12222",
      amount: "19 990 ₽",
      status: "Оплачено",
      receipt: [
        { name: "Ски-пасс взрослый", qty: 2, price: 8000, writtenOff: true, writtenOffBonuses: 5000, accrual: 350 },
        { name: "Фирменная термокружка", qty: 1, price: 1990, writtenOff: false, accrual: 99 },
        { name: "Сервисный сбор", qty: 1, price: 2000, writtenOff: false, accrual: 0 }
      ]
    },
    {
      date: "25.05.2025",
      pos: "GIFT-бутик",
      employee: "Антон Власов",
      receiptId: "CHK-11901",
      amount: "4 400 ₽",
      status: "Оплачено (списание бонусов)",
      receipt: [
        { name: "Подарочная карта", qty: 1, price: 4000, writtenOff: true, writtenOffBonuses: 4000, accrual: 0 },
        { name: "Открытка", qty: 2, price: 200, writtenOff: false, accrual: 20 }
      ]
    }
  ];
  const promoCodes = [
    { code: "VIP2025", discount: "15%", used: false, activated: "01.06.2025" },
    { code: "SKI2024", discount: "20%", used: true, activated: "01.04.2025" },
    { code: "BIRTHDAY", discount: "30%", used: false, activated: "-" }
  ];
  const gifts = [
    { date: "2024-06-10", name: "Подарочная карта 3 000 ₽", status: "Активна" },
    { date: "2023-12-15", name: "Фирменный термос", status: "Получено" },
    { date: "2023-08-09", name: "Футболка", status: "Получено" }
  ];
  const bonusHistory = [
    { date: "12.06.2025", value: "+350", type: "Начисление", campaign: "Покупка ски-пасс" },
    { date: "25.05.2025", value: "-4000", type: "Списание", campaign: "Подарочная карта" },
    { date: "24.05.2025", value: "+1200", type: "Начисление", campaign: "RFM: Gold" },
    { date: "15.04.2025", value: "-1200", type: "Списание", campaign: "Оплата заказа" }
  ];
  const npsSurveys = [
    { date: "12.06.2025", channel: "Email", score: 9, comment: "Все отлично!", order: "CHK-12222" },
    { date: "24.05.2025", channel: "SMS", score: 5, comment: "Была задержка с отправкой заказа", order: "CHK-11901" },
    { date: "10.02.2025", channel: "Telegram", score: 10, comment: "Спасибо за сервис!", order: "CHK-00101" }
  ];
  const activities = [
    { date: "13.06.2025", type: "Открытие письма", icon: <EmailIcon color="primary" />, desc: "Открыл рассылку: Летний сезон" },
    { date: "13.06.2025", type: "Клик по кнопке", icon: <SmsIcon color="success" />, desc: "Купил ски-пасс по акции" },
    { date: "12.06.2025", type: "Посещение сайта", icon: <PersonIcon color="info" />, desc: "Зашел на страницу акции" }
  ];
  const messageHistory = [
    { date: "12.06.2025 17:01", channel: "SMS", direction: "Исходящее", type: "Ручная рассылка", campaign: "Летний сезон", text: "Спасибо за ваш отзыв!" },
    { date: "11.06.2025 12:23", channel: "Telegram", direction: "Исходящее", type: "Ручная рассылка", campaign: "Акция скидка", text: "Ваша скидка активирована" }
  ];
  const tasks = [
    { due: "15.06.2025", text: "Перезвонить клиенту", status: "Открыта" },
    { due: "30.06.2025", text: "Провести опрос NPS", status: "Выполнена" }
  ];

  // Графики (макеты)
  const purchaseChart = [
    { date: "Апр", value: 9000 }, { date: "Май", value: 24000 }, { date: "Июн", value: 19990 }
  ];
  const bonusChart = [
    { date: "Апр", value: 1200 }, { date: "Май", value: 8000 }, { date: "Июн", value: 350 }
  ];

  // ----- STATES -----
  const [tab, setTab] = useState(0);
  const [editOpen, setEditOpen] = useState(false);
  const [form, setForm] = useState(profile);
  const [receiptModal, setReceiptModal] = useState({ open: false, purchase: null });
  const [msgModal, setMsgModal] = useState(false);
  const [msgChannel, setMsgChannel] = useState("SMS");
  const [msgText, setMsgText] = useState("");
  const [msgSending, setMsgSending] = useState(false);

  // ----- ЛОГИКА -----
  const openReceipt = (purchase) => setReceiptModal({ open: true, purchase });
  const closeReceipt = () => setReceiptModal({ open: false, purchase: null });
  const handleEdit = () => { setForm(profile); setEditOpen(true); };
  const handleEditClose = () => setEditOpen(false);
  const handleEditSave = () => {
    setProfile({
      ...form,
      age: form.dob ? (new Date().getFullYear() - Number(form.dob.slice(0, 4))) : profile.age
    });
    setEditOpen(false);
  };
  const handleFormChange = (field, value) => setForm((prev) => ({ ...prev, [field]: value }));
  const openMsgModal = () => { setMsgText(""); setMsgChannel("SMS"); setMsgModal(true); };
  const closeMsgModal = () => setMsgModal(false);
  const sendMessage = () => {
    if (!msgText.trim()) return;
    setMsgSending(true);
    setTimeout(() => {
      setMsgSending(false);
      setMsgModal(false);
    }, 700);
  };

  // ===== UI =====
  return (
    <Box sx={{
      minHeight: "100vh",
      bgcolor: "#f7f8fa",
      pt: 0, pb: 5,
      px: { xs: 1, md: 4 }
    }}>
      {/* ------- HEADER ------- */}
      <Paper
        sx={{
          maxWidth: 1300,
          mx: "auto",
          mt: 4,
          mb: 2,
          borderRadius: 5,
          boxShadow: "0 8px 40px 0 #c4dafc2b",
          overflow: "hidden",
          position: "relative",
          background: "linear-gradient(120deg, #e6ebfc 60%, #f5f8fd 100%)"
        }}
      >
        <Grid container>
          <Grid item xs={12} md={3} sx={{ bgcolor: "#f4f7ff", py: 5, px: 3, minHeight: 300 }}>
            <Stack alignItems="center" spacing={2}>
              <Avatar sx={{
                width: 92, height: 92, fontSize: 42, bgcolor: deepPurple[400], color: "#fff"
              }}>
                {profile.name.split(" ").map(part => part[0]).join("").toUpperCase()}
              </Avatar>
              <Typography variant="h5" fontWeight={800} color="#22334d" sx={{ textAlign: "center", mb: 1 }}>
                {profile.name}
              </Typography>
              <Stack direction="row" spacing={1} flexWrap="wrap" justifyContent="center" mb={1}>
                {profile.tags.map((t, i) => (
                  <Chip
                    key={i}
                    label={t}
                    icon={t === "VIP" ? <StarIcon sx={{ color: "#FFD700" }} /> : t === "День рождения сегодня" ? <EventIcon sx={{ color: "#ff5151" }} /> : null}
                    sx={{
                      bgcolor: t === "VIP" ? "#fffde6" : t === "День рождения сегодня" ? "#fff3f3" : "#e4eaff",
                      color: t === "VIP" ? "#D5B100" : t === "День рождения сегодня" ? "#ff5151" : "#4c56a6",
                      fontWeight: 700,
                      border: "1px solid #dde3f7"
                    }}
                  />
                ))}
              </Stack>
              <Stack alignItems="center" spacing={0.5}>
                <Chip icon={<EmailIcon />} label={profile.email} size="small" sx={{ bgcolor: "#eef0f7" }} />
                <Chip label={profile.phone} size="small" sx={{ bgcolor: "#eef0f7" }} />
              </Stack>
              <Button
                variant="outlined"
                fullWidth
                size="small"
                sx={{ mt: 3, borderRadius: 3, fontWeight: 700 }}
                startIcon={<EditIcon />}
                onClick={handleEdit}
              >Редактировать профиль</Button>
              <Button
                variant="contained"
                color="primary"
                fullWidth
                size="small"
                sx={{ borderRadius: 3, mt: 1, fontWeight: 700 }}
                startIcon={<SendIcon />}
                onClick={openMsgModal}
              >Отправить сообщение</Button>
            </Stack>
          </Grid>
          <Grid item xs={12} md={9} sx={{ py: 5, px: { xs: 2, md: 5 } }}>
            <Grid container spacing={2}>
              <Grid item xs={6} md={3}>
                <Typography fontSize={14} color="text.secondary">Общий LTV</Typography>
                <Typography variant="h6" fontWeight={800}>{profile.ltv}</Typography>
              </Grid>
              <Grid item xs={6} md={3}>
                <Typography fontSize={14} color="text.secondary">Текущий баланс бонусов</Typography>
                <Typography variant="h6" fontWeight={800}>{profile.bonus}</Typography>
              </Grid>
              <Grid item xs={6} md={3}>
                <Typography fontSize={14} color="text.secondary">Возраст</Typography>
                <Typography variant="h6" fontWeight={800}>{profile.age} лет</Typography>
              </Grid>
              <Grid item xs={6} md={3}>
                <Typography fontSize={14} color="text.secondary">Город</Typography>
                <Typography variant="h6" fontWeight={800}>{profile.city}</Typography>
              </Grid>
            </Grid>
            <Divider sx={{ my: 3 }} />
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <Typography fontSize={15} color="text.secondary" mb={.5}>Интересы</Typography>
                <Stack direction="row" spacing={1} flexWrap="wrap">
                  {profile.interests.map((interest, i) => (
                    <Chip key={i} label={interest} sx={{ bgcolor: "#f3f6fc", m: .5 }} />
                  ))}
                </Stack>
              </Grid>
              <Grid item xs={12} md={6}>
                <Typography fontSize={15} color="text.secondary" mb={.5}>Маркетинг</Typography>
                <Chip label={profile.marketing ? "Согласие на рассылку" : "Без рассылки"} color={profile.marketing ? "success" : "default"} />
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </Paper>

      {/* ------- MAIN CONTENT ------- */}
      <Paper
        sx={{
          maxWidth: 1300,
          mx: "auto",
          borderRadius: 5,
          px: { xs: 1, md: 4 },
          py: { xs: 1, md: 3 },
          mt: 2
        }}
      >
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2, fontWeight: 800 }}
        >
          <Tab label="Покупки" />
          <Tab label="Бонусы" />
          <Tab label="Промокоды" />
          <Tab label="Подарки" />
          <Tab label="История сообщений" />
          <Tab label="NPS" />
          <Tab label="Активности" />
          <Tab label="Задачи" />
        </Tabs>

        {/* Покупки */}
        <TabPanel value={tab} index={0}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Точка продаж</TableCell>
                <TableCell>Сотрудник</TableCell>
                <TableCell>Чек</TableCell>
                <TableCell>Сумма</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {purchases.map((p, i) => (
                <TableRow key={i}>
                  <TableCell>{p.date}</TableCell>
                  <TableCell>{p.pos}</TableCell>
                  <TableCell>{p.employee}</TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={() => openReceipt(p)}
                      endIcon={<ReceiptLongIcon />}
                    >
                      {p.receiptId}
                    </Button>
                  </TableCell>
                  <TableCell>{p.amount}</TableCell>
                  <TableCell>
                    <Chip label={p.status} color={p.status.includes("списание") ? "warning" : "success"} size="small" />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Бонусы */}
        <TabPanel value={tab} index={1}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Тип операции</TableCell>
                <TableCell>Кампания</TableCell>
                <TableCell>Сумма</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {bonusHistory.map((b, i) => (
                <TableRow key={i}>
                  <TableCell>{b.date}</TableCell>
                  <TableCell>
                    {b.type === "Начисление" ? (
                      <Chip label="Начисление" color="success" size="small" />
                    ) : (
                      <Chip label="Списание" color="warning" size="small" />
                    )}
                  </TableCell>
                  <TableCell>{b.campaign}</TableCell>
                  <TableCell>
                    <Typography sx={{
                      fontWeight: 700,
                      color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c"
                    }}>{b.value}</Typography>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Промокоды */}
        <TabPanel value={tab} index={2}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Промокод</TableCell>
                <TableCell>Скидка</TableCell>
                <TableCell>Активирован</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {promoCodes.map((pr, i) => (
                <TableRow key={i}>
                  <TableCell>
                    <Chip icon={<LocalOfferIcon />} label={pr.code} color="info" />
                  </TableCell>
                  <TableCell>{pr.discount}</TableCell>
                  <TableCell>{pr.activated}</TableCell>
                  <TableCell>
                    <Chip label={pr.used ? "Использован" : "Активен"} color={pr.used ? "success" : "warning"} />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Подарки */}
        <TabPanel value={tab} index={3}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Подарок</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {gifts.map((g, i) => (
                <TableRow key={i}>
                  <TableCell>{g.date}</TableCell>
                  <TableCell>
                    <Chip icon={<CardGiftcardIcon />} label={g.name} />
                  </TableCell>
                  <TableCell>
                    <Chip label={g.status} color={g.status === "Активна" ? "success" : "default"} />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* История сообщений */}
        <TabPanel value={tab} index={4}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Канал</TableCell>
                <TableCell>Способ</TableCell>
                <TableCell>Тип отправки</TableCell>
                <TableCell>Кампания</TableCell>
                <TableCell>Текст</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {messageHistory.map((m, i) => (
                <TableRow key={i}>
                  <TableCell>{m.date}</TableCell>
                  <TableCell>
                    {m.channel === "SMS" ? <Chip icon={<SmsIcon fontSize="small" />} label="SMS" size="small" /> :
                      <Chip icon={<TelegramIcon fontSize="small" />} label="Telegram" size="small" color="primary" />}
                  </TableCell>
                  <TableCell>
                    {m.direction}
                  </TableCell>
                  <TableCell>
                    {m.type}
                  </TableCell>
                  <TableCell>
                    {m.campaign}
                  </TableCell>
                  <TableCell>{m.text}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* NPS */}
        <TabPanel value={tab} index={5}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Канал</TableCell>
                <TableCell>Оценка (0-10)</TableCell>
                <TableCell>Комментарий</TableCell>
                <TableCell>Заказ</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {npsSurveys.map((n, i) => (
                <TableRow key={i}>
                  <TableCell>{n.date}</TableCell>
                  <TableCell>
                    <Chip label={n.channel} size="small" color="primary" />
                  </TableCell>
                  <TableCell>
                    <Stack direction="row" alignItems="center" spacing={0.5}>
                      <StarIcon fontSize="small" sx={{ color: n.score > 8 ? "#219653" : n.score > 6 ? "#FFB300" : "#C62828" }} />
                      <Typography fontWeight={700}>{n.score}</Typography>
                    </Stack>
                  </TableCell>
                  <TableCell>{n.comment}</TableCell>
                  <TableCell>
                    <Button
                      size="small"
                      variant="text"
                      onClick={() => openReceipt(purchases.find(x => x.receiptId === n.order))}
                      endIcon={<ReceiptLongIcon />}
                    >{n.order}</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Активности */}
        <TabPanel value={tab} index={6}>
          <Stack spacing={2}>
            {activities.map((a, i) => (
              <Paper key={i} sx={{ p: 2, display: "flex", alignItems: "center", borderRadius: 3 }}>
                <Box sx={{ mr: 2 }}>{a.icon}</Box>
                <Box>
                  <Typography fontWeight={700}>{a.type}</Typography>
                  <Typography color="text.secondary" fontSize={15}>{a.desc}</Typography>
                </Box>
                <Box sx={{ ml: "auto" }}><Typography color="text.secondary" fontSize={15}>{a.date}</Typography></Box>
              </Paper>
            ))}
          </Stack>
        </TabPanel>

        {/* Задачи */}
        <TabPanel value={tab} index={7}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Задача</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {tasks.map((t, i) => (
                <TableRow key={i}>
                  <TableCell>{t.due}</TableCell>
                  <TableCell>{t.text}</TableCell>
                  <TableCell>
                    <Chip label={t.status} color={t.status === "Открыта" ? "warning" : "success"} />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>
      </Paper>

      {/* ----------- Модальное окно ЧЕК ----------- */}
      <Dialog
        open={receiptModal.open}
        onClose={closeReceipt}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle sx={{ fontWeight: 800, fontSize: 21, letterSpacing: .5, pb: 0 }}>
          Чек {receiptModal.purchase?.receiptId} — {receiptModal.purchase?.date}
        </DialogTitle>
        <DialogContent>
          <Typography sx={{ mb: 1.5, fontSize: 16 }}>
            <b>Точка продаж:</b> {receiptModal.purchase?.pos}<br />
            <b>Сотрудник:</b> {receiptModal.purchase?.employee}
          </Typography>
          <Table size="medium">
            <TableHead>
              <TableRow>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Товар</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Кол-во</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Цена</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Сумма</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Списано бонусов</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Начислено бонусов</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {receiptModal.purchase?.receipt.map((row, i) => (
                <TableRow key={i} hover>
                  <TableCell>
                    <Stack direction="row" alignItems="center" spacing={1}>
                      <span>{row.name}</span>
                      {row.writtenOff && (
                        <Chip
                          label="Списано бонусами"
                          size="small"
                          sx={{
                            bgcolor: "#1976d2",
                            color: "#fff",
                            fontWeight: 600,
                            fontSize: 13,
                            height: "28px",
                            borderRadius: 2,
                            px: 1.5
                          }}
                        />
                      )}
                    </Stack>
                  </TableCell>
                  <TableCell>{row.qty}</TableCell>
                  <TableCell>{row.price.toLocaleString()} ₽</TableCell>
                  <TableCell>{(row.qty * row.price).toLocaleString()} ₽</TableCell>
                  <TableCell align="center">
                    {row.writtenOffBonuses
                      ? <span style={{
                        color: "#9c27b0",
                        fontWeight: 700,
                        fontSize: 16
                      }}>{row.writtenOffBonuses} бонусов</span>
                      : <span style={{ color: "#bbb" }}>—</span>
                    }
                  </TableCell>
                  <TableCell align="center">
                    {row.accrual
                      ? <span style={{
                        color: "#2e7d32",
                        fontWeight: 700,
                        fontSize: 16
                      }}>{row.accrual} бонусов</span>
                      : <span style={{ color: "#bbb" }}>—</span>
                    }
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeReceipt} variant="outlined">ЗАКРЫТЬ</Button>
        </DialogActions>
      </Dialog>

      {/* --------- Модальное окно ОТПРАВКИ СООБЩЕНИЯ --------- */}
      <Dialog
        open={msgModal}
        onClose={closeMsgModal}
        maxWidth="xs"
        fullWidth
        PaperProps={{
          sx: {
            borderRadius: 4, bgcolor: "#fff",
            boxShadow: "0 8px 32px rgba(24,32,52,0.18)"
          }
        }}
      >
        <DialogTitle>
          Отправить сообщение клиенту
        </DialogTitle>
        <DialogContent>
          <FormControl sx={{ mb: 2, minWidth: 140 }}>
            <InputLabel id="channel-label">Канал</InputLabel>
            <Select
              labelId="channel-label"
              value={msgChannel}
              label="Канал"
              onChange={e => setMsgChannel(e.target.value)}
              startAdornment={
                msgChannel === "SMS"
                  ? <SmsIcon sx={{ mr: 1, color: "#3f51b5" }} />
                  : <TelegramIcon sx={{ mr: 1, color: "#0088cc" }} />
              }
            >
              <MenuItem value="SMS">
                <Stack direction="row" alignItems="center" spacing={1}><SmsIcon />SMS</Stack>
              </MenuItem>
              <MenuItem value="Telegram">
                <Stack direction="row" alignItems="center" spacing={1}><TelegramIcon />Telegram</Stack>
              </MenuItem>
            </Select>
          </FormControl>
          <Stack spacing={1}>
            <Stack direction="row" spacing={1}>
              {MESSAGE_TEMPLATES.map((tpl, idx) => (
                <Button
                  key={idx}
                  variant="outlined"
                  size="small"
                  onClick={() => setMsgText(tpl)}
                  sx={{
                    borderRadius: 2,
                    bgcolor: "#F7F9FC",
                    fontSize: 13,
                    px: 1.5,
                    textTransform: "none",
                  }}
                  startIcon={<ContentCopyIcon fontSize="small" />}
                >{tpl.length > 30 ? tpl.slice(0, 27) + "…" : tpl}</Button>
              ))}
            </Stack>
            <TextField
              fullWidth
              multiline
              minRows={3}
              maxRows={8}
              placeholder="Введите сообщение…"
              value={msgText}
              onChange={e => setMsgText(e.target.value)}
              sx={{
                ".MuiInputBase-root": { bgcolor: "#F6F9FC", borderRadius: 2 }
              }}
            />
          </Stack>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeMsgModal}>Отмена</Button>
          <Button
            variant="contained"
            onClick={sendMessage}
            disabled={!msgText.trim() || msgSending}
            endIcon={<SendIcon />}
            sx={{ textTransform: "none" }}
          >
            {msgSending ? "Отправка..." : "Отправить"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* ----------- Модальное окно РЕДАКТИРОВАНИЕ ПРОФИЛЯ ----------- */}
      <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
        <DialogTitle>Редактирование профиля</DialogTitle>
        <DialogContent>
          <Stack spacing={2} mt={1}>
            <TextField label="ФИО" fullWidth value={form.name} onChange={e => handleFormChange("name", e.target.value)} />
            <TextField label="Email" type="email" fullWidth value={form.email} onChange={e => handleFormChange("email", e.target.value)} />
            <TextField label="Телефон" fullWidth value={form.phone} onChange={e => handleFormChange("phone", e.target.value)} />
            <TextField label="Дата рождения" type="date" fullWidth InputLabelProps={{ shrink: true }} value={form.dob} onChange={e => handleFormChange("dob", e.target.value)} />
            <Stack direction="row" spacing={2}>
              <TextField label="Город" fullWidth value={form.city} onChange={e => handleFormChange("city", e.target.value)} />
              <TextField label="Страна" fullWidth value={form.country} onChange={e => handleFormChange("country", e.target.value)} />
            </Stack>
            <Select label="Пол" fullWidth value={form.gender} onChange={e => handleFormChange("gender", e.target.value)}>
              <MenuItem value="Женский">Женский</MenuItem>
              <MenuItem value="Мужской">Мужской</MenuItem>
              <MenuItem value="Другое">Другое</MenuItem>
            </Select>
            <Select label="Тип клиента" fullWidth value={form.clientType} onChange={e => handleFormChange("clientType", e.target.value)}>
              <MenuItem value="Физ. лицо">Физ. лицо</MenuItem>
              <MenuItem value="Юр. лицо">Юр. лицо</MenuItem>
            </Select>
            <TextField label="Адрес" fullWidth value={form.address} onChange={e => handleFormChange("address", e.target.value)} />
            <Select label="Язык интерфейса" fullWidth value={form.lang} onChange={e => handleFormChange("lang", e.target.value)}>
              <MenuItem value="Русский">Русский</MenuItem>
              <MenuItem value="English">English</MenuItem>
            </Select>
            <Stack direction="row" alignItems="center" spacing={1}>
              <Checkbox checked={form.marketing} onChange={e => handleFormChange("marketing", e.target.checked)} />
              <Typography>Согласие на рассылку</Typography>
            </Stack>
          </Stack>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose}>Отмена</Button>
          <Button variant="contained" onClick={handleEditSave}>Сохранить</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
