sourcesContent:
import React, { useState } from "react";
import {
  Box, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Grid, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Paper, Tabs, Tab, Button, Dialog, DialogTitle, DialogContent, DialogActions
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import GroupAddIcon from "@mui/icons-material/GroupAdd";
import CreditCardIcon from "@mui/icons-material/CreditCard";
import PersonIcon from "@mui/icons-material/Person";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import TelegramIcon from "@mui/icons-material/Telegram";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import CakeIcon from "@mui/icons-material/Cake";
import PublicIcon from "@mui/icons-material/Public";
import HomeIcon from "@mui/icons-material/Home";

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваша бронирование подтверждено. Ждем вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

function TabPanel({ children, value, index, ...other }) {
  return (
    <div role="tabpanel" hidden={value !== index} {...other}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

export default function CustomerProfileCard() {
  const [tab, setTab] = useState(0);
  const [receiptModal, setReceiptModal] = useState({ open: false, purchase: null });

  // Профиль и демо-данные
  const profile = {
    name: "Мария Летунова",
    id: "874392",
    age: 36,
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    gender: "Женский",
    city: "Краснодар",
    country: "Россия",
    address: "ул. Ленина, д. 10, кв. 24",
    clientType: "Физ. лицо",
    lang: "Русский",
    marketing: true,
    tags: ["Постоянный клиент", "Гость сайта", "Отзывчивый"],
    vip: true,
    gold: true,
    referrals: 8,
    cardStatus: "Активна",
    cardNumber: "7712 3456 7890 0012",
    bonus: "1 563",
    ltv: "108 400 ₽"
  };

  // Промокоды
  const promoCodes = [
    { code: "VIP2025", discount: "15%", used: false },
    { code: "SKI2024", discount: "20%", used: true },
    { code: "BIRTHDAY", discount: "10%", used: false }
  ];

  // Подарки
  const gifts = [
    { date: "2024-01-15", name: "Подарочная карта 1 000 ₽", status: "Активна" },
    { date: "2023-11-22", name: "Фирменный термос", status: "Получено" },
    { date: "2023-05-12", name: "Скидка на SPA", status: "Использовано" }
  ];

  // Интересы
  const interests = [
    "Горные туры", "Зимний отдых", "SPA и релакс", "Детские активности", "Экстрим", "Кулинария", "Корпоративные мероприятия"
  ];

  // NPS
  const npsSurveys = [
    {
      date: "01.06.2025",
      channel: "Email",
      score: 10,
      comment: "Всё понравилось! Спасибо сотруднику Екатерине.",
      order: "CHK-1001"
    },
    {
      date: "15.04.2025",
      channel: "SMS",
      score: 6,
      comment: "Была небольшая задержка на ресепшене.",
      order: "CHK-1022"
    }
  ];

  // Покупки (много тестовых!)
  const purchases = [
    {
      date: "30.05.2025",
      item: "Бронирование отеля",
      amount: "4 400 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 4400, writtenOff: false }
      ]
    },
    {
      date: "12.04.2025",
      item: "Тренировка в фитнес-зале",
      amount: "0 ₽",
      status: "Оплачено (списание бонусов)",
      pos: "Фитнес-клуб Центр",
      receiptId: "CHK-1022",
      employee: "Максим Шестаков",
      receipt: [
        { name: "Тренировка фитнес", qty: 1, price: 700, writtenOff: true, writtenOffBonuses: 700 }
      ]
    },
    {
      date: "03.03.2025",
      item: "Ски-пасс",
      amount: "15 266,7 ₽",
      status: "Оплачено (частично бонусами)",
      pos: "Касса Горки Молл",
      receiptId: "CHK-9823",
      employee: "Иван Калашников",
      receipt: [
        { name: "Ски-пасс взрослый", qty: 2, price: 12000, writtenOff: true, writtenOffBonuses: 10000 },
        { name: "Ски-пасс детский", qty: 1, price: 5266.7, writtenOff: false }
      ]
    },
    {
      date: "20.02.2025",
      item: "Оплата ресторана",
      amount: "2 900 ₽",
      status: "Оплачено",
      pos: "Ресторан Кедр",
      receiptId: "CHK-9112",
      employee: "Ольга Сорокина",
      receipt: [
        { name: "Ужин", qty: 1, price: 2900, writtenOff: false }
      ]
    },
    {
      date: "08.02.2025",
      item: "Аренда инвентаря",
      amount: "1 700 ₽",
      status: "Оплачено",
      pos: "Пункт проката",
      receiptId: "CHK-8902",
      employee: "Антон Дубов",
      receipt: [
        { name: "Горные лыжи", qty: 1, price: 1200, writtenOff: false },
        { name: "Шлем", qty: 1, price: 500, writtenOff: false }
      ]
    }
  ];

  // Бонусы
  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление", campaign: "Акция «Вернем 30%»" },
    { date: "20.05.2025", value: "-375", type: "Списание", campaign: "Оплата заказа #CHK-8431" },
    { date: "13.04.2025", value: "+300", type: "Начисление", campaign: "Программа лояльности" },
    { date: "12.04.2025", value: "-700", type: "Списание", campaign: "Фитнес по бонусам" }
  ];

  // Статистика
  const paidPurchases = purchases.filter(p => p.status.includes("Оплачено"));
  const totalPurchases = paidPurchases.length;
  const avgCheck = totalPurchases === 0 ? 0 :
    Math.round(
      paidPurchases
        .map(p => Number(p.amount.replace(/[ ₽,]/g, "").replace(",", ".")))
        .reduce((a, b) => a + b, 0) / totalPurchases
    );
  const lastPurchaseDate = paidPurchases.length
    ? paidPurchases
        .map(p => p.date)
        .sort((a, b) => new Date(b.split(".").reverse().join("-")) - new Date(a.split(".").reverse().join("-")))[0]
    : "-";

  // Модалки
  const openReceipt = (purchase) => setReceiptModal({ open: true, purchase });
  const closeReceipt = () => setReceiptModal({ open: false, purchase: null });

  return (
    <Box sx={{ bgcolor: "#F4F6FA", minHeight: "100vh", py: 5, px: 1 }}>
      {/* ---------- HEADER ---------- */}
      <Box sx={{
        maxWidth: 1250, mx: "auto", bgcolor: "#fff", borderRadius: 4,
        boxShadow: "0 4px 24px rgba(20,25,55,0.06)", px: { xs: 2, sm: 5 }, py: { xs: 3, sm: 4 }
      }}>
        <Grid container spacing={3} alignItems="center" mb={0}>
          <Grid item>
            <Avatar sx={{
              width: 100, height: 100, fontSize: 48, bgcolor: "#EEF1F7",
              color: "#4361EE", border: "4px solid #F2F4F8"
            }}>
              {profile.name.split(" ").map(part => part[0]).join("").toUpperCase()}
            </Avatar>
          </Grid>
          <Grid item xs>
            <Typography variant="h3" fontWeight={700} color="#22334d" sx={{ letterSpacing: 1, mb: 1 }}>
              {profile.name}
            </Typography>
            <Stack direction="row" alignItems="center" spacing={1} mb={1}>
              <PersonIcon fontSize="small" sx={{ color: "#A6A6A6" }} />
              <Typography sx={{ fontSize: 19, color: "#5D6C86" }}>
                ID: {profile.id} &nbsp;|&nbsp; {profile.age} лет &nbsp;|&nbsp; {profile.city}, {profile.country}
              </Typography>
            </Stack>
            {/* Метки */}
            <Stack direction="row" spacing={1} flexWrap="wrap" mb={2}>
              {profile.vip && (
                <Chip
                  icon={<StarIcon sx={{ color: "#FFD700" }} />}
                  label="VIP"
                  sx={{
                    bgcolor: "#FFF9DE", color: "#BB8506", fontWeight: 700,
                    border: "2px solid #FFD700", fontSize: 18, px: 2, py: 1
                  }}
                />
              )}
              {profile.gold && (
                <Chip
                  icon={<StarIcon sx={{ color: "#FFD85F" }} />}
                  label="Gold"
                  sx={{
                    bgcolor: "#FFFBEA", color: "#AA9836", fontWeight: 700,
                    border: "2px solid #FFD85F", fontSize: 18, px: 2, py: 1
                  }}
                />
              )}
              <Chip
                icon={<GroupAddIcon sx={{ color: "#FB8C00" }} />}
                label={`Рефералы: ${profile.referrals}`}
                sx={{
                  bgcolor: "#FFF3E0", color: "#FB8C00", fontWeight: 700,
                  border: "2px solid #FB8C00", fontSize: 18, px: 2, py: 1
                }}
              />
              {profile.tags.map((tag, i) => (
                <Chip
                  key={tag}
                  label={tag}
                  sx={{
                    bgcolor: "#E3F2FD", color: "#1976d2", fontWeight: 500, fontSize: 17, px: 2, py: 1, mr: 1
                  }}
                />
              ))}
              <Chip
                icon={<CreditCardIcon sx={{ color: "#43a047" }} />}
                label={`Карта: ${profile.cardStatus}`}
                sx={{
                  bgcolor: "#E6F5EA", color: "#43a047", fontWeight: 700,
                  border: "2px solid #B0E3C0", fontSize: 17, px: 2, py: 1
                }}
              />
            </Stack>
            {/* Профиль: таблица */}
            <Table sx={{
              mt: 1,
              width: "auto",
              "& td, & th": { border: "none", fontSize: 17, py: 0.7, px: 1.5 }
            }}>
              <TableBody>
                <TableRow>
                  <TableCell><EmailIcon sx={{ mr: 1, verticalAlign: "middle", color: "#1976d2" }} />Email:</TableCell>
                  <TableCell>{profile.email}</TableCell>
                  <TableCell><SmsIcon sx={{ mr: 1, verticalAlign: "middle", color: "#1976d2" }} />Телефон:</TableCell>
                  <TableCell>{profile.phone}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell><CakeIcon sx={{ mr: 1, verticalAlign: "middle", color: "#E57373" }} />Дата рождения:</TableCell>
                  <TableCell>{profile.dob.split("-").reverse().join(".")}</TableCell>
                  <TableCell>Пол:</TableCell>
                  <TableCell>{profile.gender}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell>Тип клиента:</TableCell>
                  <TableCell>{profile.clientType}</TableCell>
                  <TableCell><PublicIcon sx={{ mr: 1, verticalAlign: "middle", color: "#1976d2" }} />Город:</TableCell>
                  <TableCell>{profile.city}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell><HomeIcon sx={{ mr: 1, verticalAlign: "middle", color: "#90caf9" }} />Адрес:</TableCell>
                  <TableCell>{profile.address}</TableCell>
                  <TableCell>Язык интерфейса:</TableCell>
                  <TableCell>{profile.lang}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell>Согласие на рассылку:</TableCell>
                  <TableCell>{profile.marketing ? "Да" : "Нет"}</TableCell>
                  <TableCell>Баланс бонусов:</TableCell>
                  <TableCell>{profile.bonus}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell>Карта:</TableCell>
                  <TableCell>{profile.cardNumber}</TableCell>
                  <TableCell>LTV:</TableCell>
                  <TableCell>{profile.ltv}</TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </Grid>
          {/* Кнопки */}
          <Grid item sx={{ minWidth: 100, textAlign: "center" }}>
            <Tooltip title="Отправить сообщение">
              <IconButton sx={{ bgcolor: "#EEF0F7", mb: 2 }}>
                <SendIcon sx={{ color: "#3949ab" }} />
              </IconButton>
            </Tooltip>
            <Tooltip title="Редактировать профиль">
              <IconButton sx={{ bgcolor: "#EEF0F7" }}>
                <EditIcon sx={{ color: "#1A2340" }} />
              </IconButton>
            </Tooltip>
          </Grid>
        </Grid>
      </Box>

      {/* ---------- ВКЛАДКИ ---------- */}
      <Box sx={{
        maxWidth: 1250, mx: "auto", mt: 4, bgcolor: "#fff", borderRadius: 4,
        boxShadow: "0 4px 24px rgba(20,25,55,0.05)", px: { xs: 1, sm: 4 }, py: 2
      }}>
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2, fontSize: 20 }}
        >
          <Tab label="История покупок" />
          <Tab label="Бонусы" />
          <Tab label="Промокоды" />
          <Tab label="Подарки" />
          <Tab label="Интересы" />
          <Tab label="NPS" />
        </Tabs>

        {/* Покупки */}
        <TabPanel value={tab} index={0}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Товар</TableCell>
                <TableCell>Точка продаж</TableCell>
                <TableCell>Сотрудник</TableCell>
                <TableCell>Чек</TableCell>
                <TableCell>Сумма</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {purchases.map((p, i) => (
                <TableRow key={i}>
                  <TableCell>{p.date}</TableCell>
                  <TableCell>{p.item}</TableCell>
                  <TableCell>{p.pos}</TableCell>
                  <TableCell>{p.employee}</TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={() => openReceipt(p)}
                      endIcon={<ReceiptLongIcon />}
                    >
                      {p.receiptId}
                    </Button>
                  </TableCell>
                  <TableCell>{p.amount}</TableCell>
                  <TableCell>
                    <Chip label={p.status} color={p.status.includes("Оплачено") ? "success" : "default"} size="small" />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Бонусы */}
        <TabPanel value={tab} index={1}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Тип операции</TableCell>
                <TableCell>Кампания</TableCell>
                <TableCell>Сумма</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {bonusHistory.map((b, i) => (
                <TableRow key={i}>
                  <TableCell>{b.date}</TableCell>
                  <TableCell>
                    <Chip label={b.type} color={b.type === "Начисление" ? "success" : "warning"} size="small" />
                  </TableCell>
                  <TableCell>{b.campaign}</TableCell>
                  <TableCell>
                    <Typography sx={{
                      fontWeight: 700,
                      color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c"
                    }}>{b.value}</Typography>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Промокоды */}
        <TabPanel value={tab} index={2}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Промокод</TableCell>
                <TableCell>Скидка</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {promoCodes.map((c, i) => (
                <TableRow key={i}>
                  <TableCell>
                    <Chip
                      icon={<LocalOfferIcon sx={{ color: "#512da8" }} />}
                      label={c.code}
                      sx={{
                        bgcolor: c.used ? "#eee" : "#ede7f6",
                        color: "#5e35b1", fontWeight: 700,
                        px: 1.5, fontSize: 17
                      }}
                    />
                  </TableCell>
                  <TableCell>{c.discount}</TableCell>
                  <TableCell>
                    <Chip
                      label={c.used ? "Использован" : "Активен"}
                      color={c.used ? "default" : "success"}
                      size="small"
                    />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Подарки */}
        <TabPanel value={tab} index={3}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Подарок</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {gifts.map((g, i) => (
                <TableRow key={i}>
                  <TableCell>{g.date}</TableCell>
                  <TableCell>
                    <Chip
                      icon={<CardGiftcardIcon sx={{ color: "#ff4081" }} />}
                      label={g.name}
                      sx={{ bgcolor: "#fce4ec", color: "#d81b60", fontWeight: 700, px: 1.5, fontSize: 17 }}
                    />
                  </TableCell>
                  <TableCell>
                    <Chip label={g.status} color={g.status === "Активна" ? "success" : "default"} size="small" />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Интересы */}
        <TabPanel value={tab} index={4}>
          <Stack direction="row" spacing={1} flexWrap="wrap">
            {interests.map((interest, i) => (
              <Chip key={i} label={interest} sx={{ fontSize: 17, p: 2, m: .5, bgcolor: "#f3f6fc", color: "#355b89", fontWeight: 600 }} />
            ))}
          </Stack>
        </TabPanel>

        {/* NPS */}
        <TabPanel value={tab} index={5}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Канал</TableCell>
                <TableCell>Оценка (0-10)</TableCell>
                <TableCell>Комментарий</TableCell>
                <TableCell>Заказ</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {npsSurveys.map((n, i) => (
                <TableRow key={i}>
                  <TableCell>{n.date}</TableCell>
                  <TableCell>
                    <Chip label={n.channel} size="small" color="primary" />
                  </TableCell>
                  <TableCell>
                    <Stack direction="row" alignItems="center" spacing={0.5}>
                      <StarIcon fontSize="small" sx={{ color: n.score > 8 ? "#219653" : n.score > 6 ? "#FFB300" : "#C62828" }} />
                      <Typography fontWeight={700}>{n.score}</Typography>
                    </Stack>
                  </TableCell>
                  <TableCell>{n.comment}</TableCell>
                  <TableCell>
                    <Button
                      size="small"
                      variant="outlined"
                      onClick={() => openReceipt(purchases.find(x=>x.receiptId===n.order))}
                      endIcon={<ReceiptLongIcon />}
                    >{n.order}</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>
      </Box>

      {/* ---------- Модальное окно ЧЕК ---------- */}
      <Dialog
        open={receiptModal.open}
        onClose={closeReceipt}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          Чек {receiptModal.purchase?.receiptId} — {receiptModal.purchase?.date}
        </DialogTitle>
        <DialogContent>
          <Typography sx={{ mb: 1.5, fontSize: 16 }}>
            <b>Точка продаж:</b> {receiptModal.purchase?.pos}<br />
            <b>Сотрудник:</b> {receiptModal.purchase?.employee}
          </Typography>
          <Table size="medium">
            <TableHead>
              <TableRow>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Товар</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Кол-во</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Цена</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Сумма</TableCell>
                <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Списано бонусов</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {receiptModal.purchase?.receipt.map((row, i) => (
                <TableRow key={i} hover>
                  <TableCell>
                    <Stack direction="row" alignItems="center" spacing={1}>
                      <span>{row.name}</span>
                      {row.writtenOff && (
                        <Chip
                          label="Списано бонусами"
                          size="small"
                          sx={{
                            bgcolor: "#1976d2",
                            color: "#fff",
                            fontWeight: 600,
                            fontSize: 13,
                            height: "28px",
                            borderRadius: 2,
                            px: 1.5
                          }}
                        />
                      )}
                    </Stack>
                  </TableCell>
                  <TableCell>{row.qty}</TableCell>
                  <TableCell>{row.price.toLocaleString()} ₽</TableCell>
                  <TableCell>{(row.qty * row.price).toLocaleString()} ₽</TableCell>
                  <TableCell align="center">
                    {row.writtenOffBonuses
                      ? <span style={{
                          color: "#9c27b0",
                          fontWeight: 700,
                          fontSize: 16
                        }}>{row.writtenOffBonuses} бонусов</span>
                      : <span style={{ color: "#bbb" }}>—</span>
                    }
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeReceipt} variant="outlined">ЗАКРЫТЬ</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
