sourcesContent:
import React, { useState } from "react";
import {
  Box, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider, Paper,
  AppBar, Toolbar, Tabs, Tab, Drawer, List, ListItem, ListItemIcon, ListItemText, Grid,
  Table, TableHead, TableBody, TableRow, TableCell, Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Select, MenuItem
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import HomeIcon from "@mui/icons-material/Home";
import PeopleIcon from "@mui/icons-material/People";
import StarIcon from "@mui/icons-material/Star";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import SettingsIcon from "@mui/icons-material/Settings";
import SmsIcon from "@mui/icons-material/Sms";
import EditIcon from "@mui/icons-material/Edit";
import SendIcon from "@mui/icons-material/Send";
import CreditCardIcon from "@mui/icons-material/CreditCard";
import CakeIcon from "@mui/icons-material/Cake";
import EmailIcon from "@mui/icons-material/Email";
import PhoneIcon from "@mui/icons-material/Phone";
import PersonIcon from "@mui/icons-material/Person";
import TranslateIcon from "@mui/icons-material/Translate";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";
import PeopleAltIcon from "@mui/icons-material/PeopleAlt";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import LogoutIcon from "@mui/icons-material/Logout";
import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";

const sideMenuItems = [
  { text: "Клиенты", icon: <PeopleIcon />, route: "clients" },
  { text: "Покупки", icon: <LocalOfferIcon />, route: "purchases" },
  { text: "Бонусы", icon: <StarIcon />, route: "bonuses" },
  { text: "Подарки", icon: <CardGiftcardIcon />, route: "gifts" },
  { text: "Настройки", icon: <SettingsIcon />, route: "settings" },
];

const tabItems = [
  "Детали",
  "Покупки",
  "Бонусы",
  "История сообщений",
  "NPS"
];

const tagConfig = {
  "VIP": { icon: <StarIcon />, color: "#FFD700", border: "#FFD700", text: "#222" },
  "Gold": { icon: <StarIcon />, color: "#F6F6BA", border: "#FFD700", text: "#856b00" },
  "Рефералы: 8": { icon: <PeopleAltIcon />, color: "#fff8ee", border: "#FFA726", text: "#FF9800" },
  "Постоянный клиент": { icon: <ThumbUpIcon />, color: "#E3F0FF", border: "#90caf9", text: "#2574b6" },
  "Гость сайта": { icon: <PersonIcon />, color: "#E3F0FF", border: "#90caf9", text: "#2574b6" },
  "Отзывчивый": { icon: <ThumbUpIcon />, color: "#E3F0FF", border: "#90caf9", text: "#2574b6" },
};

const initialProfile = {
  name: "Мария Летунова",
  id: "874392",
  age: 36,
  city: "Краснодар",
  country: "Россия",
  email: "maria.letunova@yandex.ru",
  phone: "+7 988 509-96-62",
  dob: "1989-01-16",
  gender: "Женский",
  clientType: "Физ. лицо",
  address: "ул. Ленина, д. 10, кв. 24",
  lang: "Русский",
  marketing: true,
  cardNumber: "7712 3456 7890 0012",
  cardStatus: "Активна",
  bonus: "1 563",
  ltv: "108 400 ₽",
  tags: ["VIP", "Gold", "Рефералы: 8", "Постоянный клиент", "Гость сайта", "Отзывчивый"],
};

const purchases = [
  { date: "13.06.2025", item: "Бронирование отеля", amount: "8 200 ₽", status: "Оплачено", pos: "Офис Красная Поляна" },
  { date: "05.05.2025", item: "SPA-процедура", amount: "2 800 ₽", status: "Оплачено", pos: "SPA Wellness" },
  { date: "01.05.2025", item: "Фитнес-зал", amount: "0 ₽", status: "Списано бонусами", pos: "Фитнес-клуб Центр" },
  { date: "18.04.2025", item: "Ски-пасс", amount: "12 300 ₽", status: "Оплачено", pos: "Касса Горки Молл" },
  { date: "10.04.2025", item: "Бронирование конференц-зала", amount: "6 400 ₽", status: "Оплачено", pos: "Конференц-центр" },
];

const bonusHistory = [
  { date: "12.06.2025", value: "+350", type: "Начисление", campaign: "День рождения" },
  { date: "05.06.2025", value: "-800", type: "Списание", campaign: "SPA Wellness" },
  { date: "02.06.2025", value: "+1100", type: "Начисление", campaign: "Программа лояльности" },
  { date: "25.05.2025", value: "-1 200", type: "Списание", campaign: "Оплата заказа" },
  { date: "15.05.2025", value: "+800", type: "Начисление", campaign: "Бронирование отеля" },
];

const messagesInit = [
  { date: "13.06.2025 10:12", channel: "SMS", text: "Ваш заказ подтверждён, ждём на ресепшене.", direction: "Исходящее" },
  { date: "12.06.2025 17:45", channel: "Telegram", text: "Напоминаем про SPA-процедуру завтра.", direction: "Исходящее" },
  { date: "10.06.2025 09:30", channel: "SMS", text: "Спасибо за визит!", direction: "Исходящее" },
];

const npsSurveys = [
  { date: "13.06.2025", channel: "Email", score: 9, comment: "Очень понравилось обслуживание." },
  { date: "01.05.2025", channel: "SMS", score: 8, comment: "Все хорошо, но долго ждали заселение." },
  { date: "18.04.2025", channel: "Telegram", score: 10, comment: "Лучшая команда на курорте!" },
];

export default function Bitrix24ProfileFull() {
  const [sideOpen, setSideOpen] = useState(true);
  const [tab, setTab] = useState(0);
  const [profile, setProfile] = useState(initialProfile);

  // Модальные окна
  const [editOpen, setEditOpen] = useState(false);
  const [msgOpen, setMsgOpen] = useState(false);
  const [editForm, setEditForm] = useState(profile);
  const [messages, setMessages] = useState(messagesInit);
  const [msgText, setMsgText] = useState("");
  const [msgChannel, setMsgChannel] = useState("SMS");

  // Форма редактирования
  const handleEditSave = () => {
    setProfile({ ...editForm });
    setEditOpen(false);
  };

  // Форма сообщения
  const handleSendMsg = () => {
    if (msgText.trim()) {
      setMessages([{ date: new Date().toLocaleString("ru-RU"), channel: msgChannel, text: msgText, direction: "Исходящее" }, ...messages]);
      setMsgOpen(false);
      setMsgText("");
    }
  };

  return (
    <Box sx={{ bgcolor: "#F4F6FA", minHeight: "100vh" }}>
      {/* Header */}
      <AppBar position="static" elevation={0} sx={{ bgcolor: "#fff", color: "#22334d", borderBottom: "1px solid #f1f1f3" }}>
        <Toolbar sx={{ minHeight: 66 }}>
          <IconButton edge="start" sx={{ mr: 2 }} onClick={() => setSideOpen(!sideOpen)}>
            <MenuIcon />
          </IconButton>
          <Typography variant="h5" sx={{ fontWeight: 900, letterSpacing: 1, color: "#22334d" }}>
            CXDP CRM
          </Typography>
          <Tabs value={tab} onChange={(_, v) => setTab(v)} textColor="primary" indicatorColor="primary" sx={{ ml: 3, minHeight: 0 }}>
            {tabItems.map((t, i) => (
              <Tab key={t} label={t} sx={{ fontWeight: 700, fontSize: 18, px: 2, minHeight: 44 }} />
            ))}
          </Tabs>
          <Box sx={{ flexGrow: 1 }} />
          {/* Профиль справа */}
          <Avatar sx={{ width: 38, height: 38, bgcolor: "#ecefff", color: "#4361EE", fontWeight: 900, mr: 2 }}>МЛ</Avatar>
          <Typography fontWeight={600}>{profile.name.split(" ")[0]}</Typography>
        </Toolbar>
      </AppBar>
      {/* Side menu */}
      <Drawer
        open={sideOpen}
        variant="persistent"
        sx={{
          width: sideOpen ? 235 : 64,
          flexShrink: 0,
          "& .MuiDrawer-paper": {
            width: sideOpen ? 235 : 64,
            bgcolor: "#f8fafb",
            borderRight: "1px solid #f1f1f3",
            transition: "width 0.3s",
            boxShadow: "none",
          },
        }}
      >
        <Box sx={{ display: "flex", alignItems: "center", px: 2, py: 2 }}>
          <Typography variant="h5" fontWeight={800} sx={{ letterSpacing: 1, color: "#22334d", mr: 1 }}>
            <HomeIcon sx={{ color: "#4361ee", mb: -.3, mr: 1 }} />
            CXDP
          </Typography>
          <Box sx={{ flexGrow: 1 }} />
          {sideOpen && (
            <IconButton size="small" onClick={() => setSideOpen(false)}>
              <ChevronLeftIcon />
            </IconButton>
          )}
        </Box>
        <List>
          {sideMenuItems.map((item, i) => (
            <ListItem button key={item.text} selected={i === 0} sx={{
              borderRadius: 2,
              my: .2,
              bgcolor: i === 0 ? "#e7f3ff" : undefined,
              "&:hover": { bgcolor: "#f0f6ff" }
            }}>
              <ListItemIcon sx={{ minWidth: 40 }}>{item.icon}</ListItemIcon>
              {sideOpen && <ListItemText primary={item.text} primaryTypographyProps={{ fontWeight: 600 }} />}
            </ListItem>
          ))}
        </List>
        <Box sx={{ flexGrow: 1 }} />
        <List>
          <ListItem button>
            <ListItemIcon sx={{ minWidth: 40 }}><LogoutIcon /></ListItemIcon>
            {sideOpen && <ListItemText primary="Выход" />}
          </ListItem>
        </List>
      </Drawer>
      {/* Контент */}
      <Box sx={{ pl: sideOpen ? "250px" : "80px", pt: 5, transition: "all 0.3s" }}>
        <Paper elevation={0} sx={{
          p: 4, borderRadius: 4, maxWidth: 1350, mx: "auto", mb: 3,
          boxShadow: "0 6px 24px 0 rgba(80,100,140,0.07)", background: "#fff"
        }}>
          <Grid container spacing={3} alignItems="center">
            {/* Аватар */}
            <Grid item>
              <Avatar
                sx={{
                  width: 116, height: 116, fontSize: 52, bgcolor: "#F4F6FA", color: "#4361EE",
                  boxShadow: "0 4px 12px rgba(67,97,238,0.09)"
                }}
              >
                МЛ
              </Avatar>
            </Grid>
            {/* Имя, ID, возраст, локация, бейджи, статус карты */}
            <Grid item xs>
              <Typography variant="h3" sx={{ fontWeight: 800, letterSpacing: 1, color: "#22334d", mb: .2 }}>
                {profile.name}
              </Typography>
              <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 1 }}>
                <Typography sx={{ color: "#566787", fontSize: 20, display: "flex", alignItems: "center" }}>
                  <PersonIcon fontSize="small" sx={{ mr: .5, opacity: 0.6 }} />
                  ID: {profile.id} &nbsp;|&nbsp; {profile.age} лет &nbsp;|&nbsp; {profile.city}, {profile.country}
                </Typography>
              </Stack>
              <Stack direction="row" spacing={1} alignItems="center" flexWrap="wrap" sx={{ mb: 1 }}>
                {profile.tags.map(tag => (
                  <Chip
                    key={tag}
                    label={<span style={{ fontWeight: 700, fontSize: 18 }}>
                      {tagConfig[tag]?.icon || null}&nbsp;{tag}
                    </span>}
                    sx={{
                      border: `2.5px solid ${tagConfig[tag]?.border || "#D0E2FF"}`,
                      bgcolor: tagConfig[tag]?.color || "#e3f0ff",
                      color: tagConfig[tag]?.text || "#22334d",
                      fontSize: 18,
                      px: 2, py: 1,
                      mr: 1, mb: .5,
                      fontWeight: 700,
                      boxShadow: tag === "VIP" ? "0 2px 12px #ffe38190" : "none"
                    }}
                  />
                ))}
                <Chip
                  icon={<CreditCardIcon sx={{ color: "#449b53" }} />}
                  label={<b>Карта: {profile.cardStatus}</b>}
                  sx={{
                    bgcolor: "#E6F6EB",
                    color: "#288653",
                    fontSize: 19,
                    px: 2,
                    ml: 2,
                    border: "2px solid #b4e3c5",
                    fontWeight: 700,
                    boxShadow: "0 1px 4px #3e88410a"
                  }}
                />
              </Stack>
            </Grid>
            {/* Кнопки действий */}
            <Grid item>
              <Stack direction="row" spacing={1}>
                <Tooltip title="Отправить сообщение">
                  <IconButton sx={{ bgcolor: "#f7f7fb" }} onClick={() => setMsgOpen(true)}>
                    <SendIcon fontSize="large" sx={{ color: "#3f51b5" }} />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Редактировать профиль">
                  <IconButton sx={{ bgcolor: "#f7f7fb" }} onClick={() => { setEditForm(profile); setEditOpen(true); }}>
                    <EditIcon fontSize="large" sx={{ color: "#22334d" }} />
                  </IconButton>
                </Tooltip>
              </Stack>
            </Grid>
          </Grid>
          {/* Табы */}
          <Divider sx={{ my: 4 }} />
          <Tabs value={tab} onChange={(_, v) => setTab(v)} textColor="primary" indicatorColor="primary" sx={{ mb: 2 }}>
            {tabItems.map((t, i) => (
              <Tab key={t} label={t} sx={{ fontWeight: 700, fontSize: 17, px: 2, minHeight: 44 }} />
            ))}
          </Tabs>
          {/* Tab panels */}
          {/* Детали */}
          {tab === 0 && (
            <Box sx={{ pt: 2 }}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <Stack spacing={1.3}>
                    <Typography><EmailIcon sx={{ color: "#2962ff", mr: 1 }} /> <b>Email:</b> {profile.email}</Typography>
                    <Typography><CakeIcon sx={{ color: "#f06292", mr: 1 }} /> <b>Дата рождения:</b> {profile.dob.split("-").reverse().join(".")}</Typography>
                    <Typography><PersonIcon sx={{ color: "#377e54", mr: 1 }} /> <b>Тип клиента:</b> {profile.clientType}</Typography>
                    <Typography><HomeIcon sx={{ color: "#52b8e8", mr: 1 }} /> <b>Адрес:</b> {profile.address}</Typography>
                    <Typography><b>Согласие на рассылку:</b> {profile.marketing ? "Да" : "Нет"}</Typography>
                    <Typography><CreditCardIcon sx={{ color: "#c0a75c", mr: 1 }} /> <b>Карта:</b> {profile.cardNumber}</Typography>
                  </Stack>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Stack spacing={1.3}>
                    <Typography><PhoneIcon sx={{ color: "#0052cc", mr: 1 }} /> <b>Телефон:</b> {profile.phone}</Typography>
                    <Typography><PersonIcon sx={{ color: "#de72c7", mr: 1 }} /> <b>Пол:</b> {profile.gender}</Typography>
                    <Typography><LocationOnIcon sx={{ color: "#00695c", mr: 1 }} /> <b>Город:</b> {profile.city}</Typography>
                    <Typography><TranslateIcon sx={{ color: "#0f65a5", mr: 1 }} /> <b>Язык интерфейса:</b> {profile.lang}</Typography>
                    <Typography><b>Баланс бонусов:</b> {profile.bonus}</Typography>
                    <Typography><b>LTV:</b> {profile.ltv}</Typography>
                  </Stack>
                </Grid>
              </Grid>
            </Box>
          )}
          {/* Покупки */}
          {tab === 1 && (
            <Table sx={{ mt: 2 }}>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Товар</TableCell>
                  <TableCell>Сумма</TableCell>
                  <TableCell>Статус</TableCell>
                  <TableCell>Точка продаж</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {purchases.map((p, i) => (
                  <TableRow key={i} hover>
                    <TableCell>{p.date}</TableCell>
                    <TableCell>{p.item}</TableCell>
                    <TableCell>{p.amount}</TableCell>
                    <TableCell>
                      <Chip label={p.status} size="small" color={p.status.includes("Списано") ? "warning" : "success"} />
                    </TableCell>
                    <TableCell>{p.pos}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
          {/* Бонусы */}
          {tab === 2 && (
            <Table sx={{ mt: 2 }}>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Операция</TableCell>
                  <TableCell>Кампания</TableCell>
                  <TableCell>Сумма</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {bonusHistory.map((b, i) => (
                  <TableRow key={i} hover>
                    <TableCell>{b.date}</TableCell>
                    <TableCell>
                      <Chip label={b.type} color={b.type === "Начисление" ? "success" : "warning"} size="small" />
                    </TableCell>
                    <TableCell>{b.campaign}</TableCell>
                    <TableCell>
                      <Typography sx={{ fontWeight: 700, color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c" }}>{b.value}</Typography>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
          {/* История сообщений */}
          {tab === 3 && (
            <Box sx={{ mt: 1 }}>
              <Button variant="contained" sx={{ mb: 2 }} onClick={() => setMsgOpen(true)} startIcon={<SendIcon />}>Отправить сообщение</Button>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Дата</TableCell>
                    <TableCell>Канал</TableCell>
                    <TableCell>Текст</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {messages.map((m, i) => (
                    <TableRow key={i}>
                      <TableCell>{m.date}</TableCell>
                      <TableCell>
                        <Chip icon={m.channel === "SMS" ? <SmsIcon fontSize="small" /> : <TelegramIcon fontSize="small" />} label={m.channel} size="small" />
                      </TableCell>
                      <TableCell>{m.text}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          )}
          {/* NPS */}
          {tab === 4 && (
            <Table sx={{ mt: 2 }}>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Канал</TableCell>
                  <TableCell>Оценка</TableCell>
                  <TableCell>Комментарий</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {npsSurveys.map((n, i) => (
                  <TableRow key={i}>
                    <TableCell>{n.date}</TableCell>
                    <TableCell><Chip label={n.channel} color="primary" size="small" /></TableCell>
                    <TableCell>
                      <Stack direction="row" alignItems="center" spacing={.5}>
                        <EmojiEventsIcon color={n.score > 8 ? "success" : n.score > 6 ? "warning" : "error"} fontSize="small" />
                        <Typography fontWeight={700}>{n.score}</Typography>
                      </Stack>
                    </TableCell>
                    <TableCell>{n.comment}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </Paper>

        {/* Модалка: редактирование */}
        <Dialog open={editOpen} onClose={() => setEditOpen(false)} maxWidth="sm" fullWidth>
          <DialogTitle>Редактировать профиль</DialogTitle>
          <DialogContent>
            <Stack spacing={2} mt={1}>
              <TextField label="ФИО" fullWidth value={editForm.name} onChange={e => setEditForm(f => ({ ...f, name: e.target.value }))} />
              <TextField label="Email" type="email" fullWidth value={editForm.email} onChange={e => setEditForm(f => ({ ...f, email: e.target.value }))} />
              <TextField label="Телефон" fullWidth value={editForm.phone} onChange={e => setEditForm(f => ({ ...f, phone: e.target.value }))} />
              <TextField label="Дата рождения" type="date" fullWidth InputLabelProps={{ shrink: true }} value={editForm.dob} onChange={e => setEditForm(f => ({ ...f, dob: e.target.value }))} />
              <Select label="Пол" fullWidth value={editForm.gender} onChange={e => setEditForm(f => ({ ...f, gender: e.target.value }))}>
                <MenuItem value="Женский">Женский</MenuItem>
                <MenuItem value="Мужской">Мужской</MenuItem>
                <MenuItem value="Другое">Другое</MenuItem>
              </Select>
              <TextField label="Город" fullWidth value={editForm.city} onChange={e => setEditForm(f => ({ ...f, city: e.target.value }))} />
              <TextField label="Адрес" fullWidth value={editForm.address} onChange={e => setEditForm(f => ({ ...f, address: e.target.value }))} />
            </Stack>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setEditOpen(false)}>Отмена</Button>
            <Button variant="contained" onClick={handleEditSave}>Сохранить</Button>
          </DialogActions>
        </Dialog>

        {/* Модалка: отправка сообщения */}
        <Dialog open={msgOpen} onClose={() => setMsgOpen(false)} maxWidth="xs" fullWidth>
          <DialogTitle>Отправить сообщение клиенту</DialogTitle>
          <DialogContent>
            <Stack spacing={2}>
              <Select
                value={msgChannel}
                onChange={e => setMsgChannel(e.target.value)}
                fullWidth
                label="Канал"
                sx={{ mb: 1 }}
              >
                <MenuItem value="SMS"><SmsIcon sx={{ mr: 1 }} />SMS</MenuItem>
                <MenuItem value="Telegram"><SmsIcon sx={{ mr: 1 }} />Telegram</MenuItem>
              </Select>
              <TextField
                multiline minRows={3} maxRows={7}
                fullWidth
                value={msgText}
                onChange={e => setMsgText(e.target.value)}
                placeholder="Введите сообщение…"
              />
            </Stack>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setMsgOpen(false)}>Отмена</Button>
            <Button variant="contained" onClick={handleSendMsg} disabled={!msgText.trim()}>Отправить</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
}
