import React, { useState } from "react";
import {
  Box, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider, Paper,
  AppBar, Toolbar, Tabs, Tab, Drawer, List, ListItem, ListItemIcon, ListItemText, Grid
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import HomeIcon from "@mui/icons-material/Home";
import PeopleIcon from "@mui/icons-material/People";
import StarIcon from "@mui/icons-material/Star";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import SettingsIcon from "@mui/icons-material/Settings";
import SmsIcon from "@mui/icons-material/Sms";
import EditIcon from "@mui/icons-material/Edit";
import SendIcon from "@mui/icons-material/Send";
import CreditCardIcon from "@mui/icons-material/CreditCard";
import CakeIcon from "@mui/icons-material/Cake";
import EmailIcon from "@mui/icons-material/Email";
import PhoneIcon from "@mui/icons-material/Phone";
import PersonIcon from "@mui/icons-material/Person";
import TranslateIcon from "@mui/icons-material/Translate";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";
import PeopleAltIcon from "@mui/icons-material/PeopleAlt";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import LogoutIcon from "@mui/icons-material/Logout";
import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";

const sideMenuItems = [
  { text: "Клиенты", icon: <PeopleIcon />, route: "clients" },
  { text: "Покупки", icon: <LocalOfferIcon />, route: "purchases" },
  { text: "Бонусы", icon: <StarIcon />, route: "bonuses" },
  { text: "Подарки", icon: <CardGiftcardIcon />, route: "gifts" },
  { text: "Настройки", icon: <SettingsIcon />, route: "settings" },
];

const tabItems = [
  "Общие",
  "Покупки",
  "Бонусы",
  "Промокоды",
  "Подарки",
  "Интересы",
  "NPS"
];

// --- Маппинг меток для Bitrix24
const tagConfig = {
  "VIP": { icon: <StarIcon />, color: "#FFD700", border: "#FFD700", text: "#222" },
  "Gold": { icon: <StarIcon />, color: "#F6F6BA", border: "#FFD700", text: "#856b00" },
  "Рефералы: 8": { icon: <PeopleAltIcon />, color: "#fff8ee", border: "#FFA726", text: "#FF9800" },
  "Постоянный клиент": { icon: <ThumbUpIcon />, color: "#E3F0FF", border: "#90caf9", text: "#2574b6" },
  "Гость сайта": { icon: <PersonIcon />, color: "#E3F0FF", border: "#90caf9", text: "#2574b6" },
  "Отзывчивый": { icon: <ThumbUpIcon />, color: "#E3F0FF", border: "#90caf9", text: "#2574b6" },
};

const profile = {
  name: "Мария Летунова",
  id: "874392",
  age: 36,
  city: "Краснодар",
  country: "Россия",
  email: "maria.letunova@yandex.ru",
  phone: "+7 988 509-96-62",
  dob: "16.01.1989",
  gender: "Женский",
  clientType: "Физ. лицо",
  address: "ул. Ленина, д. 10, кв. 24",
  lang: "Русский",
  marketing: true,
  cardNumber: "7712 3456 7890 0012",
  cardStatus: "Активна",
  bonus: "1 563",
  ltv: "108 400 ₽",
  tags: ["VIP", "Gold", "Рефералы: 8", "Постоянный клиент", "Гость сайта", "Отзывчивый"],
};

export default function Bitrix24Profile() {
  const [sideOpen, setSideOpen] = useState(true);
  const [tab, setTab] = useState(0);

  return (
    <Box sx={{ bgcolor: "#F4F6FA", minHeight: "100vh" }}>
      {/* Header */}
      <AppBar position="static" elevation={0} sx={{ bgcolor: "#fff", color: "#22334d", borderBottom: "1px solid #f1f1f3" }}>
        <Toolbar sx={{ minHeight: 66 }}>
          <IconButton edge="start" sx={{ mr: 2 }} onClick={() => setSideOpen(!sideOpen)}>
            <MenuIcon />
          </IconButton>
          <Typography variant="h5" sx={{ fontWeight: 900, letterSpacing: 1, color: "#22334d" }}>
            CXDP CRM
          </Typography>
          <Tabs
            value={tab}
            onChange={(_, v) => setTab(v)}
            textColor="primary"
            indicatorColor="primary"
            sx={{ ml: 3, minHeight: 0 }}
          >
            {tabItems.map((t, i) => (
              <Tab key={t} label={t} sx={{ fontWeight: 700, fontSize: 18, px: 2, minHeight: 44 }} />
            ))}
          </Tabs>
          <Box sx={{ flexGrow: 1 }} />
          {/* Профиль справа */}
          <Avatar sx={{ width: 38, height: 38, bgcolor: "#ecefff", color: "#4361EE", fontWeight: 900, mr: 2 }}>
            МЛ
          </Avatar>
          <Typography fontWeight={600}>Мария</Typography>
        </Toolbar>
      </AppBar>
      {/* Боковое меню */}
      <Drawer
        open={sideOpen}
        variant="persistent"
        sx={{
          width: sideOpen ? 235 : 64,
          flexShrink: 0,
          "& .MuiDrawer-paper": {
            width: sideOpen ? 235 : 64,
            bgcolor: "#f8fafb",
            borderRight: "1px solid #f1f1f3",
            transition: "width 0.3s",
            boxShadow: "none",
          },
        }}
      >
        <Box sx={{ display: "flex", alignItems: "center", px: 2, py: 2 }}>
          <Typography variant="h5" fontWeight={800} sx={{ letterSpacing: 1, color: "#22334d", mr: 1 }}>
            <HomeIcon sx={{ color: "#4361ee", mb: -.3, mr: 1 }} />
            CXDP
          </Typography>
          <Box sx={{ flexGrow: 1 }} />
          {sideOpen && (
            <IconButton size="small" onClick={() => setSideOpen(false)}>
              <ChevronLeftIcon />
            </IconButton>
          )}
        </Box>
        <List>
          {sideMenuItems.map((item, i) => (
            <ListItem button key={item.text} selected={i === 0} sx={{
              borderRadius: 2,
              my: .2,
              bgcolor: i === 0 ? "#e7f3ff" : undefined,
              "&:hover": { bgcolor: "#f0f6ff" }
            }}>
              <ListItemIcon sx={{ minWidth: 40 }}>{item.icon}</ListItemIcon>
              {sideOpen && <ListItemText primary={item.text} primaryTypographyProps={{ fontWeight: 600 }} />}
            </ListItem>
          ))}
        </List>
        <Box sx={{ flexGrow: 1 }} />
        <List>
          <ListItem button>
            <ListItemIcon sx={{ minWidth: 40 }}><LogoutIcon /></ListItemIcon>
            {sideOpen && <ListItemText primary="Выход" />}
          </ListItem>
        </List>
      </Drawer>
      {/* Контент */}
      <Box sx={{ pl: sideOpen ? "250px" : "80px", pt: 5, transition: "all 0.3s" }}>
        <Paper
          elevation={0}
          sx={{
            p: 4, borderRadius: 4, maxWidth: 1350, mx: "auto", mb: 3,
            boxShadow: "0 6px 24px 0 rgba(80,100,140,0.07)", background: "#fff"
          }}
        >
          <Grid container spacing={3} alignItems="center">
            {/* Аватар */}
            <Grid item>
              <Avatar
                sx={{
                  width: 116, height: 116, fontSize: 52, bgcolor: "#F4F6FA", color: "#4361EE",
                  boxShadow: "0 4px 12px rgba(67,97,238,0.09)"
                }}
              >
                МЛ
              </Avatar>
            </Grid>
            {/* Имя, ID, возраст, локация, бейджи, статус карты */}
            <Grid item xs>
              <Typography variant="h3" sx={{ fontWeight: 800, letterSpacing: 1, color: "#22334d", mb: .2 }}>
                {profile.name}
              </Typography>
              <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 1 }}>
                <Typography sx={{ color: "#566787", fontSize: 20, display: "flex", alignItems: "center" }}>
                  <PersonIcon fontSize="small" sx={{ mr: .5, opacity: 0.6 }} />
                  ID: {profile.id} &nbsp;|&nbsp; {profile.age} лет &nbsp;|&nbsp; {profile.city}, {profile.country}
                </Typography>
              </Stack>
              <Stack direction="row" spacing={1} alignItems="center" flexWrap="wrap" sx={{ mb: 1 }}>
                {profile.tags.map(tag => (
                  <Chip
                    key={tag}
                    label={<span style={{ fontWeight: 700, fontSize: 18 }}>
                      {tagConfig[tag]?.icon || null}&nbsp;{tag}
                    </span>}
                    sx={{
                      border: `2.5px solid ${tagConfig[tag]?.border || "#D0E2FF"}`,
                      bgcolor: tagConfig[tag]?.color || "#e3f0ff",
                      color: tagConfig[tag]?.text || "#22334d",
                      fontSize: 18,
                      px: 2, py: 1,
                      mr: 1, mb: .5,
                      fontWeight: 700,
                      boxShadow: tag === "VIP" ? "0 2px 12px #ffe38190" : "none"
                    }}
                  />
                ))}
                <Chip
                  icon={<CreditCardIcon sx={{ color: "#449b53" }} />}
                  label={<b>Карта: {profile.cardStatus}</b>}
                  sx={{
                    bgcolor: "#E6F6EB",
                    color: "#288653",
                    fontSize: 19,
                    px: 2,
                    ml: 2,
                    border: "2px solid #b4e3c5",
                    fontWeight: 700,
                    boxShadow: "0 1px 4px #3e88410a"
                  }}
                />
              </Stack>
            </Grid>
            {/* Кнопки действий */}
            <Grid item>
              <Stack direction="row" spacing={1}>
                <Tooltip title="Отправить сообщение">
                  <IconButton sx={{ bgcolor: "#f7f7fb" }}>
                    <SendIcon fontSize="large" sx={{ color: "#3f51b5" }} />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Редактировать профиль">
                  <IconButton sx={{ bgcolor: "#f7f7fb" }}>
                    <EditIcon fontSize="large" sx={{ color: "#22334d" }} />
                  </IconButton>
                </Tooltip>
              </Stack>
            </Grid>
          </Grid>
          {/* Данные профиля — табличка */}
          <Box sx={{ mt: 3, mb: 0 }}>
            <Grid container spacing={2}>
              {/* Левая колонка */}
              <Grid item xs={12} sm={6} md={6}>
                <Stack spacing={1.2}>
                  <Typography><EmailIcon sx={{ color: "#2962ff", mr: 1 }} /> <b>Email:</b> {profile.email}</Typography>
                  <Typography><CakeIcon sx={{ color: "#f06292", mr: 1 }} /> <b>Дата рождения:</b> {profile.dob}</Typography>
                  <Typography><PersonIcon sx={{ color: "#377e54", mr: 1 }} /> <b>Тип клиента:</b> {profile.clientType}</Typography>
                  <Typography><HomeIcon sx={{ color: "#52b8e8", mr: 1 }} /> <b>Адрес:</b> {profile.address}</Typography>
                  <Typography><b>Согласие на рассылку:</b> {profile.marketing ? "Да" : "Нет"}</Typography>
                  <Typography><CreditCardIcon sx={{ color: "#c0a75c", mr: 1 }} /> <b>Карта:</b> {profile.cardNumber}</Typography>
                </Stack>
              </Grid>
              {/* Правая колонка */}
              <Grid item xs={12} sm={6} md={6}>
                <Stack spacing={1.2}>
                  <Typography><PhoneIcon sx={{ color: "#0052cc", mr: 1 }} /> <b>Телефон:</b> {profile.phone}</Typography>
                  <Typography><PersonIcon sx={{ color: "#de72c7", mr: 1 }} /> <b>Пол:</b> {profile.gender}</Typography>
                  <Typography><LocationOnIcon sx={{ color: "#00695c", mr: 1 }} /> <b>Город:</b> {profile.city}</Typography>
                  <Typography><TranslateIcon sx={{ color: "#0f65a5", mr: 1 }} /> <b>Язык интерфейса:</b> {profile.lang}</Typography>
                  <Typography><b>Баланс бонусов:</b> {profile.bonus}</Typography>
                  <Typography><b>LTV:</b> {profile.ltv}</Typography>
                </Stack>
              </Grid>
            </Grid>
          </Box>
        </Paper>
        {/* Ниже можешь вставить все вкладки, списки покупок и т.д. */}
      </Box>
    </Box>
  );
}
