// StickyBar Component (можно вынести, но вставляю прямо)
function StickyProfileBar({ show, name, phone, status, riskFlag }) {
  return (
    <Slide in={show} direction="down" mountOnEnter unmountOnExit>
      <Paper
        elevation={3}
        sx={{
          position: "fixed",
          top: 0, left: 0, right: 0,
          zIndex: 1201,
          py: 1.2, px: 4,
          display: "flex",
          alignItems: "center",
          gap: 3,
          bgcolor: "#fff",
          borderBottom: "1.5px solid #e2e7f5",
          boxShadow: "0 4px 16px #c4dafc14",
        }}
      >
        <Typography variant="h6" fontWeight={700} sx={{ mr: 2 }}>
          {name}
        </Typography>
        <Stack direction="row" spacing={1} alignItems="center">
          <PhoneIcon fontSize="small" color="action" />
          <Typography fontWeight={500}>{phone}</Typography>
        </Stack>
        <Chip
          label={status}
          color={status === "Постоянный клиент" ? "primary" : "default"}
          sx={{ fontWeight: 700, ml: 2 }}
        />
        {riskFlag && (
          <Chip
            icon={<WarningAmberIcon sx={{ color: "#e05f45" }} />}
            label="Есть риск ухода"
            color="warning"
            size="small"
            sx={{ fontWeight: 800, ml: 2 }}
          />
        )}
      </Paper>
    </Slide>
  );
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div role="tabpanel" hidden={value !== index} {...other} style={{ width: "100%" }}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

