sourcesContent:
import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField,
  Select, MenuItem, Checkbox, FormControl, InputLabel
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import WorkspacePremiumIcon from "@mui/icons-material/WorkspacePremium";
import CreditCardIcon from "@mui/icons-material/CreditCard";
import GroupAddIcon from "@mui/icons-material/GroupAdd";

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваша бронирование подтверждена. Ждём вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

function TabPanel({ children, value, index, ...other }) {
  return (
    <div role="tabpanel" hidden={value !== index} {...other}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

export default function CustomerProfileCard() {
  // --- PROFILE
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    city: "Краснодар",
    country: "Россия",
    gender: "Женский",
    clientType: "Физ. лицо",
    company: "",
    inn: "",
    address: "ул. Ленина, д. 10, кв. 24",
    marketing: true,
    lang: "Русский",
    age: 36,
    tags: ["VIP", "Постоянный клиент", "Гость сайта", "Отзывчивый"],
    ltv: "108 400 ₽",
    bonus: "1 563",
    bonusLevel: "Gold",
    cardStatus: "Активна",
    referrals: 8,
    interests: [
      "Горные туры", "Зимний отдых", "SPA и релакс", "Детские активности", "Экстрим", "Кулинария", "Корпоративные мероприятия"
    ]
  });

  // Покупки и чеки
  const purchases = [
    {
      date: "30.05.2025",
      item: "Бронирование отеля",
      amount: "4 400 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 4400, writtenOff: false }
      ]
    },
    {
      date: "12.04.2025",
      item: "Тренировка в фитнес-зале",
      amount: "0 ₽",
      status: "Оплачено (списание бонусов)",
      pos: "Фитнес-клуб Центр",
      receiptId: "CHK-1022",
      employee: "Максим Шестаков",
      receipt: [
        { name: "Тренировка фитнес", qty: 1, price: 700, writtenOff: true, writtenOffBonuses: 700 }
      ]
    },
    {
      date: "03.03.2025",
      item: "Ски-пасс",
      amount: "15 266,7 ₽",
      status: "Оплачено (частично бонусами)",
      pos: "Касса Горки Молл",
      receiptId: "CHK-9823",
      employee: "Иван Калашников",
      receipt: [
        { name: "Ски-пасс взрослый", qty: 2, price: 12000, writtenOff: true, writtenOffBonuses: 10000 },
        { name: "Ски-пасс детский", qty: 1, price: 5266.7, writtenOff: false }
      ]
    },
    {
      date: "22.02.2025",
      item: "Ресторан «Пикник»",
      amount: "2 330 ₽",
      status: "Оплачено",
      pos: "Ресторан «Пикник»",
      receiptId: "CHK-9541",
      employee: "Маргарита Ромашова",
      receipt: [
        { name: "Обед в ресторане", qty: 1, price: 2330, writtenOff: false }
      ]
    },
    {
      date: "14.01.2025",
      item: "Массаж СПА",
      amount: "4 990 ₽",
      status: "Оплачено (списание бонусов)",
      pos: "SPA-центр Relax",
      receiptId: "CHK-9124",
      employee: "Кирилл Зимин",
      receipt: [
        { name: "Массаж общий", qty: 1, price: 4990, writtenOff: true, writtenOffBonuses: 4990 }
      ]
    }
  ];

  // NPS анкеты
  const npsSurveys = [
    {
      date: "01.06.2025",
      channel: "Email",
      score: 10,
      comment: "Всё понравилось! Спасибо сотруднику Екатерине.",
      order: "CHK-1001"
    },
    {
      date: "15.04.2025",
      channel: "SMS",
      score: 6,
      comment: "Была небольшая задержка на ресепшене.",
      order: "CHK-1022"
    },
    {
      date: "01.09.2024",
      channel: "Telegram",
      score: 9,
      comment: "Очень вкусно и быстро обслужили на банкете.",
      order: "CHK-6789"
    }
  ];

  // Бонусы
  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление", campaign: "Акция «Вернем 30%»" },
    { date: "20.05.2025", value: "-375", type: "Списание", campaign: "Оплата заказа #CHK-8431" },
    { date: "13.04.2025", value: "+300", type: "Начисление", campaign: "Программа лояльности" },
    { date: "12.04.2025", value: "-700", type: "Списание", campaign: "Фитнес по бонусам" },
    { date: "22.02.2025", value: "+233", type: "Начисление", campaign: "Бонус за заказ в ресторане" }
  ];

  // Промокоды
  const promoCodes = [
    { code: "VIP2025", discount: "15%", used: false },
    { code: "SKI2024", discount: "20%", used: true },
    { code: "RESTAURANT10", discount: "10%", used: false }
  ];

  // Подарки
  const gifts = [
    { date: "2024-01-15", name: "Подарочная карта 1 000 ₽", status: "Активна" },
    { date: "2023-11-22", name: "Фирменный термос", status: "Получено" },
    { date: "2023-10-10", name: "Скидка на массаж", status: "Использовано" }
  ];

  // Сообщения (ТОЛЬКО исходящие)
  const [messageHistory, setMessageHistory] = useState([
    { date: "10.06.2025 10:23", channel: "SMS", direction: "Исходящее", text: "Спасибо за ваш отзыв!" },
    { date: "01.06.2025 09:50", channel: "Telegram", direction: "Исходящее", text: "Добрый день! Ваша скидка активирована." }
  ]);

  // Статистика
  const paidPurchases = purchases.filter(p => p.status.includes("Оплачено"));
  const totalPurchases = paidPurchases.length;
  const avgCheck = totalPurchases === 0 ? 0 :
    Math.round(
      paidPurchases
        .map(p => Number(p.amount.replace(/[ ₽,]/g, "").replace(",", ".")))
        .reduce((a, b) => a + b, 0) / totalPurchases
    );
  const lastPurchaseDate = paidPurchases.length
    ? paidPurchases
        .map(p => p.date)
        .sort((a, b) => new Date(b.split(".").reverse().join("-")) - new Date(a.split(".").reverse().join("-")))[0]
    : "-";

  // Модалки
  const [tab, setTab] = useState(0);
  const [editOpen, setEditOpen] = useState(false);
  const [form, setForm] = useState(profile);
  const [receiptModal, setReceiptModal] = useState({ open: false, purchase: null });
  const [msgModal, setMsgModal] = useState(false);
  const [msgChannel, setMsgChannel] = useState("SMS");
  const [msgText, setMsgText] = useState("");
  const [msgSending, setMsgSending] = useState(false);

  const openReceipt = (purchase) => setReceiptModal({ open: true, purchase });
  const closeReceipt = () => setReceiptModal({ open: false, purchase: null });
  const handleEdit = () => { setForm(profile); setEditOpen(true); };
  const handleEditClose = () => setEditOpen(false);
  const handleEditSave = () => {
    setProfile({
      ...form,
      age: form.dob ? (new Date().getFullYear() - Number(form.dob.slice(0, 4))) : profile.age
    });
    setEditOpen(false);
  };
  const handleFormChange = (field, value) => setForm((prev) => ({ ...prev, [field]: value }));
  const openMsgModal = () => { setMsgText(""); setMsgChannel("SMS"); setMsgModal(true); };
  const closeMsgModal = () => setMsgModal(false);
  const sendMessage = () => {
    if (!msgText.trim()) return;
    setMsgSending(true);
    setTimeout(() => {
      setMessageHistory(prev => [
        {
          date: new Date().toLocaleString("ru-RU").slice(0, 17),
          channel: msgChannel,
          direction: "Исходящее",
          text: msgText
        }, ...prev
      ]);
      setMsgSending(false);
      setMsgModal(false);
    }, 700);
  };

  return (
    <Box sx={{ bgcolor: "#F4F6FA", minHeight: "100vh", py: 5, px: 1 }}>
      <Box sx={{
        maxWidth: 1200,
        mx: "auto",
        bgcolor: "#fff",
        borderRadius: 4,
        boxShadow: "0 4px 24px rgba(20,25,55,0.07)",
        px: { xs: 2, sm: 5 },
        py: { xs: 3, sm: 5 }
      }}>
        {/* Верхняя карточка */}
        <Grid container spacing={3} alignItems="center" mb={1}>
          <Grid item>
            <Avatar sx={{
              width: 86, height: 86, fontSize: 40, bgcolor: "#EEF1F7", color: "#4361EE"
            }}>
              {profile.name.split(" ").map(part => part[0]).join("").toUpperCase()}
            </Avatar>
          </Grid>
          <Grid item xs>
            <Typography variant="h4" fontWeight={700} color="#22334d" sx={{ letterSpacing: 1 }}>
              {profile.name}
            </Typography>
            <Typography color="text.secondary" sx={{ fontSize: 17, mt: .5, mb: 1 }}>
              <PersonIcon fontSize="small" sx={{ verticalAlign: "middle", mr: 1 }} />
              ID: {profile.id} &nbsp;|&nbsp; {profile.age} лет &nbsp;|&nbsp; {profile.city}, {profile.country}
            </Typography>

            {/* Современные метки статуса и тегов */}
            <Stack direction="row" spacing={1} flexWrap="wrap" sx={{ my: 1 }}>
              {profile.tags.map((t, idx) => (
                <Chip
                  key={idx}
                  label={t}
                  size="medium"
                  icon={
                    t === "VIP"
                      ? <StarIcon sx={{ color: "#FFD700" }} />
                      : t === "Постоянный клиент"
                      ? <WorkspacePremiumIcon sx={{ color: "#2196f3" }} />
                      : t === "Гость сайта"
                      ? <PersonIcon sx={{ color: "#cfd8dc" }} />
                      : <PersonIcon sx={{ color: "#bdbdbd" }} />
                  }
                  sx={{
                    bgcolor:
                      t === "VIP"
                        ? "#FFF8E1"
                        : t === "Постоянный клиент"
                        ? "#E3F2FD"
                        : t === "Гость сайта"
                        ? "#ECEFF1"
                        : "#f3f6fc",
                    color:
                      t === "VIP"
                        ? "#C69000"
                        : t === "Постоянный клиент"
                        ? "#1976d2"
                        : t === "Гость сайта"
                        ? "#607d8b"
                        : "#1a237e",
                    border: t === "VIP" ? "1.5px solid #FFD700" : "1px solid #e0e0e0",
                    fontWeight: t === "VIP" ? 700 : 500,
                    fontSize: 15,
                    boxShadow: t === "VIP" ? "0 2px 12px #FFFACD55" : undefined,
                    letterSpacing: 0.5,
                    px: 1.4,
                    py: 0.7,
                    borderRadius: 2,
                    textTransform: "none"
                  }}
                />
              ))}
              {/* Дополнительные метки */}
              <Chip
                icon={<CreditCardIcon sx={{ color: "#43a047" }} />}
                label={`Карта: ${profile.cardStatus}`}
                sx={{
                  bgcolor: "#E8F5E9",
                  color: "#43a047",
                  fontWeight: 600,
                  fontSize: 15,
                  borderRadius: 2,
                  px: 1.6
                }}
              />
              <Chip
                icon={<WorkspacePremiumIcon sx={{ color: "#FFD700" }} />}
                label={profile.bonusLevel}
                sx={{
                  bgcolor: "#FFFDE7",
                  color: "#B8860B",
                  fontWeight: 700,
                  border: "1.5px solid #FFD700",
                  fontSize: 15,
                  borderRadius: 2,
                  px: 1.6
                }}
              />
              <Chip
                icon={<GroupAddIcon sx={{ color: "#ff9800" }} />}
                label={`Рефералы: ${profile.referrals}`}
                sx={{
                  bgcolor: "#FFF3E0",
                  color: "#F57C00",
                  fontWeight: 600,
                  fontSize: 15,
                  borderRadius: 2,
                  px: 1.6
                }}
              />
            </Stack>

            {/* Основная таблица профиля */}
            <Table size="small" sx={{ mt: 2, maxWidth: 700 }}>
              <TableBody>
                <TableRow>
                  <TableCell sx={{ fontWeight: 500, width: 150 }}>Email:</TableCell>
                  <TableCell>{profile.email}</TableCell>
                  <TableCell sx={{ fontWeight: 500, width: 120 }}>Телефон:</TableCell>
                  <TableCell>{profile.phone}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell sx={{ fontWeight: 500 }}>Дата рождения:</TableCell>
                  <TableCell>{profile.dob.split("-").reverse().join(".")}</TableCell>
                  <TableCell sx={{ fontWeight: 500 }}>Пол:</TableCell>
                  <TableCell>{profile.gender}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell sx={{ fontWeight: 500 }}>Тип клиента:</TableCell>
                  <TableCell>{profile.clientType}</TableCell>
                  <TableCell sx={{ fontWeight: 500 }}>Город:</TableCell>
                  <TableCell>{profile.city}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell sx={{ fontWeight: 500 }}>Адрес:</TableCell>
                  <TableCell colSpan={3}>{profile.address}</TableCell>
                </TableRow>
                <TableRow>
                  <TableCell sx={{ fontWeight: 500 }}>Язык интерфейса:</TableCell>
                  <TableCell>{profile.lang}</TableCell>
                  <TableCell sx={{ fontWeight: 500 }}>Согласие на рассылку:</TableCell>
                  <TableCell>{profile.marketing ? "Да" : "Нет"}</TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </Grid>
          <Grid item>
            <Stack direction="row" spacing={1}>
              <Tooltip title="Отправить сообщение">
                <IconButton sx={{ bgcolor: "#F1F3F6" }} onClick={openMsgModal}>
                  <SendIcon fontSize="medium" sx={{ color: "#3f51b5" }} />
                </IconButton>
              </Tooltip>
              <Tooltip title="Редактировать профиль">
                <IconButton sx={{ bgcolor: "#F1F3F6" }} onClick={handleEdit}>
                  <EditIcon fontSize="medium" sx={{ color: "#22334d" }} />
                </IconButton>
              </Tooltip>
            </Stack>
          </Grid>
        </Grid>
        <Divider sx={{ my: 3 }} />

        {/* Статистика */}
        <Grid container spacing={3} mb={2}>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Общее количество покупок</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{totalPurchases}</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Средний чек</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{avgCheck} ₽</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Дата последней покупки</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{lastPurchaseDate}</Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Paper sx={{ p: 2, textAlign: "center", bgcolor: "#F8FAFF" }}>
              <Typography variant="body2" color="text.secondary">Текущий баланс бонусов</Typography>
              <Typography variant="h5" sx={{ fontWeight: 700 }}>{profile.bonus}</Typography>
            </Paper>
          </Grid>
        </Grid>
        <Divider sx={{ my: 3 }} />

        {/* Tabs */}
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          aria-label="customer tabs"
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2 }}
        >
          <Tab label="Бонусы" />
          <Tab label="Покупки и чеки" />
          <Tab label="Промокоды" />
          <Tab label="Подарки" />
          <Tab label="NPS" />
          <Tab label="Интересы" />
          <Tab icon={<SmsIcon />} iconPosition="start" label="История сообщений" />
        </Tabs>

        {/* Бонусы */}
        <TabPanel value={tab} index={0}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Тип операции</TableCell>
                <TableCell>Кампания</TableCell>
                <TableCell>Сумма</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {bonusHistory.map((b, i) => (
                <TableRow key={i}>
                  <TableCell>{b.date}</TableCell>
                  <TableCell>
                    {b.type === "Начисление" ? (
                      <Chip label="Начисление" color="success" size="small" />
                    ) : (
                      <Chip label="Списание" color="warning" size="small" />
                    )}
                  </TableCell>
                  <TableCell>{b.campaign}</TableCell>
                  <TableCell>
                    <Typography sx={{
                      fontWeight: 700,
                      color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c"
                    }}>{b.value}</Typography>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* История покупок */}
        <TabPanel value={tab} index={1}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Точка продаж</TableCell>
                <TableCell>Сотрудник</TableCell>
                <TableCell>Чек</TableCell>
                <TableCell>Сумма</TableCell>
                <TableCell>Статус</TableCell>
                <TableCell></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {purchases.map((p, i) => (
                <TableRow key={i}>
                  <TableCell>{p.date}</TableCell>
                  <TableCell>{p.pos}</TableCell>
                  <TableCell>{p.employee}</TableCell>
                  <TableCell>
                    <Button
                      variant="text"
                      size="small"
                      onClick={() => openReceipt(p)}
                      endIcon={<ReceiptLongIcon />}
                    >
                      {p.receiptId}
                    </Button>
                  </TableCell>
                  <TableCell>{p.amount}</TableCell>
                  <TableCell>{p.status}</TableCell>
                  <TableCell></TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Промокоды */}
        <TabPanel value={tab} index={2}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Промокод</TableCell>
                <TableCell>Скидка</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {promoCodes.map((p, i) => (
                <TableRow key={i}>
                  <TableCell>{p.code}</TableCell>
                  <TableCell>{p.discount}</TableCell>
                  <TableCell>
                    <Chip
                      label={p.used ? "Использован" : "Активен"}
                      color={p.used ? "default" : "success"}
                      size="small"
                      sx={{ fontWeight: 600 }}
                    />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Подарки */}
        <TabPanel value={tab} index={3}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Подарок</TableCell>
                <TableCell>Статус</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {gifts.map((g, i) => (
                <TableRow key={i}>
                  <TableCell>{g.date}</TableCell>
                  <TableCell>{g.name}</TableCell>
                  <TableCell>
                    <Chip
                      label={g.status}
                      color={g.status === "Активна" ? "success" : g.status === "Получено" ? "primary" : "default"}
                      size="small"
                      sx={{ fontWeight: 600 }}
                    />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* NPS */}
        <TabPanel value={tab} index={4}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Канал</TableCell>
                <TableCell>Оценка (0-10)</TableCell>
                <TableCell>Комментарий</TableCell>
                <TableCell>Заказ</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {npsSurveys.map((n, i) => (
                <TableRow key={i}>
                  <TableCell>{n.date}</TableCell>
                  <TableCell>
                    <Chip label={n.channel} size="small" color="primary" />
                  </TableCell>
                  <TableCell>
                    <Stack direction="row" alignItems="center" spacing={0.5}>
                      <StarIcon fontSize="small" sx={{ color: n.score > 8 ? "#219653" : n.score > 6 ? "#FFB300" : "#C62828" }} />
                      <Typography fontWeight={700}>{n.score}</Typography>
                    </Stack>
                  </TableCell>
                  <TableCell>{n.comment}</TableCell>
                  <TableCell>
                    <Button
                      size="small"
                      variant="text"
                      onClick={() => openReceipt(purchases.find(x=>x.receiptId===n.order))}
                      endIcon={<ReceiptLongIcon />}
                    >{n.order}</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* Интересы */}
        <TabPanel value={tab} index={5}>
          <Stack direction="row" spacing={1} flexWrap="wrap">
            {profile.interests.map((interest, i) => (
              <Chip key={i} label={interest} sx={{ fontSize: 16, p: 1.5, m: .5, bgcolor: "#f3f6fc" }} />
            ))}
          </Stack>
        </TabPanel>

        {/* История сообщений */}
        <TabPanel value={tab} index={6}>
          <Button variant="contained" sx={{ mb: 2 }} onClick={openMsgModal} startIcon={<SendIcon />}>Отправить сообщение</Button>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Канал</TableCell>
                <TableCell>Тип</TableCell>
                <TableCell>Текст</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {messageHistory.map((m, i) => (
                <TableRow key={i}>
                  <TableCell>{m.date}</TableCell>
                  <TableCell>
                    {m.channel === "SMS" ? <Chip icon={<SmsIcon fontSize="small" />} label="SMS" size="small" /> :
                      <Chip icon={<TelegramIcon fontSize="small" />} label="Telegram" size="small" color="primary" />}
                  </TableCell>
                  <TableCell>
                    <Chip label={m.direction} size="small" color="success" />
                  </TableCell>
                  <TableCell>{m.text}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TabPanel>

        {/* ----------- Модальное окно ЧЕК ----------- */}
        <Dialog
          open={receiptModal.open}
          onClose={closeReceipt}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle>
            Чек {receiptModal.purchase?.receiptId} — {receiptModal.purchase?.date}
          </DialogTitle>
          <DialogContent>
            <Typography sx={{ mb: 1.5, fontSize: 16 }}>
              <b>Точка продаж:</b> {receiptModal.purchase?.pos}<br />
              <b>Сотрудник:</b> {receiptModal.purchase?.employee}
            </Typography>
            <Table size="medium">
              <TableHead>
                <TableRow>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Товар</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Кол-во</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Цена</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Сумма</TableCell>
                  <TableCell sx={{ fontWeight: 700, fontSize: 16 }}>Списано бонусов</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {receiptModal.purchase?.receipt.map((row, i) => (
                  <TableRow key={i} hover>
                    <TableCell>
                      <Stack direction="row" alignItems="center" spacing={1}>
                        <span>{row.name}</span>
                        {row.writtenOff && (
                          <Chip
                            label="Списано бонусами"
                            size="small"
                            sx={{
                              bgcolor: "#1976d2",
                              color: "#fff",
                              fontWeight: 600,
                              fontSize: 13,
                              height: "28px",
                              borderRadius: 2,
                              px: 1.5
                            }}
                          />
                        )}
                      </Stack>
                    </TableCell>
                    <TableCell>{row.qty}</TableCell>
                    <TableCell>{row.price.toLocaleString()} ₽</TableCell>
                    <TableCell>{(row.qty * row.price).toLocaleString()} ₽</TableCell>
                    <TableCell align="center">
                      {row.writtenOffBonuses
                        ? <span style={{
                            color: "#9c27b0",
                            fontWeight: 700,
                            fontSize: 16
                          }}>{row.writtenOffBonuses} бонусов</span>
                        : <span style={{ color: "#bbb" }}>—</span>
                      }
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </DialogContent>
          <DialogActions>
            <Button onClick={closeReceipt} variant="outlined">ЗАКРЫТЬ</Button>
          </DialogActions>
        </Dialog>

        {/* --------- Модальное окно ОТПРАВКИ СООБЩЕНИЯ --------- */}
        <Dialog
          open={msgModal}
          onClose={closeMsgModal}
          maxWidth="xs"
          fullWidth
          PaperProps={{
            sx: {
              borderRadius: 4, bgcolor: "#fff",
              boxShadow: "0 8px 32px rgba(24,32,52,0.18)"
            }
          }}
        >
          <DialogTitle>
            Отправить сообщение клиенту
          </DialogTitle>
          <DialogContent>
            <FormControl sx={{ mb: 2, minWidth: 140 }}>
              <InputLabel id="channel-label">Канал</InputLabel>
              <Select
                labelId="channel-label"
                value={msgChannel}
                label="Канал"
                onChange={e => setMsgChannel(e.target.value)}
                startAdornment={
                  msgChannel === "SMS"
                    ? <SmsIcon sx={{ mr: 1, color: "#3f51b5" }} />
                    : <TelegramIcon sx={{ mr: 1, color: "#0088cc" }} />
                }
              >
                <MenuItem value="SMS">
                  <Stack direction="row" alignItems="center" spacing={1}><SmsIcon />SMS</Stack>
                </MenuItem>
                <MenuItem value="Telegram">
                  <Stack direction="row" alignItems="center" spacing={1}><TelegramIcon />Telegram</Stack>
                </MenuItem>
              </Select>
            </FormControl>
            <Stack spacing={1}>
              <Stack direction="row" spacing={1}>
                {MESSAGE_TEMPLATES.map((tpl, idx) => (
                  <Button
                    key={idx}
                    variant="outlined"
                    size="small"
                    onClick={() => setMsgText(tpl)}
                    sx={{
                      borderRadius: 2,
                      bgcolor: "#F7F9FC",
                      fontSize: 13,
                      px: 1.5,
                      textTransform: "none",
                    }}
                    startIcon={<ContentCopyIcon fontSize="small" />}
                  >{tpl.length > 30 ? tpl.slice(0, 27) + "…" : tpl}</Button>
                ))}
              </Stack>
              <TextField
                fullWidth
                multiline
                minRows={3}
                maxRows={8}
                placeholder="Введите сообщение…"
                value={msgText}
                onChange={e => setMsgText(e.target.value)}
                sx={{
                  ".MuiInputBase-root": { bgcolor: "#F6F9FC", borderRadius: 2 }
                }}
              />
            </Stack>
          </DialogContent>
          <DialogActions>
            <Button onClick={closeMsgModal}>Отмена</Button>
            <Button
              variant="contained"
              onClick={sendMessage}
              disabled={!msgText.trim() || msgSending}
              endIcon={<SendIcon />}
              sx={{ textTransform: "none" }}
            >
              {msgSending ? "Отправка..." : "Отправить"}
            </Button>
          </DialogActions>
        </Dialog>

        {/* ----------- Модальное окно РЕДАКТИРОВАНИЕ ПРОФИЛЯ ----------- */}
        <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
          <DialogTitle>Редактирование профиля</DialogTitle>
          <DialogContent>
            <Stack spacing={2} mt={1}>
              <TextField label="ФИО" fullWidth value={form.name} onChange={e => handleFormChange("name", e.target.value)} />
              <TextField label="Email" type="email" fullWidth value={form.email} onChange={e => handleFormChange("email", e.target.value)} />
              <TextField label="Телефон" fullWidth value={form.phone} onChange={e => handleFormChange("phone", e.target.value)} />
              <TextField label="Дата рождения" type="date" fullWidth InputLabelProps={{ shrink: true }} value={form.dob} onChange={e => handleFormChange("dob", e.target.value)} />
              <Stack direction="row" spacing={2}>
                <TextField label="Город" fullWidth value={form.city} onChange={e => handleFormChange("city", e.target.value)} />
                <TextField label="Страна" fullWidth value={form.country} onChange={e => handleFormChange("country", e.target.value)} />
              </Stack>
              <Select label="Пол" fullWidth value={form.gender} onChange={e => handleFormChange("gender", e.target.value)}>
                <MenuItem value="Женский">Женский</MenuItem>
                <MenuItem value="Мужской">Мужской</MenuItem>
                <MenuItem value="Другое">Другое</MenuItem>
              </Select>
              <Select label="Тип клиента" fullWidth value={form.clientType} onChange={e => handleFormChange("clientType", e.target.value)}>
                <MenuItem value="Физ. лицо">Физ. лицо</MenuItem>
                <MenuItem value="Юр. лицо">Юр. лицо</MenuItem>
              </Select>
              {form.clientType === "Юр. лицо" && (
                <>
                  <TextField label="Компания" fullWidth value={form.company} onChange={e => handleFormChange("company", e.target.value)} />
                  <TextField label="ИНН" fullWidth value={form.inn} onChange={e => handleFormChange("inn", e.target.value)} />
                </>
              )}
              <TextField label="Адрес" fullWidth value={form.address} onChange={e => handleFormChange("address", e.target.value)} />
              <Select label="Язык интерфейса" fullWidth value={form.lang} onChange={e => handleFormChange("lang", e.target.value)}>
                <MenuItem value="Русский">Русский</MenuItem>
                <MenuItem value="English">English</MenuItem>
              </Select>
              <Stack direction="row" alignItems="center" spacing={1}>
                <Checkbox checked={form.marketing} onChange={e => handleFormChange("marketing", e.target.checked)} />
                <Typography>Согласие на рассылку</Typography>
              </Stack>
            </Stack>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEditClose}>Отмена</Button>
            <Button variant="contained" onClick={handleEditSave}>Сохранить</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
}
