import React, { useState } from "react";
import {
  Box, AppBar, Toolbar, Drawer, List, ListItem, ListItemIcon, ListItemText, Tabs, Tab, Stack, Typography, Avatar,
  Chip, IconButton, Tooltip, Divider, Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Select, MenuItem, Checkbox, FormControl, InputLabel
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";
import GiftCardIcon from "@mui/icons-material/CardGiftcard";
import LoyaltyIcon from "@mui/icons-material/Loyalty";

const drawerWidth = 215;

const TABS = [
  { label: "Профиль", icon: <PersonIcon /> },
  { label: "Покупки", icon: <ReceiptLongIcon /> },
  { label: "Бонусы", icon: <LoyaltyIcon /> },
  { label: "Интересы", icon: <EmojiEventsIcon /> },
  { label: "NPS", icon: <StarIcon /> },
  { label: "Сообщения", icon: <SmsIcon /> },
];

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваше бронирование подтверждено. Ждём вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

function TabPanel({ children, value, index }) {
  return (
    <div role="tabpanel" hidden={value !== index}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

