      </Drawer>

      <Box sx={{ flex: 1 }}>
        <AppBar elevation={0} position="static" color="inherit" sx={{ bgcolor: "#fff", borderBottom: "1px solid #e3e8ef" }}>
          <Toolbar>
            <Typography variant="h5" fontWeight={900} color="#26304a" sx={{ letterSpacing: 2 }}>Профиль клиента</Typography>
            <Box flex={1} />
            <Button startIcon={<SendIcon />} variant="contained" color="primary" onClick={openMsgModal} sx={{ borderRadius: 2, textTransform: "none", mr: 2 }}>
              Сообщение
            </Button>
            <Button startIcon={<EditIcon />} variant="outlined" color="primary" onClick={handleEdit} sx={{ borderRadius: 2, textTransform: "none" }}>
              Редактировать
            </Button>
          </Toolbar>
        </AppBar>

        <Box sx={{ maxWidth: 1200, mx: "auto", p: 4 }}>
          {/* Карточка профиля */}
          <Paper sx={{
            display: "flex", alignItems: "center",
            p: 3, mb: 3, borderRadius: 4, boxShadow: "0 6px 24px rgba(33,39,51,0.04)"
          }}>
            <Avatar sx={{
              width: 96, height: 96, fontSize: 44,
              bgcolor: "#E7ECF5", color: "#366AD7", mr: 3, border: "4px solid #fff"
            }}>
              {profile.name.split(" ").map(part => part[0]).join("").toUpperCase()}
            </Avatar>
            <Box>
              <Stack direction="row" spacing={1.5} alignItems="center" mb={1}>
                <Typography fontSize={30} fontWeight={800}>{profile.name}</Typography>
                {profile.tags.includes("VIP") && (
                  <Chip label="VIP" icon={<StarIcon sx={{ color: "#F5B82E" }} />} sx={{
                    bgcolor: "#FFF9E1", color: "#a77b06", fontWeight: 800, fontSize: 18, px: 2, border: "2px solid #FFE199"
                  }} size="medium" />
                )}
                {profile.tags.includes("Постоянный клиент") && (
                  <Chip label="Постоянный клиент" sx={{
                    bgcolor: "#E7F9F5", color: "#007A6B", fontWeight: 700, fontSize: 16, px: 2, ml: 0.5
                  }} size="medium" />
                )}
              </Stack>
              <Stack direction="row" spacing={1.5} alignItems="center" mb={1}>
                <Typography fontWeight={500} color="#57617a">ID: {profile.id}</Typography>
                <Typography fontWeight={500} color="#57617a">Возраст: {profile.age}</Typography>
                <Typography fontWeight={500} color="#57617a">{profile.city}, {profile.country}</Typography>
                <Chip icon={<ReceiptLongIcon />} label={profile.card.status} sx={{
                  bgcolor: "#E7F5E9", color: "#188035", fontWeight: 700, fontSize: 16, px: 2
                }} size="small" />
              </Stack>
              <Stack direction="row" spacing={1.5} alignItems="center">
                <EmailIcon sx={{ color: "#5880ea" }} />
                <Typography fontWeight={500}>{profile.email}</Typography>
                <SmsIcon sx={{ color: "#2ecf8f" }} />
                <Typography fontWeight={500}>{profile.phone}</Typography>
              </Stack>
            </Box>
