              <Typography variant="body2" color="text.secondary" mt={1}>LTV: {profile.ltv}</Typography>
            </Box>
          </Paper>

          {/* Вкладки */}
          <Tabs value={tab} onChange={(_, v) => setTab(v)} textColor="primary" indicatorColor="primary" sx={{ mb: 3 }}>
            {TABS.map((tabObj, i) =>
              <Tab key={tabObj.label} label={tabObj.label} icon={tabObj.icon} iconPosition="start" sx={{ fontWeight: 700, fontSize: 16, px: 3, minHeight: 56 }} />
