                    <Stack direction="row" alignItems="center" spacing={1}><SmsIcon /><b>Телефон:</b> {profile.phone}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Дата рождения:</b> {profile.dob.split("-").reverse().join(".")}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Пол:</b> {profile.gender}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Город:</b> {profile.city}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Адрес:</b> {profile.address}</Stack>
                  </Stack>
                </Paper>
              </Grid>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3, borderRadius: 3 }}>
                  <Typography variant="h6" fontWeight={700} mb={2}>Детали программы</Typography>
                  <Stack spacing={1.2}>
                    <Stack direction="row" alignItems="center" spacing={1}><LoyaltyIcon /><b>Тип клиента:</b> {profile.clientType}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><LoyaltyIcon /><b>Рефералов:</b> {profile.referrals}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><ReceiptLongIcon /><b>Карта:</b> {profile.card.number}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><LocalOfferIcon /><b>Статус карты:</b> {profile.card.status}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><GiftCardIcon /><b>Промокоды:</b> {profile.promoCodes.map(pc => (
                      <Chip key={pc.code} icon={<LocalOfferIcon />} label={pc.code + (pc.used ? " (исп.)" : "")} sx={{ ml: 1, bgcolor: pc.used ? "#e0e0e0" : "#fdf3db", color: "#f18f13", fontWeight: 600 }} size="small" />
                    ))}</Stack>
                    <Stack direction="row" alignItems="center" spacing={1}><GiftCardIcon /><b>Подарки:</b> {profile.gifts.map(gift => (
