import React, { useState } from "react";
import {
  Box, Grid, Paper, Avatar, Typography, Chip, Stack, Tooltip,
  IconButton, Tabs, Tab, Table, TableBody, TableCell, TableHead, TableRow,
  Dialog, DialogTitle, DialogContent, DialogActions, Button
} from "@mui/material";
import EmailIcon from "@mui/icons-material/Email";
import PhoneIcon from "@mui/icons-material/Sms";
import CakeIcon from "@mui/icons-material/Cake";
import PersonIcon from "@mui/icons-material/Person";
import HomeIcon from "@mui/icons-material/Home";
import AssignmentIndIcon from "@mui/icons-material/AssignmentInd";
import StarIcon from "@mui/icons-material/Star";
import EditIcon from "@mui/icons-material/Edit";
import SendIcon from "@mui/icons-material/Send";
import GroupAddIcon from "@mui/icons-material/GroupAdd";
import CreditCardIcon from "@mui/icons-material/CreditCard";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import WorkspacePremiumIcon from "@mui/icons-material/WorkspacePremium";
import ForumIcon from "@mui/icons-material/Forum";
import InfoOutlinedIcon from "@mui/icons-material/InfoOutlined";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import TelegramIcon from "@mui/icons-material/Telegram";
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";

const profile = {
  name: "Мария Летунова",
  id: "874392",
  age: 36,
  city: "Краснодар",
  country: "Россия",
  email: "maria.letunova@yandex.ru",
  phone: "+7 988 509-96-62",
  dob: "16.01.1989",
  gender: "Женский",
  address: "ул. Ленина, д. 10, кв. 24",
  marketing: true,
  lang: "Русский",
  clientType: "Физ. лицо",
  referrals: 8,
  cardNumber: "7712 3456 7890 0012",
  cardStatus: "Активна",
  ltv: "108 400 ₽",
  bonus: 1563,
  status: [
