import React from "react";
import {
  Box, Grid, Paper, Typography, Chip, Stack, Avatar,
  IconButton, Tooltip, Accordion, AccordionSummary, AccordionDetails, Menu, MenuItem
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import EditIcon from "@mui/icons-material/Edit";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";
import EmailIcon from "@mui/icons-material/Email";
import LocalPhoneIcon from "@mui/icons-material/LocalPhone";
import TimelineIcon from "@mui/icons-material/Timeline";
import SupportAgentIcon from "@mui/icons-material/SupportAgent";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import LoyaltyIcon from "@mui/icons-material/Loyalty";
import TagIcon from "@mui/icons-material/Tag";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";

export default function CustomerProfileCard() {
  // Меню секции
  const [anchorEl, setAnchorEl] = React.useState(null);
  const handleMenuOpen = (e) => setAnchorEl(e.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);

  // Интересы
  const [interestFilter, setInterestFilter] = React.useState("");
  const interests = [
    "Горные туры", "Отели 4–5*", "Зимний отдых", "Фитнес и SPA",
    "Детские активности", "SPA", "Экстрим", "Кулинария", "Семейный отдых"
  ];
  const filteredInterests = interestFilter
    ? interests.filter((i) => i.toLowerCase().includes(interestFilter.toLowerCase()))
    : interests;

  // Данные
  const tags = ["VIP", "Низкая активность", "Гость сайта", "Риск оттока"];
  const timeline = [
    { date: "10.06.2025", text: "Вход на сайт" },
    { date: "09.06.2025", text: "Запрос баланса" },
    { date: "08.06.2025", text: "Просмотр условий акции" },
    { date: "06.06.2025", text: "Отзыв в приложении" }
  ];
  const purchases = [
    { date: "30.05.2025", item: "Бронирование отеля", amount: "4 400 ₽" },
    { date: "03.03.2025", item: "Ски-пасс", amount: "25 266,7 ₽" },
    { date: "03.03.2025", item: "Бронирование отеля", amount: "12 615,83 ₽" },
    { date: "30.08.2024", item: "Сувенирный магазин", amount: "20 ₽" }
  ];
  const support = [
    { date: "02.06.2025", type: "Чат", text: "Проблема с бронированием" },
    { date: "25.05.2025", type: "Email", text: "Запрос на возврат" },
    { date: "12.05.2025", type: "Телефон", text: "Вопрос по акциям" }
  ];
  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление" },
    { date: "20.05.2025", value: "-375", type: "Списание" },
    { date: "10.04.2025", value: "+300", type: "Начисление" }
  ];

  return (
    <Box sx={{ bgcolor: "#f5f7fa", minHeight: "100vh", py: 4 }}>
      <Box sx={{
        maxWidth: 1200, mx: "auto", bgcolor: "#fff", borderRadius: 5,
        boxShadow: "0 8px 32px rgba(51,102,255,0.08)", p: { xs: 2, md: 5 }
      }}>
        {/* Верхний блок: имя, бейджи, кнопки */}
        <Stack direction={{ xs: "column", sm: "row" }} spacing={3} alignItems="center" mb={3}>
          <Avatar sx={{
            width: 80, height: 80, fontSize: 38, bgcolor: "primary.main", boxShadow: 3
          }}>МЛ</Avatar>
          <Box flex={1}>
            <Typography variant="h4" fontWeight={700} color="primary.main">
              Мария Летунова
            </Typography>
            <Typography color="text.secondary" mb={1}>
              <PersonIcon fontSize="small" sx={{ verticalAlign: "middle", mr: 0.5 }} />
              ID: 874392 &nbsp;|&nbsp; 36 лет &nbsp;|&nbsp; Краснодар
            </Typography>
            <Stack direction="row" spacing={1} mt={1} flexWrap="wrap">
              {tags.map((t, idx) => (
                <Chip key={idx} label={t}
                  color={t === "VIP" ? "success" : t === "Риск оттока" ? "error" : "primary"}
                  variant={t === "VIP" ? "filled" : "outlined"}
                />
              ))}
            </Stack>
          </Box>
          <Stack spacing={1} direction="row">
            <Tooltip title="Позвонить">
              <IconButton color="primary"><LocalPhoneIcon /></IconButton>
            </Tooltip>
            <Tooltip title="Email">
              <IconButton color="secondary"><EmailIcon /></IconButton>
            </Tooltip>
            <Tooltip title="WhatsApp">
              <IconButton sx={{ color: "#25D366" }}><WhatsAppIcon /></IconButton>
            </Tooltip>
            <Tooltip title="Редактировать">
              <IconButton color="primary"><EditIcon /></IconButton>
            </Tooltip>
            <Tooltip title="Меню секции">
              <IconButton onClick={handleMenuOpen}><MoreVertIcon /></IconButton>
            </Tooltip>
            <Menu open={!!anchorEl} anchorEl={anchorEl} onClose={handleMenuClose}>
              <MenuItem onClick={handleMenuClose}>Экспортировать</MenuItem>
              <MenuItem onClick={handleMenuClose}>Изменить статус</MenuItem>
              <MenuItem onClick={handleMenuClose}>Добавить тег</MenuItem>
            </Menu>
          </Stack>
        </Stack>

        <Grid container spacing={3}>
          {/* Контакты */}
          <Grid item xs={12} md={3}>
            <Accordion defaultExpanded sx={{ borderRadius: 3 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography fontWeight={600} color="primary">
                  Контакты
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Stack spacing={1}>
                  <Typography fontSize={15}>
                    <LocalPhoneIcon fontSize="small" sx={{ color: "text.secondary", mr: .5 }} />
                    <b>+7 988 509-96-62</b>
                  </Typography>
                  <Typography fontSize={15}>
                    <EmailIcon fontSize="small" sx={{ color: "text.secondary", mr: .5 }} />
                    <b>maria.letunova@yandex.ru</b>
                  </Typography>
                  <Typography fontSize={15}><b>Дата рождения:</b> 16.01.1989</Typography>
                  <Typography fontSize={15}><b>Город:</b> Краснодар</Typography>
                </Stack>
              </AccordionDetails>
            </Accordion>
          </Grid>
          {/* Бонусы и история бонусов */}
          <Grid item xs={12} md={3}>
            <Accordion defaultExpanded sx={{ borderRadius: 3 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography fontWeight={600} color="primary">
                  <LoyaltyIcon fontSize="small" sx={{ mr: .5 }} />
                  Бонусы
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Typography fontSize={15}><b>Баланс:</b> 1 563 бонуса</Typography>
                <Typography fontSize={15}><b>LTV:</b> 46 301,3 ₽</Typography>
                <Typography fontWeight={600} color="text.secondary" mt={2} mb={.5}>История бонусов:</Typography>
                <Stack spacing={.5}>
                  {bonusHistory.map((b, idx) => (
                    <Typography
                      key={idx}
                      fontSize={15}
                      color={b.type === "Начисление" ? "success.main" : "error.main"}
                    >
                      {b.value} бонусов — {b.date} ({b.type})
                    </Typography>
                  ))}
                </Stack>
              </AccordionDetails>
            </Accordion>
          </Grid>
          {/* Интересы */}
          <Grid item xs={12} md={3}>
            <Accordion defaultExpanded sx={{ borderRadius: 3 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography fontWeight={600} color="primary">
                  <TagIcon fontSize="small" sx={{ mr: .5 }} />
                  Интересы
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <input
                  style={{
                    width: "100%", padding: "6px 12px",
                    borderRadius: 6, border: "1px solid #eee", marginBottom: 8
                  }}
                  placeholder="Фильтр по интересам"
                  value={interestFilter}
                  onChange={e => setInterestFilter(e.target.value)}
                />
                <Stack direction="row" spacing={1} flexWrap="wrap">
                  {filteredInterests.length === 0 ? (
                    <Typography color="text.secondary">Совпадений не найдено</Typography>
                  ) : (
                    filteredInterests.map((i, idx) => (
                      <Chip key={idx} label={i} variant="outlined" color="primary" sx={{ mb: .5 }} />
                    ))
                  )}
                </Stack>
              </AccordionDetails>
            </Accordion>
          </Grid>
          {/* Теги */}
          <Grid item xs={12} md={3}>
            <Accordion defaultExpanded sx={{ borderRadius: 3 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography fontWeight={600} color="primary">
                  Сегменты и теги
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Stack direction="row" spacing={1} flexWrap="wrap">
                  {tags.map((tag, idx) => (
                    <Chip
                      key={idx}
                      label={tag}
                      color={tag === "VIP" ? "success" : tag === "Риск оттока" ? "error" : "primary"}
                      variant={tag === "VIP" ? "filled" : "outlined"}
                    />
                  ))}
                </Stack>
              </AccordionDetails>
            </Accordion>
          </Grid>
        </Grid>

        {/* История покупок и активность/обращения */}
        <Grid container spacing={3} mt={1}>
          {/* История покупок */}
          <Grid item xs={12} md={6}>
            <Accordion defaultExpanded sx={{ borderRadius: 3 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography fontWeight={600} color="primary">
                  <ShoppingCartIcon fontSize="small" sx={{ mr: .5 }} />
                  История покупок
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Stack spacing={.5}>
                  {purchases.map((p, idx) => (
                    <Typography key={idx} fontSize={15} color="text.secondary">
                      {p.date} — {p.item} — <b>{p.amount}</b>
                    </Typography>
                  ))}
                </Stack>
              </AccordionDetails>
            </Accordion>
          </Grid>
          {/* Лента активности */}
          <Grid item xs={12} md={3}>
            <Accordion defaultExpanded sx={{ borderRadius: 3 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography fontWeight={600} color="primary">
                  <TimelineIcon fontSize="small" sx={{ mr: .5 }} />
                  Лента активности
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Stack>
                  {timeline.map((event, idx) => (
                    <Stack key={idx} direction="row" alignItems="center" spacing={1} mb={.5}>
                      <Chip size="small" color="info" label={event.date} sx={{ minWidth: 86 }} />
                      <Typography fontSize={15}>{event.text}</Typography>
                    </Stack>
                  ))}
                </Stack>
              </AccordionDetails>
            </Accordion>
          </Grid>
          {/* Обращения */}
          <Grid item xs={12} md={3}>
            <Accordion defaultExpanded sx={{ borderRadius: 3 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography fontWeight={600} color="primary">
                  <SupportAgentIcon fontSize="small" sx={{ mr: .5 }} />
                  Обращения
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Stack spacing={.5}>
                  {support.map((s, idx) => (
                    <Typography key={idx} fontSize={15} color="text.secondary">
                      {s.date} — {s.type}: {s.text}
                    </Typography>
                  ))}
                </Stack>
              </AccordionDetails>
            </Accordion>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
}
