sourcesContent:
import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField,
  Select, MenuItem, Checkbox, FormControl, InputLabel, Badge
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import EventIcon from "@mui/icons-material/Event";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import WarningAmberIcon from "@mui/icons-material/WarningAmber";
import TrendingUpIcon from "@mui/icons-material/TrendingUp";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip as ChartTooltip, ResponsiveContainer } from "recharts";

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваша бронирование подтверждено. Ждем вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

function TabPanel({ children, value, index, ...other }) {
  return (
    <div role="tabpanel" hidden={value !== index} {...other}>
      {value === index && <Box sx={{ pt: 2 }}>{children}</Box>}
    </div>
  );
}

function SegmentTag({ label, color = "default", icon }) {
  return (
    <Chip
      icon={icon}
      label={label}
      sx={{
        bgcolor:
          color === "gold"
            ? "#FFD700"
            : color === "danger"
            ? "#FFE6E6"
            : color === "vip"
            ? "#4e73df"
            : "#f3f6fc",
        color:
          color === "gold"
            ? "#2a2a2a"
            : color === "danger"
            ? "#C62828"
            : color === "vip"
            ? "#fff"
            : "#334",
        fontWeight: 600,
        borderRadius: 2,
        mr: .5,
        mb: .5,
        fontSize: 15,
        px: 1.5,
      }}
      size="medium"
    />
  );
}

export default function CustomerProfileCard() {
  // --- PROFILE DATA
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    city: "Краснодар",
    country: "Россия",
    gender: "Женский",
    clientType: "Физ. лицо",
    company: "",
    inn: "",
    address: "ул. Ленина, д. 10, кв. 24",
    marketing: true,
    lang: "Русский",
    age: 36,
    tags: ["VIP", "Часто покупает", "День рождения сегодня"],
    ltv: "158 400 ₽",
    bonus: "2 763",
    risk: true,
    interests: [
      "Горные туры", "Зимний отдых", "SPA и релакс", "Детские активности", "Экстрим", "Кулинария", "Корпоративные мероприятия"
    ]
  });

  // --- СЕГМЕНТАЦИЯ
  const segments = [
    { label: "VIP", color: "vip", icon: <StarIcon /> },
    { label: "Часто покупает", color: "gold", icon: <TrendingUpIcon /> },
    { label: "По интересам: Экстрим", color: "default", icon: <EventIcon /> },
    { label: "Риск ухода", color: "danger", icon: <WarningAmberIcon /> },
    { label: "День рождения сегодня", color: "gold", icon: <EventIcon /> },
  ];

  // --- ПОКУПКИ
  const purchases = [
    {
      date: "16.06.2025",
      item: "Ски-пасс + Бар",
      amount: "19 866 ₽",
      status: "Оплачено",
      pos: "Касса Горки Молл",
      receiptId: "CHK-1045",
      employee: "Иван Калашников",
      receipt: [
        { name: "Ски-пасс взрослый", qty: 2, price: 12000, writtenOff: true, writtenOffBonuses: 10000, accrual: 1500 },
        { name: "Ски-пасс детский", qty: 1, price: 5266, writtenOff: false, accrual: 500 },
        { name: "Пицца бар", qty: 3, price: 800, writtenOff: false, accrual: 120 },
        { name: "Лимонад", qty: 2, price: 300, writtenOff: true, writtenOffBonuses: 600, accrual: 40 }
      ]
    },
    {
      date: "04.06.2025",
      item: "Бронирование отеля",
      amount: "7 800 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 7800, writtenOff: false, accrual: 700 }
      ]
    },
    {
      date: "12.04.2025",
      item: "Тренировка в фитнес-зале",
      amount: "0 ₽",
      status: "Оплачено (списание бонусов)",
      pos: "Фитнес-клуб Центр",
      receiptId: "CHK-1022",
      employee: "Максим Шестаков",
      receipt: [
        { name: "Тренировка фитнес", qty: 1, price: 700, writtenOff: true, writtenOffBonuses: 700, accrual: 0 }
      ]
    }
  ];

  // --- БОНУСЫ
  const bonusHistory = [
    { date: "16.06.2025", value: "+2120", type: "Начисление", campaign: "Июнь: на всё 10%" },
    { date: "16.06.2025", value: "-10600", type: "Списание", campaign: "Оплата заказа CHK-1045" },
    { date: "04.06.2025", value: "+700", type: "Начисление", campaign: "Бронирование" },
    { date: "12.04.2025", value: "-700", type: "Списание", campaign: "Фитнес по бонусам" }
  ];

  // --- ПРОМОКОДЫ/ПОДАРКИ
  const promoCodes = [
    { code: "VIP2025", discount: "15%", used: false, activated: "14.06.2025" },
    { code: "SKI2024", discount: "20%", used: true, activated: "15.04.2025" }
  ];
  const gifts = [
    { date: "2024-01-15", name: "Подарочная карта 1 000 ₽", status: "Активна" },
    { date: "2023-11-22", name: "Фирменный термос", status: "Получено" }
  ];

  // --- NPS анкеты
  const npsSurveys = [
    {
      date: "01.06.2025",
      channel: "Email",
      score: 10,
      comment: "Всё понравилось! Спасибо сотруднику Екатерине.",
      order: "CHK-1001"
    },
    {
      date: "15.04.2025",
      channel: "SMS",
      score: 6,
      comment: "Была небольшая задержка на ресепшене.",
      order: "CHK-1022"
    },
    {
      date: "01.09.2024",
      channel: "Telegram",
      score: 9,
      comment: "Очень вкусно и быстро обслужили на банкете.",
      order: "CHK-6789"
    }
  ];

  // --- История сообщений
  const [messageHistory, setMessageHistory] = useState([
    { date: "16.06.2025 13:21", channel: "SMS", direction: "Исходящее", type: "Ручная рассылка", campaign: "Акция Лето", text: "Спасибо за заказ! Ваши бонусы начислены." },
    { date: "04.06.2025 10:01", channel: "Telegram", direction: "Исходящее", type: "Ручная рассылка", campaign: "Лояльность", text: "Скидка 10% активирована." },
    { date: "01.06.2025 09:50", channel: "SMS", direction: "Исходящее", type: "Автомат. уведомление", campaign: "NPS", text: "Пожалуйста, оцените сервис!" }
  ]);

  // --- Активности
  const activities = [
    { date: "16.06.2025", type: "Открытие письма", desc: "Рассылка Акция Лето", icon: <EmailIcon color="primary" /> },
    { date: "15.06.2025", type: "Переход по ссылке", desc: "Промо NPS", icon: <SmsIcon color="primary" /> },
    { date: "14.06.2025", type: "Клик на баннер", desc: "Главная страница", icon: <TrendingUpIcon color="success" /> }
  ];

  // --- Задачи
  const tasks = [
    { due: "18.06.2025", text: "Перезвонить по новому заказу", status: "Открыта" },
    { due: "20.06.2025", text: "Отправить подарок", status: "Выполнено" }
  ];

  // --- Графики (например, покупки/бонусы)
  const purchaseChart = [
    { date: "01.06", value: 7000 },
    { date: "03.06", value: 8200 },
    { date: "04.06", value: 7800 },
    { date: "10.06", value: 9000 },
    { date: "16.06", value: 19866 }
  ];
  const bonusChart = [
    { date: "01.06", value: 350 },
    { date: "03.06", value: 410 },
    { date: "04.06", value: 700 },
    { date: "10.06", value: 980 },
    { date: "16.06", value: 2120 }
  ];

  // --- Модалки
  const [tab, setTab] = useState(0);
  const [editOpen, setEditOpen] = useState(false);
  const [form, setForm] = useState(profile);
  const [receiptModal, setReceiptModal] = useState({ open: false, purchase: null });
  const [msgModal, setMsgModal] = useState(false);
  const [msgChannel, setMsgChannel] = useState("SMS");
  const [msgText, setMsgText] = useState("");
  const [msgSending, setMsgSending] = useState(false);

  // Статистика
  const paidPurchases = purchases.filter(p => p.status.includes("Оплачено"));
  const totalPurchases = paidPurchases.length;
  const avgCheck = totalPurchases === 0 ? 0 :
    Math.round(
      paidPurchases
        .map(p => Number(p.amount.replace(/[ ₽,]/g, "").replace(",", ".")))
        .reduce((a, b) => a + b, 0) / totalPurchases
    );
  const lastPurchaseDate = paidPurchases.length
    ? paidPurchases
        .map(p => p.date)
        .sort((a, b) => new Date(b.split(".").reverse().join("-")) - new Date(a.split(".").reverse().join("-")))[0]
    : "-";

  // --------- Handlers
  const openReceipt = (purchase) => setReceiptModal({ open: true, purchase });
  const closeReceipt = () => setReceiptModal({ open: false, purchase: null });
  const handleEdit = () => { setForm(profile); setEditOpen(true); };
  const handleEditClose = () => setEditOpen(false);
  const handleEditSave = () => {
    setProfile({
      ...form,
      age: form.dob ? (new Date().getFullYear() - Number(form.dob.slice(0, 4))) : profile.age
    });
    setEditOpen(false);
  };
  const handleFormChange = (field, value) => setForm((prev) => ({ ...prev, [field]: value }));
