import React, { useState } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, IconButton, Tooltip, Divider,
  Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField,
  Select, MenuItem, Checkbox, FormControl, InputLabel
} from "@mui/material";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import BusinessIcon from "@mui/icons-material/Business";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import PhoneIcon from "@mui/icons-material/Phone";
import StarIcon from "@mui/icons-material/Star";
import InstagramIcon from "@mui/icons-material/Instagram";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";
import VpnKeyIcon from "@mui/icons-material/VpnKey";
import WarningAmberIcon from "@mui/icons-material/WarningAmber";
import AssignmentIndIcon from "@mui/icons-material/AssignmentInd";
import BadgeIcon from "@mui/icons-material/Badge";
import GroupIcon from "@mui/icons-material/Group";
import DiamondIcon from "@mui/icons-material/Diamond";
import CelebrationIcon from "@mui/icons-material/Celebration";
import MarkEmailReadIcon from "@mui/icons-material/MarkEmailRead";
import StarOutlineIcon from "@mui/icons-material/StarOutline";
import EditIcon from "@mui/icons-material/Edit";

// Emoji или кастом для VK
const VkIcon = () => <span style={{ fontWeight: 700, fontSize: 20, color: "#4C75A3" }}>VK</span>;

// DEMO COPY FUNCTION
function copy(value) {
  navigator.clipboard?.writeText(value);
}

// =========== DEMO DATA =========== //
const profileInitial = {
  name: "Мария Летунова",
  id: "874392",
  status: "VIP",
  registrationSource: "Сайт",
  registrationDate: "2022-03-10",
  lastActivity: "2025-06-16 17:23",
  preferredChannel: "Telegram",
  email: "maria.letunova@yandex.ru",
  phone: "+7 988 509-96-62",
  phones: ["+7 918 123-45-67"],
  messengers: [
    { type: "WhatsApp", value: "+7 988 509-96-62", link: "https://wa.me/79885099662" },
    { type: "Telegram", value: "@letunova_maria", link: "https://t.me/letunova_maria" }
  ],
  socials: [
    { type: "VK", link: "https://vk.com/letunova" },
    { type: "Instagram", link: "https://instagram.com/letunova" }
  ],
  dob: "1989-01-16",
  city: "Краснодар",
  country: "Россия",
  gender: "Женский",
  clientType: "Юр. лицо",
  company: "ООО Курорт Сочи",
  inn: "2312011220",
  ogrn: "1192312002123",
  position: "Директор по развитию",
  role: "ЛПР",
  address: "ул. Ленина, д. 10, кв. 24",
  marketing: true,
  lang: "Русский",
  tags: ["VIP", "Часто покупает", "Сочи", "День рождения сегодня"],
  segments: [
    { type: "RFM", label: "Топ-10%", color: "#1976d2" },
    { type: "Интересы", label: "Семейный отдых", color: "#43a047" },
    { type: "Гео", label: "Краснодар", color: "#ffa000" }
  ],
  ltv: "153 800 ₽",
  bonus: "1 563",
  favoriteProducts: ["Горные туры", "Ски-пассы", "Фирменные сувениры"],
  consents: [
    { type: "Реклама", date: "2023-05-12", value: true },
    { type: "Персональные данные", date: "2021-05-17", value: true },
    { type: "Push", date: "2024-02-01", value: false }
  ],
  security: {
    lastLogin: "2025-06-16 17:23",
    lastPasswordChange: "2024-10-21",
    suspicious: true,
    notes: ["Вход с нового устройства 16.06.2025"]
  },
  antifraud: {
    suspiciousRefunds: true,
    suspiciousBonuses: false,
    notes: ["Много возвратов за 3 месяца"]
  }
};

const purchases = [
  {
    date: "30.05.2025",
    id: "CHK-1001",
    items: [
      { name: "Бронирование отеля", qty: 1, price: 4400, bonusEarned: 400, bonusSpent: 0 },
      { name: "Ски-пасс взрослый", qty: 2, price: 12000, bonusEarned: 1200, bonusSpent: 1000 }
    ],
    pos: "Офис Красная Поляна",
    employee: "Екатерина Миронова",
    amount: "28 400 ₽",
    status: "Оплачено"
  },
  {
    date: "12.04.2025",
    id: "CHK-1022",
    items: [
      { name: "Тренировка в фитнес-зале", qty: 1, price: 700, bonusEarned: 0, bonusSpent: 700 },
      { name: "SPA-процедура", qty: 1, price: 3500, bonusEarned: 100, bonusSpent: 0 }
    ],
    pos: "Фитнес-клуб Центр",
    employee: "Максим Шестаков",
    amount: "4 200 ₽",
    status: "Оплачено (списание бонусов)"
  }
];

const bonusHistory = [
  { date: "30.05.2025", value: "+1512", type: "Начисление", campaign: "Акция «Вернем 30%»" },
