function ProfileTable({ profile }) {
  // Можно вынести блоками, но тут — кратко, всё в Bitrix24‑стиле
  return (
    <Table sx={{ maxWidth: 520 }}>
      <TableBody>
        <TableRow>
          <TableCell sx={{ fontWeight: 600, width: 140 }}>ФИО</TableCell>
          <TableCell>{profile.name}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>ID / Статус</TableCell>
          <TableCell>
            <Stack direction="row" spacing={1} alignItems="center">
              <Chip
                label={profile.id}
                color="primary"
                size="small"
                icon={<AssignmentIndIcon fontSize="small" />}
                onClick={() => copy(profile.id)}
                sx={{ cursor: "pointer" }}
              />
              <Chip
                label={profile.status}
                color={profile.status === "VIP" ? "warning" : "default"}
                size="small"
                icon={<DiamondIcon fontSize="small" />}
              />
              {profile.tags.map((tag, i) =>
                <Chip
                  key={i}
                  label={tag}
                  size="small"
                  sx={{
                    bgcolor:
                      tag === "День рождения сегодня"
                        ? "#43a047"
                        : tag === "VIP"
                          ? "#ffd700"
                          : "#e3f2fd",
                    color: tag === "VIP" ? "#333" : "#222"
                  }}
                  icon={tag === "День рождения сегодня" ? <CelebrationIcon sx={{ color: "#fff" }} /> : undefined}
                />
              )}
            </Stack>
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Компания / роль</TableCell>
          <TableCell>
            {profile.company} (ИНН: {profile.inn}, ОГРН: {profile.ogrn})<br />
            {profile.position && <span style={{ color: "#6c6c6c" }}>{profile.position} / {profile.role}</span>}
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Email</TableCell>
          <TableCell>
            <Chip icon={<EmailIcon />} label={profile.email} />
            <IconButton size="small" onClick={() => copy(profile.email)}><ContentCopyIcon fontSize="small" /></IconButton>
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Телефоны / мессенджеры</TableCell>
          <TableCell>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              <Chip icon={<PhoneIcon />} label={profile.phone} />
              {profile.phones.map((p, i) => <Chip key={i} icon={<PhoneIcon />} label={p} />)}
              {profile.messengers.map((msg, i) => (
                <Chip
                  key={i}
                  icon={msg.type === "Telegram" ? <TelegramIcon /> : <WhatsAppIcon />}
                  label={msg.value}
                  component="a"
                  href={msg.link}
                  clickable
                  sx={{ bgcolor: msg.type === "Telegram" ? "#E5F5FD" : "#E7FEE7" }}
                />
              ))}
              {profile.socials.map((soc, i) =>
                <IconButton key={i} component="a" href={soc.link} target="_blank" size="small">
                  {soc.type === "VK" ? <VkIcon /> : soc.type === "Instagram" ? <InstagramIcon sx={{ color: "#e1306c" }} /> : null}
                </IconButton>
              )}
            </Stack>
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Предпочтительный канал</TableCell>
          <TableCell>
            <Chip
              label={profile.preferredChannel}
              icon={
                profile.preferredChannel === "Email"
                  ? <EmailIcon />
                  : profile.preferredChannel === "Telegram"
                    ? <TelegramIcon />
                    : <SmsIcon />
              }
              sx={{ bgcolor: "#e8f5e9" }}
            />
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Адрес / город</TableCell>
          <TableCell>{profile.address}<br />{profile.city}, {profile.country}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Любимые продукты</TableCell>
          <TableCell>
            {profile.favoriteProducts.map((f, i) => <Chip key={i} label={f} sx={{ m: .2, bgcolor: "#f3e5f5" }} />)}
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Сегменты</TableCell>
          <TableCell>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.segments.map((seg, i) => (
                <Chip
                  key={i}
                  label={seg.label}
                  size="small"
                  sx={{ bgcolor: seg.color, color: "#fff", fontWeight: 700 }}
                  icon={<GroupIcon />}
                />
              ))}
            </Stack>
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Дата рождения / пол</TableCell>
          <TableCell>
            {profile.dob.split("-").reverse().join(".")} / {profile.gender}
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Источник / дата регистрации</TableCell>
          <TableCell>
            {profile.registrationSource}, {profile.registrationDate}
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Дата последней активности</TableCell>
          <TableCell>{profile.lastActivity}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Язык / рассылки</TableCell>
          <TableCell>
            {profile.lang} <span style={{ color: "#888" }}>{profile.marketing ? " (разрешены рассылки)" : " (отписан)"}</span>
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Безопасность</TableCell>
          <TableCell>
            <Chip icon={<VpnKeyIcon />} label={`Последний вход: ${profile.security.lastLogin}`} size="small" />
            <Chip icon={<StarOutlineIcon />} label={`Пароль: ${profile.security.lastPasswordChange}`} size="small" />
            {profile.security.suspicious && <Chip icon={<WarningAmberIcon />} label="Подозрительная активность" color="warning" />}
            {profile.security.notes.map((n, i) => <span key={i} style={{ color: "#b71c1c", fontSize: 12, marginLeft: 8 }}>{n}</span>)}
          </TableCell>
        </TableRow>
        <TableRow>
          <TableCell sx={{ fontWeight: 600 }}>Антифрод</TableCell>
          <TableCell>
            {profile.antifraud.suspiciousRefunds && <Chip color="error" label="Много возвратов" size="small" />}
            {profile.antifraud.suspiciousBonuses && <Chip color="error" label="Сомнительные бонусы" size="small" />}
            {profile.antifraud.notes.map((n, i) => <span key={i} style={{ color: "#b71c1c", fontSize: 12, marginLeft: 8 }}>{n}</span>)}
            {!profile.antifraud.suspiciousRefunds && !profile.antifraud.suspiciousBonuses && !profile.antifraud.notes.length && "—"}
          </TableCell>
        </TableRow>
      </TableBody>
    </Table>
  );
}

