export default function CustomerProfileCard() {
  const [tab, setTab] = useState(0);
  const [profile] = useState(profileInitial);
  const [receiptModal, setReceiptModal] = useState({ open: false, items: [], info: null });

  // Вкладки: Профиль, Покупки, Бонусы, NPS, Сообщения
  return (
    <Box sx={{ bgcolor: "#F4F6FA", minHeight: "100vh", py: 5, px: 1 }}>
      <Box sx={{
        maxWidth: 1200,
        mx: "auto",
        bgcolor: "#fff",
        borderRadius: 4,
        boxShadow: "0 4px 24px rgba(20,25,55,0.09)",
        px: { xs: 2, sm: 5 },
        py: { xs: 3, sm: 5 }
      }}>
        {/* Bitrix24 header style */}
        <Stack direction={{ xs: "column", md: "row" }} alignItems={{ md: "center" }} spacing={2} mb={3}>
          <Avatar
            sx={{
              width: 96, height: 96,
              fontSize: 36,
              bgcolor: "#EFF2FB",
              color: "#1976d2",
              border: "3px solid #ECF1F9"
            }}
          >
            {profile.name.split(" ").map(w => w[0]).join("").toUpperCase()}
          </Avatar>
          <Box flex={1}>
            <Typography variant="h4" fontWeight={700} color="#22334d">
              {profile.name}
              {profile.status === "VIP" && <DiamondIcon fontSize="inherit" sx={{ color: "#FFD700", ml: 1, mb: -.3 }} />}
            </Typography>
            <Typography color="text.secondary" sx={{ fontSize: 17, mt: .5, mb: 1 }}>
              <AssignmentIndIcon fontSize="small" sx={{ verticalAlign: "middle", mr: .5 }} />
              ID: {profile.id} &nbsp; | &nbsp; {profile.city}, {profile.country}
            </Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.tags.map((t, idx) => (
                <Chip
                  key={idx}
                  label={t}
                  size="small"
                  sx={{
                    bgcolor:
                      t === "День рождения сегодня"
                        ? "#43a047"
                        : t === "VIP"
                          ? "#ffd700"
                          : "#e3f2fd",
                    color: t === "VIP" ? "#333" : "#222"
                  }}
                  icon={t === "День рождения сегодня" ? <CelebrationIcon sx={{ color: "#fff" }} /> : undefined}
                />
              ))}
            </Stack>
          </Box>
          <Stack direction="row" spacing={2}>
            <Button variant="outlined" color="primary" startIcon={<MarkEmailReadIcon />}>Отправить сообщение</Button>
            <Button variant="outlined" color="secondary" startIcon={<EditIcon />}>Редактировать</Button>
          </Stack>
        </Stack>

        <Divider sx={{ mb: 2 }} />

        {/* Tabs */}
        <Tabs
          value={tab}
          onChange={(_, v) => setTab(v)}
          aria-label="customer tabs"
          textColor="primary"
          indicatorColor="primary"
          variant="scrollable"
          allowScrollButtonsMobile
          sx={{ mb: 2, borderBottom: "1px solid #e3e3e3" }}
        >
          <Tab label="Профиль" />
          <Tab label="Покупки" />
          <Tab label="Бонусы" />
          <Tab label="NPS" />
          <Tab label="Сообщения" />
        </Tabs>

        {/* Профиль */}
        {tab === 0 && (
          <Box sx={{ py: 2 }}>
            <ProfileTable profile={profile} />
          </Box>
        )}

        {/* Покупки */}
        {tab === 1 && (
          <Box sx={{ py: 2 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Точка продаж</TableCell>
                  <TableCell>Сотрудник</TableCell>
                  <TableCell>Чек</TableCell>
                  <TableCell>Сумма</TableCell>
                  <TableCell>Статус</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {purchases.map((p, i) => (
                  <TableRow key={i}>
                    <TableCell>{p.date}</TableCell>
                    <TableCell>{p.pos}</TableCell>
                    <TableCell>{p.employee}</TableCell>
                    <TableCell>
                      <Button
                        variant="text"
                        size="small"
                        onClick={() => setReceiptModal({ open: true, items: p.items, info: p })}
                        endIcon={<ReceiptLongIcon />}
                      >
                        {p.id}
                      </Button>
                    </TableCell>
                    <TableCell>{p.amount}</TableCell>
                    <TableCell>{p.status}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Box>
        )}

        {/* Бонусы */}
        {tab === 2 && (
          <Box sx={{ py: 2 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Тип операции</TableCell>
                  <TableCell>Кампания</TableCell>
                  <TableCell>Сумма</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {bonusHistory.map((b, i) => (
                  <TableRow key={i}>
                    <TableCell>{b.date}</TableCell>
                    <TableCell>
                      {b.type === "Начисление"
                        ? <Chip label="Начисление" color="success" size="small" />
                        : <Chip label="Списание" color="warning" size="small" />}
                    </TableCell>
                    <TableCell>{b.campaign}</TableCell>
                    <TableCell>
                      <Typography sx={{ fontWeight: 700, color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c" }}>{b.value}</Typography>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Box>
        )}

        {/* NPS */}
        {tab === 3 && (
          <Box sx={{ py: 2 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Канал</TableCell>
                  <TableCell>Оценка (0-10)</TableCell>
                  <TableCell>Комментарий</TableCell>
                  <TableCell>Заказ</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {npsSurveys.map((n, i) => (
                  <TableRow key={i}>
                    <TableCell>{n.date}</TableCell>
                    <TableCell>
                      <Chip label={n.channel} size="small" color="primary" />
                    </TableCell>
                    <TableCell>
                      <Stack direction="row" alignItems="center" spacing={0.5}>
                        <StarIcon fontSize="small" sx={{ color: n.score > 8 ? "#219653" : n.score > 6 ? "#FFB300" : "#C62828" }} />
                        <Typography fontWeight={700}>{n.score}</Typography>
                      </Stack>
                    </TableCell>
                    <TableCell>{n.comment}</TableCell>
                    <TableCell>
                      <Button
                        size="small"
                        variant="text"
                        onClick={() => setReceiptModal({ open: true, items: purchases.find(x => x.id === n.order)?.items || [], info: purchases.find(x => x.id === n.order) })}
                        endIcon={<ReceiptLongIcon />}
                      >{n.order}</Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Box>
        )}

        {/* Сообщения */}
        {tab === 4 && (
          <Box sx={{ py: 2 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Канал</TableCell>
                  <TableCell>Способ</TableCell>
                  <TableCell>Кампания</TableCell>
                  <TableCell>Текст</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {messages.map((m, i) => (
                  <TableRow key={i}>
                    <TableCell>{m.date}</TableCell>
                    <TableCell>
                      {m.channel === "SMS"
                        ? <Chip icon={<SmsIcon fontSize="small" />} label="SMS" size="small" />
                        : <Chip icon={<TelegramIcon fontSize="small" />} label="Telegram" size="small" color="primary" />}
                    </TableCell>
                    <TableCell>
                      {m.sendType}
                    </TableCell>
                    <TableCell>
                      {m.campaign}
                    </TableCell>
                    <TableCell>{m.text}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Box>
        )}

        {/* ============= МОДАЛЬНОЕ ОКНО ЧЕКА ============= */}
        <Dialog
          open={receiptModal.open}
          onClose={() => setReceiptModal({ open: false, items: [], info: null })}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle>
            Чек {receiptModal.info?.id} — {receiptModal.info?.date}
          </DialogTitle>
          <DialogContent>
            <Typography sx={{ mb: 1.5, fontSize: 16 }}>
              <b>Точка продаж:</b> {receiptModal.info?.pos}<br />
              <b>Сотрудник:</b> {receiptModal.info?.employee}
            </Typography>
            <Table size="medium">
              <TableHead>
                <TableRow>
                  <TableCell>Товар</TableCell>
                  <TableCell>Кол-во</TableCell>
                  <TableCell>Цена</TableCell>
                  <TableCell>Сумма</TableCell>
                  <TableCell>Начислено</TableCell>
                  <TableCell>Списано</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {receiptModal.items.map((row, i) => (
                  <TableRow key={i}>
                    <TableCell>{row.name}</TableCell>
                    <TableCell>{row.qty}</TableCell>
                    <TableCell>{row.price.toLocaleString()} ₽</TableCell>
                    <TableCell>{(row.qty * row.price).toLocaleString()} ₽</TableCell>
                    <TableCell>
                      {row.bonusEarned > 0 && (
                        <Chip
                          label={`+${row.bonusEarned} бонусов`}
                          color="success"
                          size="small"
                          sx={{ fontWeight: 700 }}
                        />
                      )}
                    </TableCell>
                    <TableCell>
                      {row.bonusSpent > 0 && (
                        <Chip
                          label={`-${row.bonusSpent} бонусов`}
                          color="warning"
                          size="small"
                          sx={{ fontWeight: 700 }}
                        />
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setReceiptModal({ open: false, items: [], info: null })} variant="outlined">Закрыть</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
