import React, { useState } from "react";
import {
  Box, AppBar, Toolbar, Tabs, Tab, Stack, Typography, Avatar,
  Chip, IconButton, Tooltip, Divider, Table, TableBody, TableRow, TableCell, TableHead, Grid, Paper,
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Select, MenuItem, Checkbox, FormControl, InputLabel
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";
import GiftCardIcon from "@mui/icons-material/CardGiftcard";
import LoyaltyIcon from "@mui/icons-material/Loyalty";

const TABS = [
  { label: "Профиль", icon: <PersonIcon /> },
  { label: "Покупки", icon: <ReceiptLongIcon /> },
  { label: "Бонусы", icon: <LoyaltyIcon /> },
  { label: "Интересы", icon: <EmojiEventsIcon /> },
  { label: "NPS", icon: <StarIcon /> },
  { label: "Сообщения", icon: <SmsIcon /> },
];

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваше бронирование подтверждено. Ждём вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

function TabPanel({ children, value, index }) {
  return (
    <div role="tabpanel" hidden={value !== index}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

export default function CustomerProfileCard() {
  // ——— State
  const [tab, setTab] = useState(0);

  // ——— Test profile
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    city: "Краснодар",
    country: "Россия",
    gender: "Женский",
    clientType: "Физ. лицо",
    address: "ул. Ленина, д. 10, кв. 24",
    lang: "Русский",
    age: 36,
    tags: ["VIP", "Постоянный клиент", "Гость сайта"],
    bonus: "1 563",
    ltv: "108 400 ₽",
    referrals: 8,
    card: {
      status: "Активна",
      number: "7712 3456 7890 0012"
    },
    marketing: true,
    promoCodes: [
      { code: "VIP2025", discount: "15%", used: false },
      { code: "SKI2024", discount: "20%", used: true }
    ],
    gifts: [
      { date: "2024-01-15", name: "Подарочная карта 1 000 ₽", status: "Активна" },
      { date: "2023-11-22", name: "Фирменный термос", status: "Получено" }
    ],
    interests: [
      "Горные туры", "Зимний отдых", "SPA и релакс", "Детские активности", "Экстрим", "Кулинария", "Корпоративные мероприятия"
    ]
  });

  // Покупки с составом чека
  const purchases = [
    {
      date: "30.05.2025",
      item: "Бронирование отеля",
      amount: "4 400 ₽",
      status: "Оплачено",
      pos: "Офис Красная Поляна",
      receiptId: "CHK-1001",
      employee: "Екатерина Миронова",
      receipt: [
        { name: "Бронирование отеля", qty: 1, price: 4400, writtenOff: false }
      ]
    },
    {
      date: "12.04.2025",
      item: "Тренировка в фитнес-зале",
      amount: "0 ₽",
      status: "Оплачено (бонусы)",
      pos: "Фитнес-клуб Центр",
      receiptId: "CHK-1022",
      employee: "Максим Шестаков",
      receipt: [
        { name: "Тренировка фитнес", qty: 1, price: 700, writtenOff: true, writtenOffBonuses: 700 }
      ]
    },
    {
      date: "03.03.2025",
      item: "Ски-пасс",
      amount: "15 266,7 ₽",
      status: "Оплачено (частично бонусами)",
      pos: "Касса Горки Молл",
      receiptId: "CHK-9823",
      employee: "Иван Калашников",
      receipt: [
        { name: "Ски-пасс взрослый", qty: 2, price: 6000, writtenOff: true, writtenOffBonuses: 10000 },
        { name: "Ски-пасс детский", qty: 1, price: 5266.7, writtenOff: false }
      ]
    },
    {
      date: "24.01.2025",
      item: "Сувениры",
      amount: "4 100 ₽",
      status: "Оплачено",
      pos: "Магазин GIFT",
      receiptId: "CHK-9827",
      employee: "Оксана Фролова",
      receipt: [
        { name: "Магнитик", qty: 3, price: 250, writtenOff: false },
        { name: "Кружка фирменная", qty: 2, price: 750, writtenOff: false },
        { name: "Рюкзак", qty: 1, price: 1800, writtenOff: false }
      ]
    }
  ];

  // Бонусы
  const bonusHistory = [
    { date: "30.05.2025", value: "+1512.6", type: "Начисление", campaign: "Вернем 30%" },
    { date: "20.05.2025", value: "-375", type: "Списание", campaign: "Оплата заказа #CHK-8431" },
    { date: "13.04.2025", value: "+300", type: "Начисление", campaign: "Лояльность" },
    { date: "12.04.2025", value: "-700", type: "Списание", campaign: "Фитнес по бонусам" }
  ];

  // NPS анкеты
  const npsSurveys = [
    { date: "01.06.2025", channel: "Email", score: 10, comment: "Всё понравилось! Спасибо сотруднику Екатерине.", order: "CHK-1001" },
    { date: "15.04.2025", channel: "SMS", score: 6, comment: "Была небольшая задержка на ресепшене.", order: "CHK-1022" },
    { date: "01.09.2024", channel: "Telegram", score: 9, comment: "Очень вкусно и быстро обслужили на банкете.", order: "CHK-6789" }
  ];

  // Сообщения
  const [messageHistory, setMessageHistory] = useState([
    { date: "10.06.2025 10:23", channel: "SMS", direction: "Исходящее", text: "Спасибо за ваш отзыв!" },
    { date: "01.06.2025 09:50", channel: "Telegram", direction: "Исходящее", text: "Добрый день! Ваша скидка активирована." }
  ]);

  // ——— Модалки
  const [editOpen, setEditOpen] = useState(false);
  const [form, setForm] = useState(profile);
  const [msgModal, setMsgModal] = useState(false);
  const [msgChannel, setMsgChannel] = useState("SMS");
  const [msgText, setMsgText] = useState("");
  const [msgSending, setMsgSending] = useState(false);
  const [receiptModal, setReceiptModal] = useState({ open: false, receipt: null, meta: null });

  // ——— Методы
  const handleEdit = () => { setForm(profile); setEditOpen(true); };
  const handleEditClose = () => setEditOpen(false);
  const handleEditSave = () => {
    setProfile({
      ...form,
      age: form.dob ? (new Date().getFullYear() - Number(form.dob.slice(0, 4))) : profile.age
    });
    setEditOpen(false);
  };
  const handleFormChange = (field, value) => setForm((prev) => ({ ...prev, [field]: value }));
  const openMsgModal = () => { setMsgText(""); setMsgChannel("SMS"); setMsgModal(true); };
  const closeMsgModal = () => setMsgModal(false);
  const sendMessage = () => {
    if (!msgText.trim()) return;
    setMsgSending(true);
    setTimeout(() => {
      setMessageHistory(prev => [
        {
          date: new Date().toLocaleString("ru-RU").slice(0, 17),
          channel: msgChannel,
          direction: "Исходящее",
          text: msgText
        }, ...prev
      ]);
      setMsgSending(false);
      setMsgModal(false);
    }, 700);
  };
  const openReceiptModal = (p) => setReceiptModal({ open: true, receipt: p.receipt, meta: p });
  const closeReceiptModal = () => setReceiptModal({ open: false, receipt: null, meta: null });

  // ——— UI без левого меню
  return (
    <Box sx={{ bgcolor: "#f4f8fb", minHeight: "100vh" }}>
      <AppBar elevation={0} position="static" color="inherit" sx={{ bgcolor: "#fff", borderBottom: "1px solid #e3e8ef" }}>
        <Toolbar>
          <Typography variant="h5" fontWeight={900} color="#26304a" sx={{ letterSpacing: 2 }}>Профиль клиента</Typography>
          <Box flex={1} />
          <Button startIcon={<SendIcon />} variant="contained" color="primary" onClick={openMsgModal} sx={{ borderRadius: 2, textTransform: "none", mr: 2 }}>
            Сообщение
          </Button>
          <Button startIcon={<EditIcon />} variant="outlined" color="primary" onClick={handleEdit} sx={{ borderRadius: 2, textTransform: "none" }}>
            Редактировать
          </Button>
        </Toolbar>
      </AppBar>

      <Box sx={{ maxWidth: 1200, mx: "auto", p: 4 }}>
        {/* Карточка профиля */}
        <Paper sx={{
          display: "flex", alignItems: "center",
          p: 3, mb: 3, borderRadius: 4, boxShadow: "0 6px 24px rgba(33,39,51,0.04)"
        }}>
          <Avatar sx={{
            width: 96, height: 96, fontSize: 44,
            bgcolor: "#E7ECF5", color: "#366AD7", mr: 3, border: "4px solid #fff"
          }}>
            {profile.name.split(" ").map(part => part[0]).join("").toUpperCase()}
          </Avatar>
          <Box>
            <Stack direction="row" spacing={1.5} alignItems="center" mb={1}>
              <Typography fontSize={30} fontWeight={800}>{profile.name}</Typography>
              {profile.tags.includes("VIP") && (
                <Chip label="VIP" icon={<StarIcon sx={{ color: "#F5B82E" }} />} sx={{
                  bgcolor: "#FFF9E1", color: "#a77b06", fontWeight: 800, fontSize: 18, px: 2, border: "2px solid #FFE199"
                }} size="medium" />
              )}
              {profile.tags.includes("Постоянный клиент") && (
                <Chip label="Постоянный клиент" sx={{
                  bgcolor: "#E7F9F5", color: "#007A6B", fontWeight: 700, fontSize: 16, px: 2, ml: 0.5
                }} size="medium" />
              )}
            </Stack>
            <Stack direction="row" spacing={1.5} alignItems="center" mb={1}>
              <Typography fontWeight={500} color="#57617a">ID: {profile.id}</Typography>
              <Typography fontWeight={500} color="#57617a">Возраст: {profile.age}</Typography>
              <Typography fontWeight={500} color="#57617a">{profile.city}, {profile.country}</Typography>
              <Chip icon={<ReceiptLongIcon />} label={profile.card.status} sx={{
                bgcolor: "#E7F5E9", color: "#188035", fontWeight: 700, fontSize: 16, px: 2
              }} size="small" />
            </Stack>
            <Stack direction="row" spacing={1.5} alignItems="center">
              <EmailIcon sx={{ color: "#5880ea" }} />
              <Typography fontWeight={500}>{profile.email}</Typography>
              <SmsIcon sx={{ color: "#2ecf8f" }} />
              <Typography fontWeight={500}>{profile.phone}</Typography>
            </Stack>
          </Box>
          <Box flex={1} />
          <Box textAlign="right">
            <Typography variant="body2" color="text.secondary">Баланс бонусов</Typography>
            <Typography variant="h4" color="primary" fontWeight={900} lineHeight={1}>{profile.bonus} <span style={{ fontSize: 20 }}>бонусов</span></Typography>
            <Typography variant="body2" color="text.secondary" mt={1}>LTV: {profile.ltv}</Typography>
          </Box>
        </Paper>

        {/* Вкладки */}
        <Tabs value={tab} onChange={(_, v) => setTab(v)} textColor="primary" indicatorColor="primary" sx={{ mb: 3 }}>
          {TABS.map((tabObj, i) =>
            <Tab key={tabObj.label} label={tabObj.label} icon={tabObj.icon} iconPosition="start" sx={{ fontWeight: 700, fontSize: 16, px: 3, minHeight: 56 }} />
          )}
        </Tabs>

        {/* Контент вкладок */}
        <TabPanel value={tab} index={0}>
          {/* Основная таблица профиля */}
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 3, borderRadius: 3 }}>
                <Typography variant="h6" fontWeight={700} mb={2}>Контактные данные</Typography>
                <Stack spacing={1.2}>
                  <Stack direction="row" alignItems="center" spacing={1}><EmailIcon /><b>Email:</b> {profile.email}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><SmsIcon /><b>Телефон:</b> {profile.phone}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Дата рождения:</b> {profile.dob.split("-").reverse().join(".")}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Пол:</b> {profile.gender}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Город:</b> {profile.city}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><PersonIcon /><b>Адрес:</b> {profile.address}</Stack>
                </Stack>
              </Paper>
            </Grid>
            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 3, borderRadius: 3 }}>
                <Typography variant="h6" fontWeight={700} mb={2}>Детали программы</Typography>
                <Stack spacing={1.2}>
                  <Stack direction="row" alignItems="center" spacing={1}><LoyaltyIcon /><b>Тип клиента:</b> {profile.clientType}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><LoyaltyIcon /><b>Рефералов:</b> {profile.referrals}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><ReceiptLongIcon /><b>Карта:</b> {profile.card.number}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><LocalOfferIcon /><b>Статус карты:</b> {profile.card.status}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><GiftCardIcon /><b>Промокоды:</b> {profile.promoCodes.map(pc => (
                    <Chip key={pc.code} icon={<LocalOfferIcon />} label={pc.code + (pc.used ? " (исп.)" : "")} sx={{ ml: 1, bgcolor: pc.used ? "#e0e0e0" : "#F2FBF6" }} />
                  ))}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><GiftCardIcon /><b>Подарки:</b> {profile.gifts.map(gift => (
                    <Chip key={gift.name} icon={<GiftCardIcon />} label={gift.name} sx={{ ml: 1, bgcolor: "#F8F2FF" }} />
                  ))}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><LoyaltyIcon /><b>Язык интерфейса:</b> {profile.lang}</Stack>
                  <Stack direction="row" alignItems="center" spacing={1}><LoyaltyIcon /><b>Согласие на рассылку:</b> {profile.marketing ? "Да" : "Нет"}</Stack>
                </Stack>
              </Paper>
            </Grid>
          </Grid>
        </TabPanel>

        <TabPanel value={tab} index={1}>
          {/* История покупок */}
          <Paper sx={{ p: 2, borderRadius: 3 }}>
            <Typography variant="h6" fontWeight={700} mb={2}>История покупок</Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Точка продаж</TableCell>
                  <TableCell>Сотрудник</TableCell>
                  <TableCell>Сумма</TableCell>
                  <TableCell>Статус</TableCell>
                  <TableCell>Чек</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {purchases.map((p, i) => (
                  <TableRow key={i}>
                    <TableCell>{p.date}</TableCell>
                    <TableCell>{p.pos}</TableCell>
                    <TableCell>{p.employee}</TableCell>
                    <TableCell>{p.amount}</TableCell>
                    <TableCell>
                      <Chip label={p.status} color={p.status.includes("Оплачено") ? "success" : "warning"} size="small" />
                    </TableCell>
                    <TableCell>
                      <Button
                        size="small"
                        variant="outlined"
                        onClick={() => openReceiptModal(p)}
                        startIcon={<ReceiptLongIcon />}
                        sx={{ textTransform: "none" }}
                      >
                        {p.receiptId}
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={2}>
          {/* Бонусы */}
          <Paper sx={{ p: 2, borderRadius: 3 }}>
            <Typography variant="h6" fontWeight={700} mb={2}>История бонусов</Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Тип</TableCell>
                  <TableCell>Кампания</TableCell>
                  <TableCell>Сумма</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {bonusHistory.map((b, i) => (
                  <TableRow key={i}>
                    <TableCell>{b.date}</TableCell>
                    <TableCell>
                      <Chip label={b.type} color={b.type === "Начисление" ? "success" : "warning"} size="small" />
                    </TableCell>
                    <TableCell>{b.campaign}</TableCell>
                    <TableCell>
                      <Typography sx={{ fontWeight: 700, color: b.type === "Начисление" ? "#2e7d32" : "#b71c1c" }}>{b.value}</Typography>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={3}>
          <Paper sx={{ p: 2, borderRadius: 3 }}>
            <Typography variant="h6" fontWeight={700} mb={2}>Интересы</Typography>
            <Stack direction="row" spacing={1} flexWrap="wrap">
              {profile.interests.map((interest, i) => (
                <Chip key={i} label={interest} sx={{ fontSize: 16, p: 1.5, m: .5, bgcolor: "#f3f6fc" }} />
              ))}
            </Stack>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={4}>
          <Paper sx={{ p: 2, borderRadius: 3 }}>
            <Typography variant="h6" fontWeight={700} mb={2}>Оценки NPS</Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Канал</TableCell>
                  <TableCell>Оценка (0-10)</TableCell>
                  <TableCell>Комментарий</TableCell>
                  <TableCell>Заказ</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {npsSurveys.map((n, i) => (
                  <TableRow key={i}>
                    <TableCell>{n.date}</TableCell>
                    <TableCell>
                      <Chip label={n.channel} size="small" color="primary" />
                    </TableCell>
                    <TableCell>
                      <Stack direction="row" alignItems="center" spacing={0.5}>
                        <StarIcon fontSize="small" sx={{ color: n.score > 8 ? "#219653" : n.score > 6 ? "#FFB300" : "#C62828" }} />
                        <Typography fontWeight={700}>{n.score}</Typography>
                      </Stack>
                    </TableCell>
                    <TableCell>{n.comment}</TableCell>
                    <TableCell>
                      <Chip label={n.order} icon={<ReceiptLongIcon />} size="small" />
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </TabPanel>

        <TabPanel value={tab} index={5}>
          <Paper sx={{ p: 2, borderRadius: 3 }}>
            <Typography variant="h6" fontWeight={700} mb={2}>История сообщений</Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Дата</TableCell>
                  <TableCell>Канал</TableCell>
                  <TableCell>Тип</TableCell>
                  <TableCell>Текст</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {messageHistory.map((m, i) => (
                  <TableRow key={i}>
                    <TableCell>{m.date}</TableCell>
                    <TableCell>
                      <Chip icon={m.channel === "SMS" ? <SmsIcon fontSize="small" /> : <TelegramIcon fontSize="small" />} label={m.channel} size="small" color={m.channel === "SMS" ? "info" : "primary"} />
                    </TableCell>
                    <TableCell>
                      <Chip label={m.direction} size="small" color={m.direction === "Исходящее" ? "success" : "warning"} />
                    </TableCell>
                    <TableCell>{m.text}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </TabPanel>
      </Box>

      {/* --------- Модальное окно ЧЕКА --------- */}
      <Dialog open={receiptModal.open} onClose={closeReceiptModal} maxWidth="sm" fullWidth>
        <DialogTitle>
          Чек {receiptModal.meta?.receiptId} — {receiptModal.meta?.date}
        </DialogTitle>
        <DialogContent>
          {receiptModal.meta && (
            <Typography sx={{ mb: 1.5, fontSize: 16 }}>
              <b>Точка продаж:</b> {receiptModal.meta.pos}<br />
              <b>Сотрудник:</b> {receiptModal.meta.employee}
            </Typography>
          )}
          <Table size="medium">
            <TableHead>
              <TableRow>
                <TableCell sx={{ fontWeight: 700 }}>Товар</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>Кол-во</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>Цена</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>Сумма</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>Списано бонусов</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {receiptModal.receipt && receiptModal.receipt.map((row, i) => (
                <TableRow key={i} hover>
                  <TableCell>
                    <Stack direction="row" alignItems="center" spacing={1}>
                      <span>{row.name}</span>
                      {row.writtenOff && (
                        <Chip
                          label="Бонусами"
                          size="small"
                          sx={{
                            bgcolor: "#1976d2",
                            color: "#fff",
                            fontWeight: 600,
                            fontSize: 13,
                            borderRadius: 2,
                            px: 1.5
                          }}
                        />
                      )}
                    </Stack>
                  </TableCell>
                  <TableCell>{row.qty}</TableCell>
                  <TableCell>{row.price.toLocaleString()} ₽</TableCell>
                  <TableCell>{(row.qty * row.price).toLocaleString()} ₽</TableCell>
                  <TableCell align="center">
                    {row.writtenOffBonuses
                      ? <span style={{
                          color: "#9c27b0",
                          fontWeight: 700,
                          fontSize: 16
                        }}>{row.writtenOffBonuses} бонусов</span>
                      : <span style={{ color: "#bbb" }}>—</span>
                    }
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeReceiptModal} variant="outlined">Закрыть</Button>
        </DialogActions>
      </Dialog>

      {/* --------- Модальное окно ОТПРАВКИ СООБЩЕНИЯ --------- */}
      <Dialog open={msgModal} onClose={closeMsgModal} maxWidth="xs" fullWidth>
        <DialogTitle>Отправить сообщение клиенту</DialogTitle>
        <DialogContent>
          <FormControl sx={{ mb: 2, minWidth: 140 }}>
            <InputLabel id="channel-label">Канал</InputLabel>
            <Select
              labelId="channel-label"
              value={msgChannel}
              label="Канал"
              onChange={e => setMsgChannel(e.target.value)}
              startAdornment={
                msgChannel === "SMS"
                  ? <SmsIcon sx={{ mr: 1, color: "#3f51b5" }} />
                  : <TelegramIcon sx={{ mr: 1, color: "#0088cc" }} />
              }
            >
              <MenuItem value="SMS"><Stack direction="row" alignItems="center" spacing={1}><SmsIcon />SMS</Stack></MenuItem>
              <MenuItem value="Telegram"><Stack direction="row" alignItems="center" spacing={1}><TelegramIcon />Telegram</Stack></MenuItem>
            </Select>
          </FormControl>
          <Stack spacing={1}>
            <Stack direction="row" spacing={1}>
              {MESSAGE_TEMPLATES.map((tpl, idx) => (
                <Button key={idx} variant="outlined" size="small" onClick={() => setMsgText(tpl)}
                  sx={{ borderRadius: 2, bgcolor: "#F7F9FC", fontSize: 13, px: 1.5, textTransform: "none" }}
                  startIcon={<ContentCopyIcon fontSize="small" />}>
                  {tpl.length > 30 ? tpl.slice(0, 27) + "…" : tpl}
                </Button>
              ))}
            </Stack>
            <TextField
              fullWidth multiline minRows={3} maxRows={8} placeholder="Введите сообщение…"
              value={msgText} onChange={e => setMsgText(e.target.value)}
              sx={{ ".MuiInputBase-root": { bgcolor: "#F6F9FC", borderRadius: 2 } }}
            />
          </Stack>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeMsgModal}>Отмена</Button>
          <Button variant="contained" onClick={sendMessage} disabled={!msgText.trim() || msgSending} endIcon={<SendIcon />} sx={{ textTransform: "none" }}>
            {msgSending ? "Отправка..." : "Отправить"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* ----------- Модальное окно РЕДАКТИРОВАНИЕ ПРОФИЛЯ ----------- */}
      <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
        <DialogTitle>Редактирование профиля</DialogTitle>
        <DialogContent>
          <Stack spacing={2} mt={1}>
            <TextField label="ФИО" fullWidth value={form.name} onChange={e => handleFormChange("name", e.target.value)} />
            <TextField label="Email" type="email" fullWidth value={form.email} onChange={e => handleFormChange("email", e.target.value)} />
            <TextField label="Телефон" fullWidth value={form.phone} onChange={e => handleFormChange("phone", e.target.value)} />
            <TextField label="Дата рождения" type="date" fullWidth InputLabelProps={{ shrink: true }} value={form.dob} onChange={e => handleFormChange("dob", e.target.value)} />
            <Stack direction="row" spacing={2}>
              <TextField label="Город" fullWidth value={form.city} onChange={e => handleFormChange("city", e.target.value)} />
              <TextField label="Страна" fullWidth value={form.country} onChange={e => handleFormChange("country", e.target.value)} />
            </Stack>
            <Select label="Пол" fullWidth value={form.gender} onChange={e => handleFormChange("gender", e.target.value)}>
              <MenuItem value="Женский">Женский</MenuItem>
              <MenuItem value="Мужской">Мужской</MenuItem>
              <MenuItem value="Другое">Другое</MenuItem>
            </Select>
            <Select label="Тип клиента" fullWidth value={form.clientType} onChange={e => handleFormChange("clientType", e.target.value)}>
              <MenuItem value="Физ. лицо">Физ. лицо</MenuItem>
              <MenuItem value="Юр. лицо">Юр. лицо</MenuItem>
            </Select>
            <TextField label="Адрес" fullWidth value={form.address} onChange={e => handleFormChange("address", e.target.value)} />
            <Select label="Язык интерфейса" fullWidth value={form.lang} onChange={e => handleFormChange("lang", e.target.value)}>
              <MenuItem value="Русский">Русский</MenuItem>
              <MenuItem value="English">English</MenuItem>
            </Select>
            <Stack direction="row" alignItems="center" spacing={1}>
              <Checkbox checked={form.marketing} onChange={e => handleFormChange("marketing", e.target.checked)} />
              <Typography>Согласие на рассылку</Typography>
            </Stack>
          </Stack>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose}>Отмена</Button>
          <Button variant="contained" onClick={handleEditSave}>Сохранить</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
