import React, { useState, useRef, useEffect } from "react";
import {
  Box, Tabs, Tab, Stack, Typography, Avatar, Chip, Divider, Table, TableBody, TableRow,
  TableCell, TableHead, Grid, Paper, Dialog, DialogTitle, DialogContent, DialogActions,
  Button, TextField, Select, MenuItem, Checkbox, FormControl, InputLabel
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import EmailIcon from "@mui/icons-material/Email";
import SmsIcon from "@mui/icons-material/Sms";
import TelegramIcon from "@mui/icons-material/Telegram";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";
import SendIcon from "@mui/icons-material/Send";
import StarIcon from "@mui/icons-material/Star";
import PersonIcon from "@mui/icons-material/Person";
import CardGiftcardIcon from "@mui/icons-material/CardGiftcard";
import LocalOfferIcon from "@mui/icons-material/LocalOffer";
import EventIcon from "@mui/icons-material/Event";
import WarningAmberIcon from "@mui/icons-material/WarningAmber";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import MapIcon from "@mui/icons-material/Map";
import PhoneIcon from "@mui/icons-material/Phone";
import { deepPurple } from "@mui/material/colors";
import Slide from "@mui/material/Slide";

const MESSAGE_TEMPLATES = [
  "Спасибо за ваш заказ! Если будут вопросы — пишите в ответ.",
  "Напоминаем: у вас осталось неиспользовано X бонусов.",
  "Ваша бронирование подтверждено. Ждем вас на курорте!",
  "До конца акции осталось 2 дня — не упустите скидку!",
];

// StickyBar Component (можно вынести, но вставляю прямо)
function StickyProfileBar({ show, name, phone, status, riskFlag }) {
  return (
    <Slide in={show} direction="down" mountOnEnter unmountOnExit>
      <Paper
        elevation={3}
        sx={{
          position: "fixed",
          top: 0, left: 0, right: 0,
          zIndex: 1201,
          py: 1.2, px: 4,
          display: "flex",
          alignItems: "center",
          gap: 3,
          bgcolor: "#fff",
          borderBottom: "1.5px solid #e2e7f5",
          boxShadow: "0 4px 16px #c4dafc14",
        }}
      >
        <Typography variant="h6" fontWeight={700} sx={{ mr: 2 }}>
          {name}
        </Typography>
        <Stack direction="row" spacing={1} alignItems="center">
          <PhoneIcon fontSize="small" color="action" />
          <Typography fontWeight={500}>{phone}</Typography>
        </Stack>
        <Chip
          label={status}
          color={status === "Постоянный клиент" ? "primary" : "default"}
          sx={{ fontWeight: 700, ml: 2 }}
        />
        {riskFlag && (
          <Chip
            icon={<WarningAmberIcon sx={{ color: "#e05f45" }} />}
            label="Есть риск ухода"
            color="warning"
            size="small"
            sx={{ fontWeight: 800, ml: 2 }}
          />
        )}
      </Paper>
    </Slide>
  );
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div role="tabpanel" hidden={value !== index} {...other} style={{ width: "100%" }}>
      {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
    </div>
  );
}

export default function CustomerProfileCard() {
  // --- Профиль
  const [profile, setProfile] = useState({
    name: "Мария Летунова",
    id: "874392",
    email: "maria.letunova@yandex.ru",
    phone: "+7 988 509-96-62",
    dob: "1989-01-16",
    city: "Краснодар",
    region: "Краснодарский край",
    geoTags: ["Красная Поляна", "Сочи"],
    country: "Россия",
    gender: "Женский",
    clientType: "Физ. лицо",
    address: "ул. Ленина, д. 10, кв. 24",
    marketing: true,
    consentAds: true,
    consentPersonal: true,
    consentPush: false,
    lang: "Русский",
    age: 36,
    rfm: "Gold",
    segments: ["VIP", "По интересам: SPA", "Geo: Красная поляна", "RFM: Gold"],
    tags: ["VIP", "День рождения сегодня", "RFM: Gold", "Есть риск ухода"],
    interests: ["Горные туры", "Зимний отдых", "SPA и релакс", "Детские активности", "Экстрим"],
    ltv: "188 200 ₽",
